
-- Eliminar la base de datos pokedex si existe
IF EXISTS (SELECT * FROM sys.databases WHERE name = 'pokedex')
BEGIN
    DROP DATABASE pokedex;
END;
GO

-- Crear y usar la base de datos pokedex
CREATE DATABASE pokedex;
GO

USE pokedex;
GO

-- Creación de la tabla tipo
CREATE TABLE tipo (
    id INT,
    nombre NVARCHAR(150) NOT NULL,
    CONSTRAINT pk_tipo PRIMARY KEY (id)
);      

-- Creación de la tabla fortaleza
CREATE TABLE fortaleza (
    idfuerte INT,
    iddebil INT,
    CONSTRAINT pk_fortaleza PRIMARY KEY (idfuerte, iddebil),
    CONSTRAINT fk_fortaleza_tipofuerte FOREIGN KEY (idfuerte) REFERENCES tipo(id),
    CONSTRAINT fk_fortaleza_tipodebil FOREIGN KEY (iddebil) REFERENCES tipo(id)
);

-- Creación de la tabla region
CREATE TABLE region (
    idregion INT,
    nombreregion NVARCHAR(15) NOT NULL,
    CONSTRAINT pk_region PRIMARY KEY (idregion)
);           

-- Creación de la tabla pokemon
CREATE TABLE pokemon (
    numeropokedex INT,
    nombre NVARCHAR(150) NOT NULL,
    tipo1 INT NOT NULL,
    tipo2 INT DEFAULT NULL,
    peso DECIMAL(10,2) NOT NULL,
    altura DECIMAL(10,2) NOT NULL,
    ps INT NOT NULL,
    ataque INT NOT NULL,
    defensa INT NOT NULL,
    at_especial INT NOT NULL,
    df_especial INT NOT NULL,
    velocidad INT NOT NULL,
    descripcion NVARCHAR(500) NOT NULL,
    categoria NVARCHAR(150) NOT NULL,
    CONSTRAINT pk_pokemon PRIMARY KEY (numeropokedex),
    CONSTRAINT fk_pokemon_tipo1 FOREIGN KEY (tipo1) REFERENCES tipo(id),
    CONSTRAINT fk_pokemon_tipo2 FOREIGN KEY (tipo2) REFERENCES tipo(id),
    CONSTRAINT ck_Pokemon_peso CHECK (peso >= 0),
    CONSTRAINT ck_Pokemon_altura CHECK (altura >= 0),
    CONSTRAINT ck_Pokemon_ps CHECK (ps >= 0 AND ps <= 255),
    CONSTRAINT ck_Pokemon_ataque CHECK (ataque >= 0 AND ataque <= 255),
    CONSTRAINT ck_Pokemon_defensa CHECK (defensa >= 0 AND defensa <= 255),
    CONSTRAINT ck_Pokemon_at_especial CHECK (at_especial >= 0 AND at_especial <= 255),
    CONSTRAINT ck_Pokemon_df_especial CHECK (df_especial >= 0 AND df_especial <= 255),
    CONSTRAINT ck_Pokemon_velocidad CHECK (velocidad >= 0 AND velocidad <= 255)
);

-- Creación de la tabla habilidad
CREATE TABLE habilidad (
    id INT,
    nombre NVARCHAR(150) NOT NULL,
    descripcion NVARCHAR(500) DEFAULT NULL,
    CONSTRAINT pk_habilidad PRIMARY KEY (id)
);

-- Relación entre Pokémon y habilidades
CREATE TABLE pokemon_habilidad (
    id_pokemon INT,
    id_habilidad INT,
    CONSTRAINT pk_pokemon_habilidad PRIMARY KEY (id_pokemon, id_habilidad),
    CONSTRAINT fk_pokemon_habilidad_pokemon FOREIGN KEY (id_pokemon) REFERENCES pokemon(numeropokedex),
    CONSTRAINT fk_pokemon_habilidad_habilidad FOREIGN KEY (id_habilidad) REFERENCES habilidad(id),
);

-- Relación entre Pokémon y región
CREATE TABLE pokemon_region (
    id_pokemon INT,
    id_region INT,
    CONSTRAINT pk_pokemon_region PRIMARY KEY (id_pokemon, id_region),
    CONSTRAINT fk_pokemon_region_pokemon FOREIGN KEY (id_pokemon) REFERENCES pokemon(numeropokedex),
    CONSTRAINT fk_pokemon_region_region FOREIGN KEY (id_region) REFERENCES region(idregion)
);

-- Creación de la tabla evolucion
CREATE TABLE evolucion (
    id_pokemon_base INT,
    id_pokemon_evoluciona INT,
    metodo NVARCHAR(150) NOT NULL,
    CONSTRAINT pk_evolucion PRIMARY KEY (id_pokemon_base, id_pokemon_evoluciona),
    CONSTRAINT fk_evolucion_pokemonbase FOREIGN KEY (id_pokemon_base) REFERENCES pokemon(numeropokedex),
    CONSTRAINT fk_evolucion_pokemonevoluciona FOREIGN KEY (id_pokemon_evoluciona) REFERENCES pokemon(numeropokedex)    
);

