INSERT INTO habilidad (id, nombre, descripcion) VALUES (208, 'Banco', 'Forma bancos con sus congéneres cuando tiene muchos ps, lo cual le otorga más fuerza. Cuando le quedan pocos ps, el banco se dispersa.');
