INSERT INTO habilidad (id, nombre, descripcion) VALUES (210, 'Fuerte Afecto', 'Al derrotar a un rival, los vínculos con su entrenador se refuerzan y se convierte en greninja ash. Su shuriken de agua también se ve potenciado.');
