INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (1, 'Bulbasaur', 12, 4, 6.9, 0.7, 45, 49, 49, 65, 65, 45, 'Una rara semilla le fue plantada en el lomo al nacer. la planta brota y crece con este pokémon.', 'Pokémon Semilla');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (2, 'Ivysaur', 12, 4, 13.0, 1.0, 60, 62, 63, 80, 80, 60, 'Este pokémon tiene un bulbo en el lomo. Dicen que, al absorber nutrientes, el bulbo se transforma en una flor grande.', 'Pokémon Semilla');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (3, 'Venusaur', 12, 4, 100.0, 2.0, 80, 82, 83, 100, 100, 80, 'Llena su cuerpo de energía con los rayos solares que captan los anchos pétalos de su flor.', 'Pokémon Semilla');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (4, 'Charmander', 10, NULL, 8.5, 0.6, 39, 52, 43, 60, 50, 65, 'La llama de su cola indica la fuerza vital de charmander. Será brillante si está sano.', 'Pokémon Lagartija');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (5, 'Charmeleon', 10, NULL, 19.0, 1.1, 58, 64, 58, 80, 65, 80, 'Suele usar la cola para derribar a su rival. Cuando lo tira, se vale de sus afiladas garras para acabar con él.', 'Pokémon Llama');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (6, 'Charizard', 10, 3, 90.5, 1.7, 78, 84, 78, 109, 85, 100, 'Cuando lanza una descarga de fuego supercaliente, la roja llama de su cola brilla más intensamente.', 'Pokémon Llama');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (7, 'Squirtle', 11, NULL, 9.0, 0.5, 44, 48, 65, 50, 64, 43, 'Se protege con su caparazón y luego contraataca lanzando agua a presión cuando tiene oportunidad.', 'Pokémon Tortuguita');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (8, 'Wartortle', 11, NULL, 22.5, 1.0, 59, 63, 80, 65, 80, 58, 'Si es golpeado, esconderá su cabeza. Aun así, su cola puede seguir golpeando.', 'Pokémon Tortuga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (9, 'Blastoise', 11, NULL, 85.5, 1.6, 79, 83, 100, 85, 105, 78, 'Para acabar con su enemigo, lo aplasta con el peso de su cuerpo. En momentos de apuro, se esconde en el caparazón.', 'Pokémon Armazón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (10, 'Caterpie', 7, NULL, 2.9, 0.3, 45, 30, 35, 20, 20, 45, 'Para protegerse despide un hedor horrible de sus antenas, con el que repele a sus enemigos.', 'Pokémon Gusano');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (11, 'Metapod', 7, NULL, 9.9, 0.7, 50, 20, 55, 25, 25, 30, 'Su frágil cuerpo está recubierto de una coraza dura como el acero. Permanece quieto en su desarrollo.', 'Pokémon Capullo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (12, 'Butterfree', 7, 3, 32.0, 1.1, 60, 45, 50, 90, 80, 70, 'Adora el néctar de las flores. Puede localizar hasta las más pequeñas cantidades de polen.', 'Pokémon Mariposa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (13, 'Weedle', 7, 4, 3.2, 0.3, 40, 35, 30, 20, 20, 50, 'Suele habitar bosques y praderas. Tiene un afilado y venenoso aguijón de unos 5 cm encima de la cabeza.', 'Pokémon Oruga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (14, 'Kakuna', 7, 4, 10.0, 0.6, 45, 25, 50, 25, 25, 35, 'Casi incapaz de moverse, este pokémon solo puede endurecer su caparazón para protegerse.', 'Pokémon Capullo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (15, 'Beedrill', 7, 4, 29.5, 1.0, 65, 90, 40, 45, 80, 75, 'Tiene 3 aguijones venenosos en sus patas y cola. suelen pinchar a sus enemigos repetidas veces.', 'Pokémon Abeja Veneno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (16, 'Pidgey', 1, 3, 1.8, 0.3, 40, 45, 40, 35, 35, 56, 'Muy común en bosques y selvas. Aletea al nivel del suelo para levantar la gravilla.', 'Pokémon Pajarito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (17, 'Pidgeotto', 1, 3, 30.0, 1.1, 63, 60, 55, 50, 50, 71, 'Tiene unas garras desarrolladas. Puede atrapar un exeggcute y transportarlo desde una distancia de casi 100 km.', 'Pokémon Pájaro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (18, 'Pidgeot', 1, 3, 39.5, 1.5, 83, 80, 75, 70, 70, 101, 'Cuando caza, vuela muy deprisa a ras del agua y sorprende a inocentes presas como magikarp.', 'Pokémon Pájaro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (19, 'Rattata', 1, NULL, 3.5, 0.3, 30, 56, 35, 25, 35, 72, 'Vive allí donde haya comida disponible. Busca todo el día, sin descanso, algo comestible.', 'Pokémon Ratón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (20, 'Raticate', 1, NULL, 18.5, 0.7, 55, 81, 60, 50, 70, 97, 'Lima sus colmillos royendo objetos duros. Con ellos puede destruir incluso paredes de hormigón.', 'Pokémon Ratón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (21, 'Spearow', 1, 3, 2.0, 0.3, 40, 60, 30, 31, 31, 70, 'Muy protector de su territorio, mueve sus cortas alas sin descanso para lanzarse a toda velocidad.', 'Pokémon Pajarito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (22, 'Fearow', 1, 3, 38.0, 1.2, 65, 90, 65, 61, 61, 100, 'Con sus enormes y magníficas alas, puede seguir volando sin tener que aterrizar para descansar.', 'Pokémon Pico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (23, 'Ekans', 4, NULL, 6.9, 2.0, 35, 60, 44, 40, 54, 55, 'Cuanto más viejo, más crece este pokémon. Por la noche, descansa en las ramas de los árboles.', 'Pokémon Serpiente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (24, 'Arbok', 4, NULL, 65.0, 3.5, 60, 95, 69, 65, 79, 80, 'El dibujo que tiene en la panza aterroriza. Los rivales más débiles salen huyendo al verlo.', 'Pokémon Cobra');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (25, 'Pikachu', 13, NULL, 6.0, 0.4, 35, 55, 40, 50, 50, 90, 'Levanta su cola para vigilar los alrededores. A veces, puede ser alcanzado por un rayo en esa pose.', 'Pokémon Ratón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (26, 'Raichu', 13, NULL, 30.0, 0.8, 60, 90, 55, 90, 80, 110, 'Cuando se carga de electricidad, sus músculos se tensan y se vuelve más agresivo de lo normal.', 'Pokémon Ratón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (27, 'Sandshrew', 5, NULL, 12.0, 0.6, 50, 75, 85, 20, 30, 40, 'Este pokémon permanece bajo tierra. Si se siente amenazado, se enrosca para defenderse.', 'Pokémon Ratón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (28, 'Sandslash', 5, NULL, 29.5, 1.0, 75, 100, 110, 45, 55, 65, 'Si cava a gran velocidad, puede que se le caigan las garras y púas. Vuelven a crecerle en un día.', 'Pokémon Ratón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (29, 'Nidoran_h', 4, NULL, 7.0, 0.4, 55, 47, 52, 40, 40, 41, 'Aunque pequeñas, sus venenosas púas son muy peligrosas. Tienen un cuerno más pequeño que los machos.', 'Pokémon Pin Veneno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (30, 'Nidorina', 4, NULL, 20.0, 0.8, 70, 62, 67, 55, 55, 56, 'Tiene un carácter afable. Emite ondas ultrasónicas para confundir al enemigo.', 'Pokémon Pin Veneno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (31, 'Nidoqueen', 4, 5, 60.0, 1.3, 90, 92, 87, 75, 85, 76, 'Usa su cuerpo duro y escamoso para sellar la entrada a su madriguera y protegerse de los depredadores.', 'Pokémon Taladro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (32, 'Nidoran_m', 4, NULL, 9.0, 0.5, 46, 57, 40, 40, 40, 50, 'Saca las orejas por encima de la hierba para explorar el territorio. Le protege su cuerno venenoso.', 'Pokémon Pin Veneno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (33, 'Nidorino', 4, NULL, 19.5, 0.9, 61, 72, 57, 55, 55, 65, 'Levanta sus grandes orejas para vigilar. Si detecta algo, atacará de inmediato.', 'Pokémon Pin Veneno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (34, 'Nidoking', 4, 5, 62.0, 1.4, 81, 102, 77, 85, 75, 85, 'Es fácil reconocerlo por tener una dura piel y un gran cuerno lleno de peligrosísimo veneno.', 'Pokémon Taladro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (35, 'Clefairy', 18, NULL, 7.5, 0.6, 70, 45, 48, 60, 65, 35, 'Se dice que la felicidad llegará a quien vea a un grupo de clefairy bailando a la luz de la luna llena.', 'Pokémon Hada');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (36, 'Clefable', 18, NULL, 40.0, 1.3, 95, 70, 73, 95, 90, 60, 'Su oído es tan agudo que puede oír una aguja caer a 1 km. Vive en montañas solitarias.', 'Pokémon Hada');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (37, 'Vulpix', 10, NULL, 9.9, 0.6, 38, 41, 40, 50, 65, 65, 'Cuando nace solo tiene una cola, pero a medida que crece, esta se va dividiendo desde la punta.', 'Pokémon Zorro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (38, 'Ninetales', 10, NULL, 19.9, 1.1, 73, 76, 75, 81, 100, 100, 'Tiene nueve colas y un pelaje de color dorado. dicen que este pokémon llega a vivir 1000 años.', 'Pokémon Zorro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (39, 'Jigglypuff', 1, 18, 5.5, 0.5, 115, 45, 20, 45, 25, 20, 'Cautiva con la mirada a su enemigo y hace que se quede profundamente dormido mientras entona una dulce melodía.', 'Pokémon Globo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (40, 'Wigglytuff', 1, 18, 12.0, 1.0, 140, 70, 45, 85, 50, 45, 'Su piel es tan suave que si dos de ellos se acurrucan juntos, no querrán separarse nunca.', 'Pokémon Globo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (41, 'Zubat', 4, 3, 7.5, 0.8, 40, 45, 35, 30, 40, 55, 'Aunque carezca de ojos, puede detectar obstáculos con las ondas ultrasónicas que emite su boca.', 'Pokémon Murciélago');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (42, 'Golbat', 4, 3, 55.0, 1.6, 75, 80, 70, 65, 75, 90, 'Cuando ataque, seguirá chupando energía de su víctima, aunque pese tanto que ya no pueda volar.', 'Pokémon Murciélago');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (43, 'Oddish', 12, 4, 5.4, 0.5, 45, 50, 55, 75, 65, 30, 'Durante el día, se agazapa en el frío subsuelo huyendo del sol. La luz de la luna le hace crecer mucho.', 'Pokémon Hierbajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (44, 'Gloom', 12, 4, 8.6, 0.8, 60, 65, 70, 85, 75, 40, '¡huele bastante mal! de todas formas, una de cada mil personas aprecian su fétido olor.', 'Pokémon Hierbajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (45, 'Vileplume', 12, 4, 18.6, 1.2, 75, 80, 85, 110, 90, 50, 'Cuanto mayores son sus pétalos, más tóxico es su polen. Le pesa la cabeza y le cuesta mantenerla erguida.', 'Pokémon Flor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (46, 'Paras', 7, 12, 5.4, 0.3, 35, 70, 55, 45, 55, 25, 'Lleva en el lomo dos setas parásitas llamadas tochukaso, que crecen con él.', 'Pokémon Hongo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (47, 'Parasect', 7, 12, 29.5, 1.0, 60, 95, 80, 60, 80, 30, 'Parasect está dominado por una seta parásita mayor que él. Dispersa esporas venenosas.', 'Pokémon Hongo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (48, 'Venonat', 7, 4, 30.0, 1.0, 60, 55, 50, 40, 55, 45, 'Sus grandes ojos son en realidad grupos de ojos diminutos. Por la noche se siente atraído por la luz.', 'Pokémon Insecto');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (49, 'Venomoth', 7, 4, 12.5, 1.5, 70, 65, 60, 90, 75, 90, 'Lanza unas escamas que paralizan a cualquiera. quien las toque, no podrá ni ponerse de pie.', 'Pokémon Polilla Ven.');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (50, 'Diglett', 5, NULL, 0.8, 0.2, 10, 55, 25, 35, 45, 95, 'Vive un metro por debajo del suelo, donde se alimenta de raíces. También aparece en la superficie.', 'Pokémon Topo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (51, 'Dugtrio', 5, NULL, 33.3, 0.7, 35, 100, 50, 50, 70, 120, 'En combate, cava la tierra, se esconde y sale de repente para golpear a su rival. Nunca se sabe por dónde puede aparecer.', 'Pokémon Topo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (52, 'Meowth', 1, NULL, 4.2, 0.4, 40, 45, 35, 40, 40, 90, 'Es de naturaleza nocturna. Le atraen los objetos brillantes.', 'Pokémon Gato Araña');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (53, 'Persian', 1, NULL, 32.0, 1.0, 65, 70, 60, 65, 65, 115, 'Aunque es muy admirado por el pelo, es difícil de entrenar como mascota, porque es un poco travieso.', 'Pokémon Gato Fino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (54, 'Psyduck', 11, NULL, 19.6, 0.8, 50, 52, 48, 65, 50, 55, 'Padece continuamente dolores de cabeza. Cuando son muy fuertes, empieza a usar misteriosos poderes.', 'Pokémon Pato');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (55, 'Golduck', 11, NULL, 76.6, 1.7, 80, 82, 78, 95, 80, 85, 'Aparece en ríos al anochecer. Puede usar poderes telequinéticos si su frente brilla misteriosamente.', 'Pokémon Pato');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (56, 'Mankey', 2, NULL, 28.0, 0.5, 40, 80, 35, 35, 45, 70, 'Es peligroso acercarse si se enfada sin razón aparente, ya que no distingue entre amigos y enemigos.', 'Pokémon Mono Cerdo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (57, 'Primeape', 2, NULL, 32.0, 1.0, 65, 105, 60, 60, 70, 95, 'Solo se calma cuando nadie está cerca. Llegar a ver ese momento es realmente difícil.', 'Pokémon Mono Cerdo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (58, 'Growlithe', 10, NULL, 19.0, 0.7, 55, 70, 45, 70, 50, 60, 'Es muy agradable y leal. Para ahuyentar al enemigo, se pone a ladrar y a dar bocados.', 'Pokémon Perrito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (59, 'Arcanine', 10, NULL, 155.0, 1.9, 90, 110, 80, 100, 80, 95, 'Un pokémon muy admirado desde la antigüedad por su belleza. Corre ágilmente como si tuviera alas.', 'Pokémon Leyenda');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (60, 'Poliwag', 11, NULL, 12.4, 0.6, 40, 50, 40, 40, 40, 90, 'Tiene una piel extraordinaria, fina y húmeda, que deja entrever las vísceras que tiene dispuestas en espiral.', 'Pokémon Renacuajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (61, 'Poliwhirl', 11, NULL, 20.0, 1.0, 65, 65, 65, 50, 50, 90, 'Capaz de vivir dentro o fuera del agua. Fuera del agua suda para mantener baboso su cuerpo.', 'Pokémon Renacuajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (62, 'Poliwrath', 11, 2, 54.0, 1.3, 90, 95, 95, 70, 90, 70, 'Tiene músculos muy desarrollados. Es capaz de nadar en el océano sin descanso.', 'Pokémon Renacuajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (63, 'Abra', 14, NULL, 19.5, 0.9, 25, 20, 15, 105, 55, 90, 'Duerme 18 horas al día y mientras lo hace es capaz de usar una serie de poderes extrasensoriales.', 'Pokémon Psi');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (64, 'Kadabra', 14, NULL, 56.5, 1.3, 40, 35, 30, 120, 70, 105, 'Cuando utiliza su poder psíquico, emite poderosas ondas alfa que pueden destruir dispositivos.', 'Pokémon Psi');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (65, 'Alakazam', 14, NULL, 48.0, 1.5, 55, 50, 45, 135, 95, 120, 'Sus neuronas se multiplican continuamente durante su vida. Por eso, siempre lo recuerda todo.', 'Pokémon Psi');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (66, 'Machop', 2, NULL, 19.5, 0.8, 70, 80, 50, 35, 35, 35, 'Levanta un graveler para mantener sus músculos en forma. Domina todas las artes marciales.', 'Pokémon Superpoder');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (67, 'Machoke', 2, NULL, 70.5, 1.5, 80, 100, 70, 50, 60, 45, 'Su musculoso cuerpo es tan fuerte que usa un cinto antifuerza para controlar sus movimientos.', 'Pokémon Superpoder');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (68, 'Machamp', 2, NULL, 130.0, 1.6, 90, 130, 80, 65, 85, 55, 'Tiene cuatro brazos tan bien desarrollados que puede dar una serie de 1000 puñetazos en cuestión de dos segundos.', 'Pokémon Superpoder');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (69, 'Bellsprout', 12, 4, 4.0, 0.7, 50, 75, 35, 70, 30, 40, 'Aunque su cuerpo es extremadamente delgado, es muy rápido a la hora de capturar sus presas.', 'Pokémon Flor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (70, 'Weepinbell', 12, 4, 6.4, 1.0, 65, 90, 50, 85, 45, 55, 'Las hojas que tiene actúan como cuchillas en combate. Otra de sus armas es el corrosivo fluido que expulsa.', 'Pokémon Matamoscas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (71, 'Victreebel', 12, 4, 15.5, 1.7, 80, 105, 65, 100, 70, 70, 'Dicen que vive en grandes colonias en el interior de las junglas, aunque nadie ha podido verificarlo.', 'Pokémon Matamoscas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (72, 'Tentacool', 11, 4, 45.5, 0.9, 40, 40, 35, 50, 100, 70, 'Su cuerpo se compone casi en exclusiva de agua. lanza extraños rayos con sus ojos cristalinos.', 'Pokémon Medusa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (73, 'Tentacruel', 11, 4, 55.0, 1.6, 80, 70, 65, 80, 120, 100, 'Cuando caza, extiende los cortos tentáculos que tiene para atrapar e inmovilizar a su presa.', 'Pokémon Medusa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (74, 'Geodude', 6, 5, 20.0, 0.4, 40, 80, 100, 30, 30, 20, 'Aparecen en llanos y montañas. Como parecen rocas, la gente se tropieza con ellos o los pisa.', 'Pokémon Roca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (75, 'Graveler', 6, 5, 105.0, 1.0, 55, 95, 115, 45, 45, 35, 'De naturaleza descuidada y libre, no le importa dañarse cuando baja rodando montañas.', 'Pokémon Roca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (76, 'Golem', 6, 5, 300.0, 1.4, 80, 120, 130, 55, 65, 45, 'Se lanza montaña abajo y deja un surco desde la cima hasta el pie. Es mejor mantenerse alejado.', 'Pokémon Megatón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (77, 'Ponyta', 10, NULL, 30.0, 1.0, 50, 85, 55, 65, 65, 90, 'Cuando nace, apenas puede tenerse en pie. Pero va fortaleciendo las patas en cuanto empieza a galopar.', 'Pokémon Caballo Fuego');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (78, 'Rapidash', 10, NULL, 95.0, 1.7, 65, 100, 70, 80, 80, 105, 'Galopa a casi 240 km por hora. Su crin ardiente parece una flecha cuando corre.', 'Pokémon Caballo Fuego');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (79, 'Slowpoke', 11, 14, 36.0, 1.2, 90, 65, 65, 40, 40, 15, 'Descansa ocioso junto al agua. Si algo muerde su cola, no lo notará en todo el día.', 'Pokémon Atontado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (80, 'Slowbro', 11, 14, 78.5, 1.6, 95, 75, 110, 100, 80, 30, 'Tiene una cola tan apetecible, que el shellder que va enganchado a ella no se soltará por nada del mundo.', 'Pokémon Ermitaño');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (81, 'Magnemite', 13, 9, 6.0, 0.3, 25, 35, 70, 95, 55, 45, 'Las unidades a los lados de su cuerpo generan energía antigravitatoria para mantenerlo en el aire.', 'Pokémon Imán');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (82, 'Magneton', 13, 9, 60.0, 1.0, 50, 60, 95, 120, 70, 70, 'Lo constituye un grupo de magnemite. Descarga potentes ondas magnéticas de alto voltaje.', 'Pokémon Imán');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (83, 'Farfetch’d', 1, 3, 15.0, 0.8, 52, 90, 55, 58, 62, 60, 'El puerro que lleva es su mejor arma. Suele usarlo como espada para cortar cosas.', 'Pokémon Pato Salvaje');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (84, 'Doduo', 1, 3, 39.2, 1.4, 35, 85, 45, 35, 35, 75, 'Este pokémon de dos cabezas es el resultado de una mutación. Cuando corre, puede alcanzar casi 100 km por hora.', 'Pokémon Ave Gemela');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (85, 'Dodrio', 1, 3, 85.2, 1.8, 60, 110, 70, 60, 60, 110, 'Más vale no perder de vista ninguna de las tres cabezas. De lo contrario, el número de picotazos será enorme.', 'Pokémon Ave Triple');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (86, 'Seel', 11, NULL, 90.0, 1.1, 65, 45, 55, 45, 70, 45, 'Este pokémon vive en icebergs. Nada en el mar y usa el cuerno de su cabeza para romper el hielo.', 'Pokémon León Marino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (87, 'Dewgong', 11, 15, 120.0, 1.7, 90, 70, 80, 70, 95, 70, 'Está recubierto de un luminoso pelaje blanco. Este pokémon aumenta su actividad cuando bajan las temperaturas.', 'Pokémon León Marino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (88, 'Grimer', 4, NULL, 30.0, 0.9, 80, 80, 50, 40, 50, 25, 'Nace de lodo alterado al filtrarse en el agua los rayos x reflejados por la luna. Se alimenta de sustancias desagradables.', 'Pokémon Lodo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (89, 'Muk', 4, NULL, 30.0, 1.2, 105, 105, 75, 65, 100, 50, 'Les encanta reunirse en zonas apestosas donde se acumula el lodo, haciendo su olor más insoportable.', 'Pokémon Lodo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (90, 'Shellder', 11, NULL, 4.0, 0.3, 30, 65, 100, 45, 25, 40, 'La concha lo protege de cualquier tipo de ataque. solo es vulnerable cuando se abre.', 'Pokémon Bivalvo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (91, 'Cloyster', 11, 15, 132.5, 1.5, 50, 95, 180, 85, 45, 70, 'A los cloyster que viven en las fuertes corrientes marinas les crecen largas y afiladas púas en la concha.', 'Pokémon Bivalvo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (92, 'Gastly', 8, 4, 0.1, 1.3, 30, 35, 30, 100, 35, 80, 'Su etéreo cuerpo está hecho de gas. Puede envolver a un oponente de cualquier tamaño hasta ahogarlo.', 'Pokémon Gas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (93, 'Haunter', 8, 4, 0.1, 1.6, 45, 50, 45, 115, 55, 95, 'Cuando tienes la sensación de que te están observando, seguro que es porque haunter está cerca.', 'Pokémon Gas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (94, 'Gengar', 8, 4, 40.5, 1.5, 60, 65, 60, 130, 75, 110, 'Se esconde entre las sombras. Se dice que donde gengar acecha, la temperatura baja 5 °c.', 'Pokémon Sombra');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (95, 'Onix', 6, 5, 210.0, 8.8, 35, 45, 160, 30, 45, 70, 'Cava a gran velocidad en busca de comida. Los túneles que deja son usados por los diglett.', 'Pokémon Serpiente Roca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (96, 'Drowzee', 14, NULL, 32.4, 1.0, 60, 48, 45, 43, 90, 42, 'Adormece a sus enemigos y se come sus sueños. a veces se pone enfermo si come pesadillas.', 'Pokémon Hipnosis');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (97, 'Hypno', 14, NULL, 75.6, 1.6, 85, 73, 70, 73, 115, 67, 'Lleva un péndulo en la mano. Una vez, hizo desaparecer a un niño al que había hipnotizado.', 'Pokémon Hipnosis');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (98, 'Krabby', 11, NULL, 6.5, 0.4, 30, 105, 90, 25, 25, 50, 'Ante el peligro, se camufla con las burbujas que desprende su boca, para parecer más grande.', 'Pokémon Cangrejo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (99, 'Kingler', 11, NULL, 60.0, 1.3, 55, 130, 115, 50, 50, 75, 'La pinza tan grande que tiene posee una fuerza de 10 000 caballos de potencia. Pero, por su gran tamaño, cuesta moverla.', 'Pokémon Tenaza');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (100, 'Voltorb', 13, NULL, 10.4, 0.5, 40, 30, 50, 55, 55, 100, 'Fue descubierto cuando se crearon las poké balls. se dice que tiene algo que ver con ellas.', 'Pokémon Bola');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (101, 'Electrode', 13, NULL, 66.6, 1.2, 60, 50, 70, 80, 80, 150, 'Explotan a la mínima. Por eso se les tiene mucho miedo. Estos pokémon reciben el mote de bomba ball.', 'Pokémon Bola');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (102, 'Exeggcute', 12, 14, 2.5, 0.4, 60, 40, 80, 60, 45, 40, 'Estos seis huevos se comunican por telepatía. Si se separan, se pueden reunir rápidamente.', 'Pokémon Huevo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (103, 'Exeggutor', 12, 14, 120.0, 2.0, 95, 95, 85, 125, 75, 55, 'Sus tres cabezas piensan de forma independiente. sin embargo, son amigas y no suelen discutir nunca.', 'Pokémon Coco');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (104, 'Cubone', 5, NULL, 6.5, 0.4, 50, 50, 95, 40, 50, 35, 'Lleva puesto el cráneo de su madre. Cuando se siente solo se pone a gritar muy fuerte.', 'Pokémon Solitario');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (105, 'Marowak', 5, NULL, 45.0, 1.0, 60, 80, 110, 50, 80, 45, 'Es pequeño y siempre ha sido muy débil. Cuando empezó a usar huesos, se volvió más violento.', 'Pokémon Apilahueso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (106, 'Hitmonlee', 2, NULL, 49.8, 1.5, 50, 120, 53, 35, 110, 87, 'Encoge y estira las patas a su antojo. Cuando las estira, es capaz de propinar una buena patada al enemigo.', 'Pokémon Patada');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (107, 'Hitmonchan', 2, NULL, 50.2, 1.4, 50, 105, 79, 35, 110, 76, 'Los potentes golpes de sus brazos pueden pulverizar el hormigón. Descansa tras luchar tres minutos.', 'Pokémon Puñetazo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (108, 'Lickitung', 1, NULL, 65.5, 1.2, 90, 55, 75, 60, 75, 30, 'Su larga lengua, recubierta de saliva pegajosa, se pega a todo, por lo que es muy útil.', 'Pokémon Lametazo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (109, 'Koffing', 4, NULL, 1.0, 0.6, 40, 65, 95, 60, 45, 35, 'Tiene forma de globo y es muy ligero. Está compuesto por gases tóxicos y apesta.', 'Pokémon Gas Venenoso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (110, 'Weezing', 4, NULL, 9.5, 1.2, 65, 90, 120, 85, 70, 60, 'Si uno de los gemelos koffing se infla, el otro se desinfla. Mezclan constantemente sus venenosos gases.', 'Pokémon Gas Venenoso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (111, 'Rhyhorn', 5, 6, 115.0, 1.0, 80, 85, 95, 30, 30, 25, 'Es muy fuerte, pero no especialmente listo. Es capaz de derribar rascacielos usando placaje varias veces.', 'Pokémon Clavos');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (112, 'Rhydon', 5, 6, 120.0, 1.9, 105, 130, 120, 45, 45, 40, 'La piel le sirve de escudo protector. Puede vivir en lava líquida a 2000 °c de temperatura.', 'Pokémon Taladro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (113, 'Chansey', 1, NULL, 34.6, 1.1, 250, 5, 5, 35, 105, 50, 'Se dice que reparte felicidad. Se caracteriza por su compasión y reparte sus huevos entre la gente herida.', 'Pokémon Huevo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (114, 'Tangela', 12, NULL, 35.0, 1.0, 65, 55, 115, 100, 40, 60, 'Se camufla con la multitud de lianas que envuelven su cuerpo y que no dejan de crecer a lo largo de toda su vida.', 'Pokémon Enredadera');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (115, 'Kangaskhan', 1, NULL, 80.0, 2.2, 105, 95, 80, 40, 80, 90, 'Lleva a su cría en la bolsa de su panza. Solo deja que su cría salga a jugar cuando no siente peligro.', 'Pokémon Maternal');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (116, 'Horsea', 11, NULL, 8.0, 0.4, 30, 40, 70, 70, 25, 60, 'Es famoso por derribar a bichos voladores lanzando tinta desde la superficie del agua.', 'Pokémon Dragón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (117, 'Seadra', 11, NULL, 25.0, 1.2, 55, 65, 95, 95, 45, 85, 'Las afiladas púas que le recubren el cuerpo se le erizan y pueden causar el debilitamiento con solo tocarlo.', 'Pokémon Dragón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (118, 'Goldeen', 11, NULL, 15.0, 0.6, 45, 67, 60, 35, 50, 63, 'Nada a una velocidad de cinco nudos. Si siente peligro, golpea con su afilado cuerno.', 'Pokémon Pez Color');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (119, 'Seaking', 11, NULL, 39.0, 1.3, 80, 92, 65, 65, 80, 68, 'En otoño, cuando se reproducen, se les puede ver nadando con energía por ríos y arroyos.', 'Pokémon Pez Color');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (120, 'Staryu', 11, NULL, 34.5, 0.8, 30, 45, 55, 70, 55, 85, 'Aunque sus brazos se rompan podrán regenerarse, siempre y cuando su núcleo siga intacto.', 'Pokémon Estrella');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (121, 'Starmie', 11, 14, 80.0, 1.1, 60, 75, 85, 100, 85, 115, 'Su núcleo central brilla con los colores del arcoíris. para algunos tiene el valor de una gema.', 'Pokémon Misterioso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (122, 'Mr. Mime', 14, 18, 54.5, 1.3, 40, 45, 65, 100, 120, 90, 'Para repeler ataques, solidifica el aire y crea muros invisibles con emanaciones de sus dedos.', 'Pokémon Barrera');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (123, 'Scyther', 7, 3, 56.0, 1.5, 70, 110, 80, 55, 80, 105, 'Destroza a su presa con las guadañas que tiene. no es común que use las alas para volar.', 'Pokémon Mantis');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (124, 'Jynx', 15, 14, 40.6, 1.4, 65, 50, 35, 115, 95, 95, 'Camina moviendo las caderas de forma llamativa. puede hacer que la gente baile a su ritmo.', 'Pokémon Forma Humana');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (125, 'Electabuzz', 13, NULL, 30.0, 1.1, 65, 83, 57, 95, 85, 105, 'Por la superficie de su piel corre la electricidad. En la oscuridad, su cuerpo se torna blanquecino.', 'Pokémon Eléctrico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (126, 'Magmar', 10, NULL, 44.5, 1.3, 65, 95, 57, 100, 85, 93, 'A este pokémon se lo encontraron cerca de un volcán. esta criatura ígnea tiene una temperatura corporal de unos 1200 °c.', 'Pokémon Escupefuego');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (127, 'Pinsir', 7, NULL, 55.0, 1.5, 65, 125, 100, 55, 70, 85, 'Atrapa presas con sus pinzas hasta que las parte en dos. Lanza lejos lo que no puede quebrar.', 'Pokémon Escarabajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (128, 'Tauros', 1, NULL, 88.4, 1.4, 75, 100, 95, 40, 70, 110, 'Después de animarse a luchar fustigándose con sus tres colas, carga a toda velocidad.', 'Pokémon Toro Bravo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (129, 'Magikarp', 11, NULL, 10.0, 0.9, 20, 10, 55, 15, 20, 80, 'No es precisamente rápido ni fuerte. Es el pokémon más debilucho y simplón de todos los que hay.', 'Pokémon Pez');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (130, 'Gyarados', 11, 3, 235.0, 6.5, 95, 125, 79, 60, 100, 81, 'En la literatura antigua se dice que un gyarados rebosante de violencia arrasó un poblado.', 'Pokémon Atrocidad');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (131, 'Lapras', 11, 15, 220.0, 2.5, 130, 85, 80, 85, 95, 60, 'Son buenos de corazón. Muchos fueron capturados por ser tan pacíficos. Ahora hay muchos menos.', 'Pokémon Transporte');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (132, 'Ditto', 1, NULL, 4.0, 0.3, 48, 48, 48, 48, 48, 48, 'Puede alterar por completo su estructura celular para emular cualquier objeto que vea.', 'Pokémon Transformación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (133, 'Eevee', 1, NULL, 6.5, 0.3, 55, 55, 50, 45, 65, 55, 'Un extraño pokémon que se adapta a los entornos más hostiles gracias a sus diferentes evoluciones.', 'Pokémon Evolución');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (134, 'Vaporeon', 11, NULL, 29.0, 1.0, 130, 65, 60, 110, 95, 65, 'Prefiere las bellas costas. Con células parecidas al agua, podría fundirse en la misma.', 'Pokémon Burbuja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (135, 'Jolteon', 13, NULL, 24.5, 0.8, 65, 65, 60, 110, 95, 130, 'Todos los pelos de su cuerpo se ponen de punta si empieza a cargarse de electricidad.', 'Pokémon Relámpago');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (136, 'Flareon', 10, NULL, 25.0, 0.9, 65, 130, 60, 95, 110, 65, 'Almacena llamas en su cuerpo, que alcanza una temperatura de 900 °c antes de un combate.', 'Pokémon Llama');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (137, 'Porygon', 1, NULL, 36.5, 0.8, 65, 60, 70, 85, 75, 40, 'Pokémon creado por el hombre tras muchas investigaciones. Sus habilidades son básicas.', 'Pokémon Virtual');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (138, 'Omanyte', 6, 11, 7.5, 0.4, 35, 40, 100, 90, 55, 35, 'Pokémon prehistórico que vivió en el océano primordial. Para nadar se valía de sus 10 tentáculos.', 'Pokémon Espiral');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (139, 'Omastar', 6, 11, 35.0, 1.0, 70, 60, 125, 115, 70, 55, 'Tiene los tentáculos tan desarrollados que le sirven de manos y pies. Con ellos atrapa a su presa y le da un bocado.', 'Pokémon Espiral');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (140, 'Kabuto', 6, 11, 11.5, 0.5, 30, 80, 90, 55, 45, 55, 'Se cree que habitó las playas hace 300 millones de años. Se protege con una dura concha.', 'Pokémon Armazón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (141, 'Kabutops', 6, 11, 40.5, 1.3, 60, 115, 105, 65, 70, 80, 'Repliega sus extremidades en el agua para hacerse más compacto, y mueve su concha para nadar rápido.', 'Pokémon Armazón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (142, 'Aerodactyl', 6, 3, 59.0, 1.8, 80, 105, 65, 60, 75, 130, 'Se regeneró a partir de material genético de un dinosaurio encontrado en ámbar. Cuando vuela emite escandalosos alaridos.', 'Pokémon Fósil');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (143, 'Snorlax', 1, NULL, 460.0, 2.1, 160, 110, 65, 65, 110, 30, 'No se encuentra satisfecho hasta que no se come 400 kg de comida cada día. Cuando acaba de comer, se queda dormido.', 'Pokémon Dormir');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (144, 'Articuno', 15, 3, 55.4, 1.7, 90, 85, 100, 95, 125, 85, 'Legendario pokémon pájaro capaz de generar ventiscas congelando la humedad del aire.', 'Pokémon Congelar');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (145, 'Zapdos', 13, 3, 52.6, 1.6, 90, 90, 85, 125, 90, 100, 'Es un legendario pájaro pokémon. Dicen que aparece entre las nubes lanzando enormes rayos brillantes.', 'Pokémon Eléctrico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (146, 'Moltres', 10, 3, 60.0, 2.0, 90, 100, 90, 125, 85, 90, 'Es más conocido como el legendario pájaro de fuego. con cada aleteo crea brillantes llamas.', 'Pokémon Llama');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (147, 'Dratini', 16, NULL, 3.3, 1.8, 41, 64, 45, 50, 50, 50, 'Se le llama el pokémon espejismo porque son muy pocos los que lo han visto. Se encontró su muda.', 'Pokémon Dragón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (148, 'Dragonair', 16, NULL, 16.5, 4.0, 61, 84, 65, 70, 70, 70, 'Sus cristalinos orbes parecen darle al pokémon el poder de controlar el clima libremente.', 'Pokémon Dragón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (149, 'Dragonite', 16, 3, 210.0, 2.2, 91, 134, 95, 100, 100, 80, 'A pesar del tamaño que tiene y de lo pesado que es, puede volar. Es capaz de dar la vuelta al mundo en solo 16 horas.', 'Pokémon Dragón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (150, 'Mewtwo', 14, NULL, 122.0, 2.0, 106, 110, 90, 154, 90, 130, 'Fue creado por un científico tras años de horribles experimentos de ingeniería genética.', 'Pokémon Genético');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (151, 'Mew', 14, NULL, 4.0, 0.4, 100, 100, 100, 100, 100, 100, 'Varios científicos lo consideran el antecesor de los pokémon porque usa todo tipo de movimientos.', 'Pokémon Nueva Especie');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (152, 'Chikorita', 12, NULL, 6.4, 0.9, 45, 49, 65, 49, 65, 45, 'Un dulce aroma se desprende de la hoja de su cabeza. Es dócil y le encanta absorber los rayos de sol.', 'Pokémon Hoja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (153, 'Bayleef', 12, NULL, 15.8, 1.2, 60, 62, 80, 63, 80, 60, 'Emana un aroma picante de su cuello. El aroma actúa como estimulante para recuperar salud.', 'Pokémon Hoja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (154, 'Meganium', 12, NULL, 100.5, 1.8, 80, 82, 100, 83, 100, 80, 'El olor que desprenden sus pétalos contiene una sustancia que calma el instinto agresivo.', 'Pokémon Hierba');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (155, 'Cyndaquil', 10, NULL, 7.9, 0.5, 39, 52, 43, 60, 50, 65, 'Es un pokémon muy tímido. Si se asusta, las llamas de su lomo arden con más fuerza.', 'Pokémon Ratón Fuego');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (156, 'Quilava', 10, NULL, 19.0, 0.9, 58, 64, 58, 80, 65, 80, 'Antes de empezar a luchar, se gira ante su rival para alardear del alcance y la fuerza de sus llamas.', 'Pokémon Volcán');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (157, 'Typhlosion', 10, NULL, 79.5, 1.7, 78, 84, 78, 109, 85, 100, 'Si su furia crece, se calienta tanto que cualquier cosa que toque se prenderá al instante.', 'Pokémon Volcán');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (158, 'Totodile', 11, NULL, 9.5, 0.6, 50, 65, 64, 44, 48, 43, 'Es pequeño pero violento. No dudará en morder cualquier cosa que se mueva cerca.', 'Pokémon Fauces');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (159, 'Croconaw', 11, NULL, 25.0, 1.1, 65, 80, 80, 59, 63, 58, 'Si pierde un colmillo, le crecerá otro en su lugar. siempre tiene 48 colmillos en su boca.', 'Pokémon Fauces');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (160, 'Feraligatr', 11, NULL, 88.8, 2.3, 85, 105, 100, 79, 83, 78, 'Se suele mover lento, pero alcanza velocidades de vértigo al atacar y morder a sus enemigos.', 'Pokémon Fauces');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (161, 'Sentret', 1, NULL, 6.0, 0.8, 35, 46, 34, 35, 45, 20, 'Cuando hace de centinela, avisa del peligro a los demás, chillando y dando golpes al suelo con la cola.', 'Pokémon Explorador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (162, 'Furret', 1, NULL, 32.5, 1.8, 85, 76, 64, 45, 55, 90, 'Acurruca a sus crías para que duerman. Acorrala a los enemigos con su enorme velocidad.', 'Pokémon Alargado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (163, 'Hoothoot', 1, 3, 21.2, 0.7, 60, 30, 30, 36, 56, 50, 'Su percepción del tiempo es exacta. Ocurra lo que ocurra, lleva el ritmo moviendo la cabeza.', 'Pokémon Búho');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (164, 'Noctowl', 1, 3, 40.8, 1.6, 100, 50, 50, 86, 96, 70, 'Sus ojos están especialmente adaptados. concentran la luz para poder ver en la oscuridad.', 'Pokémon Búho');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (165, 'Ledyba', 7, 3, 10.8, 1.0, 40, 20, 30, 40, 80, 55, 'Cuando llega el frío, se reúnen muchos ledyba de todas partes para darse calor.', 'Pokémon 5 Estrellas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (166, 'Ledian', 7, 3, 35.6, 1.4, 55, 35, 50, 55, 110, 85, 'Cuando parpadean las estrellas en el cielo nocturno, se mece desprendiendo un polvo brillante.', 'Pokémon 5 Estrellas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (167, 'Spinarak', 7, 4, 8.5, 0.5, 40, 60, 40, 40, 40, 30, 'Teje su telaraña con hilo fino pero resistente. Espera pacientemente a que caiga su presa.', 'Pokémon Escupesoga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (168, 'Ariados', 7, 4, 33.5, 1.1, 70, 90, 70, 60, 70, 40, 'Teje su telaraña desde atrás y desde su boca. Es muy difícil saber dónde está cada cual.', 'Pokémon Pata Larga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (169, 'Crobat', 4, 3, 75.0, 1.8, 85, 90, 80, 70, 80, 130, 'Es tan silencioso cuando vuela en la oscuridad con sus cuatro alas, que cuesta percibirlo cuando se acerca.', 'Pokémon Murciélago');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (170, 'Chinchou', 11, 13, 12.0, 0.5, 75, 38, 38, 56, 56, 67, 'En el oscuro fondo del océano, su único modo de comunicarse es emitiendo luces parpadeantes.', 'Pokémon Rape');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (171, 'Lanturn', 11, 13, 22.5, 1.2, 125, 58, 58, 76, 76, 67, 'La luz de lanturn se puede ver desde lo más profundo. Se le llama la estrella de alta mar.', 'Pokémon Luz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (172, 'Pichu', 13, NULL, 2.0, 0.3, 20, 40, 15, 35, 35, 60, 'Cuando juegan, se tocan las colas y emiten chispas. al parecer, es una prueba de valor.', 'Pokémon Ratoncito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (173, 'Cleffa', 18, NULL, 3.0, 0.3, 50, 25, 28, 45, 55, 15, 'Por su inusual forma estrellada, la gente cree que procede de un meteorito que cayó a la tierra.', 'Pokémon Estrella');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (174, 'Igglybuff', 1, 18, 1.0, 0.3, 90, 30, 15, 40, 20, 15, 'Para caminar, en lugar de usar los pies que tiene, opta por dar botes con su cuerpo redondo y blandito.', 'Pokémon Globo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (175, 'Togepi', 18, NULL, 1.5, 0.3, 35, 20, 65, 40, 65, 20, 'El cascarón parece estar lleno de alegría. Dicen que trae buena suerte si es tratado con cariño.', 'Pokémon Bolaclavos');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (176, 'Togetic', 18, 3, 3.2, 0.6, 55, 40, 85, 80, 105, 40, 'Si no está con gente amable, se entristece. Puede flotar en el aire sin mover las alas.', 'Pokémon Felicidad');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (177, 'Natu', 14, 3, 2.0, 0.2, 40, 50, 45, 70, 45, 70, 'Va dando saltitos porque sus alas no han crecido lo suficiente. Siempre está pendiente de algo.', 'Pokémon Pajarito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (178, 'Xatu', 14, 3, 15.0, 1.5, 65, 75, 70, 95, 70, 95, 'Cuando se pone a meditar al amanecer, pierde la noción del tiempo. Puede pasarse así todo el día.', 'Pokémon Místico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (179, 'Mareep', 13, NULL, 7.8, 0.6, 55, 40, 40, 65, 45, 35, 'Su pelaje lanudo dobla su volumen al acumular electricidad estática. Tocarlo puede dar calambre.', 'Pokémon Lana');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (180, 'Flaaffy', 13, NULL, 13.3, 0.8, 70, 55, 55, 80, 60, 45, 'Su esponjosa lana almacena electricidad. Su piel gomosa evita que sea electrocutado.', 'Pokémon Lana');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (181, 'Ampharos', 13, NULL, 61.5, 1.4, 90, 75, 85, 115, 90, 55, 'La punta de su cola reluce brillantemente y puede ser vista desde lejos. Es usado como faro.', 'Pokémon Luz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (182, 'Bellossom', 12, NULL, 5.8, 0.4, 75, 80, 95, 90, 100, 50, 'Los bellossom suelen reunirse para bailar. Dicen que sus danzas son un ritual para invocar al sol.', 'Pokémon Flor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (183, 'Marill', 11, 18, 8.5, 0.4, 70, 20, 50, 20, 50, 40, 'Tiene una piel que repele el agua. De hecho, puede pasarse horas jugando en el agua sin mojarse.', 'Pokémon Ratón Agua');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (184, 'Azumarill', 11, 18, 28.5, 0.8, 100, 50, 80, 60, 80, 50, 'Sus largas orejas son sensores que pueden percibir movimientos de seres vivos en el lecho del río.', 'Pokémon Conejo Agua');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (185, 'Sudowoodo', 6, NULL, 38.0, 1.2, 70, 100, 115, 30, 65, 30, 'Aunque pretende ser un árbol, en su composición se parece más a una roca que a una planta.', 'Pokémon Imitación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (186, 'Politoed', 11, NULL, 33.9, 1.1, 90, 75, 75, 90, 100, 70, 'Cuando tres o más de estos se juntan, emiten un sonido muy fuerte parecido a un bramido.', 'Pokémon Rana');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (187, 'Hoppip', 12, 3, 0.5, 0.4, 35, 35, 40, 35, 55, 50, 'Su cuerpo es tan ligero, que debe agarrarse bien al suelo con las patas para no irse con el viento.', 'Pokémon Algadón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (188, 'Skiploom', 12, 3, 1.0, 0.6, 55, 45, 50, 45, 65, 80, 'La flor de su cabeza se abre y cierra según suba o baje la temperatura.', 'Pokémon Algadón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (189, 'Jumpluff', 12, 3, 3.0, 0.8, 75, 55, 70, 55, 95, 110, 'Los vientos estacionales lo llevan por todo el mundo, donde va soltando sus esporas.', 'Pokémon Algadón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (190, 'Aipom', 1, NULL, 11.5, 0.8, 55, 70, 55, 40, 55, 85, 'Vive en lo alto de los árboles. Cuando salta de rama en rama, usa su cola para equilibrarse.', 'Pokémon Cola Larga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (191, 'Sunkern', 12, NULL, 1.8, 0.3, 30, 30, 30, 30, 30, 30, 'Puede caer del cielo de repente. Si le ataca un spearow, moverá violentamente sus hojas.', 'Pokémon Semilla');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (192, 'Sunflora', 12, NULL, 8.5, 0.8, 75, 75, 55, 105, 85, 30, 'A medida que se acerca el verano, va adquiriendo un color más vivo e intenso en los pétalos de la cara.', 'Pokémon Sol');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (193, 'Yanma', 7, 3, 38.0, 1.2, 65, 65, 45, 75, 45, 95, 'Su campo visual es de 360° sin girar la cabeza. detecta hasta las presas que están a su espalda.', 'Pokémon Ala Clara');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (194, 'Wooper', 11, 5, 8.5, 0.4, 55, 45, 45, 25, 25, 15, 'Este pokémon vive en aguas frías. Sale del agua para buscar comida cuando refresca el ambiente.', 'Pokémon Pez Agua');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (195, 'Quagsire', 11, 5, 75.0, 1.4, 95, 85, 85, 65, 65, 35, 'Es un pokémon bastante perezoso. Para pescar permanece en el fondo del río con la boca abierta.', 'Pokémon Pez Agua');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (196, 'Espeon', 14, NULL, 26.5, 0.9, 65, 65, 60, 130, 95, 110, 'Al predecir el siguiente movimiento de su rival, chasquea el extremo de la cola bífida que tiene.', 'Pokémon Sol');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (197, 'Umbreon', 17, NULL, 27.0, 1.0, 95, 65, 110, 60, 130, 65, 'Creado cuando la luz lunar alteró la estructura genética de eevee. Busca presas en la oscuridad.', 'Pokémon Luz Lunar');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (198, 'Murkrow', 17, 3, 2.1, 0.5, 60, 85, 42, 85, 42, 91, 'Esconde objetos brillantes en un lugar secreto. meowth y murkrow suelen saquear su escondite.', 'Pokémon Oscuridad');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (199, 'Slowking', 11, 14, 79.5, 2.0, 95, 75, 80, 100, 110, 30, 'Su intelecto e intuición son increíbles. No pierde la compostura en ninguna situación.', 'Pokémon Regio');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (200, 'Misdreavus', 8, NULL, 1.0, 0.7, 60, 60, 60, 85, 85, 85, 'Se deleita con el miedo, que absorbe con sus esferas rojas. Por el día, duerme en la oscuridad.', 'Pokémon Chirrido');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (201, 'Unown', 14, NULL, 5.0, 0.5, 48, 72, 48, 72, 48, 48, 'Su cuerpo fino y plano aparece siempre en los muros. su forma parece tener algún significado.', 'Pokémon Símbolo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (202, 'Wobbuffet', 14, NULL, 28.5, 1.3, 190, 33, 58, 33, 58, 33, 'Odia la luz y las sacudidas. Si le atacan, infla su cuerpo para aumentar su contraataque.', 'Pokémon Paciente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (203, 'Girafarig', 1, 14, 41.5, 1.5, 70, 80, 65, 90, 65, 85, 'Cuando duerme, la cabeza de su cola se queda vigilando, ya que ella no necesita dormir.', 'Pokémon Gran Cuello');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (204, 'Pineco', 7, NULL, 7.2, 0.6, 50, 65, 90, 35, 35, 15, 'Le gusta engrosar su coraza añadiendo cortezas de árbol. El aumento de peso no le molesta.', 'Pokémon Larva');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (205, 'Forretress', 7, 9, 125.8, 1.2, 75, 90, 140, 60, 60, 40, 'Se protege con su concha de acero de tal manera que solo se pueden ver sus ojos.', 'Pokémon Larva');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (206, 'Dunsparce', 1, NULL, 14.0, 1.5, 100, 70, 70, 65, 65, 45, 'Este pokémon escapa hacia atrás perforando el suelo con su cola, si es descubierto.', 'Pokémon Serpiente Tierra');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (207, 'Gligar', 5, 3, 64.8, 1.1, 65, 75, 105, 35, 65, 85, 'A veces se sujeta a los precipicios. Cuando divisa una presa, despliega sus alas y planea para atacar.', 'Pokémon Escorpiala');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (208, 'Steelix', 9, 5, 400.0, 9.2, 75, 85, 200, 55, 65, 30, 'Su cuerpo es más duro que cualquier metal debido a la gran presión y calor de su hábitat.', 'Pokémon Serpiente Férrea');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (209, 'Snubbull', 18, NULL, 7.8, 0.6, 60, 80, 50, 40, 40, 30, 'Muy travieso por naturaleza, a muchas mujeres les gusta jugar con él porque es muy cariñoso.', 'Pokémon Hada');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (210, 'Granbull', 18, NULL, 48.7, 1.4, 90, 120, 75, 60, 60, 45, 'A pesar de su aspecto, es muy tímido. Sin embargo, cuando se enfada, ataca con sus colmillos.', 'Pokémon Hada');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (211, 'Qwilfish', 11, 4, 3.9, 0.5, 65, 95, 85, 55, 55, 85, 'Para arrojar sus venenosas púas, infla su cuerpo bebiendo hasta diez litros de agua de una sola vez.', 'Pokémon Globo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (212, 'Scizor', 7, 9, 118.0, 1.8, 70, 130, 100, 55, 80, 65, 'Las pinzas que posee tienen acero. Con ellas, no hay nada que se le resista. Rompen todo lo que pillan.', 'Pokémon Tenaza');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (213, 'Shuckle', 7, 6, 20.5, 0.6, 20, 10, 230, 10, 230, 5, 'Las bayas que almacena en su caparazón con forma de tarro se convierten al final en un espeso zumo.', 'Pokémon Moho');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (214, 'Heracross', 7, 2, 54.0, 1.5, 80, 125, 75, 40, 95, 85, 'Este fuerte pokémon clava su preciado cuerno en la tripa del enemigo, lo eleva y luego lo arroja.', 'Pokémon Cuerno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (215, 'Sneasel', 17, 15, 28.0, 0.9, 55, 95, 55, 35, 75, 115, 'Se alimenta de huevos robados en nidos. Clava sus afiladas garras en los puntos débiles de sus rivales.', 'Pokémon Garra Filo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (216, 'Teddiursa', 1, NULL, 8.8, 0.6, 60, 80, 50, 50, 50, 40, 'Si encuentra miel, brillará la marca de su frente. se lame las garras cuando están llenas de miel.', 'Pokémon Osito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (217, 'Ursaring', 1, NULL, 125.8, 1.8, 90, 130, 75, 75, 75, 55, 'Capaz de percibir cualquier tipo de aroma, encuentra sin problemas todo alimento enterrado bajo tierra.', 'Pokémon Hibernante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (218, 'Slugma', 10, NULL, 35.0, 0.7, 40, 40, 40, 70, 40, 20, 'Asiduo a las zonas volcánicas, se desliza lentamente en busca de lugares calientes.', 'Pokémon Lava');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (219, 'Magcargo', 10, 6, 55.0, 0.8, 60, 50, 120, 90, 80, 30, 'En ocasiones, su resbaladiza concha lanza intensas llamaradas que recorren todo su cuerpo.', 'Pokémon Lava');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (220, 'Swinub', 15, 5, 6.5, 0.4, 50, 50, 40, 30, 30, 50, 'Frota su hocico contra el suelo para desenterrar comida. A veces descubre aguas termales.', 'Pokémon Cerdo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (221, 'Piloswine', 15, 5, 55.8, 1.1, 100, 100, 80, 60, 60, 50, 'Aunque tiene las patas cortas, las fuertes pezuñas le permiten agarrarse bien al suelo, a pesar del hielo.', 'Pokémon Puerco');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (222, 'Corsola', 11, 6, 5.0, 0.6, 65, 55, 95, 65, 95, 35, 'Crece y muda continuamente. La parte superior de su cabeza es muy apreciada por su belleza.', 'Pokémon Coral');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (223, 'Remoraid', 11, NULL, 12.0, 0.6, 35, 65, 35, 65, 35, 65, 'Tiene muy buena puntería. Dispara agua a sus presas, hasta si se están moviendo, a más de 100 metros.', 'Pokémon Reactor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (224, 'Octillery', 11, NULL, 28.5, 0.9, 75, 105, 75, 105, 75, 45, 'Tiene querencia por los agujeros. Ataca escupiendo tinta metido en las grietas de rocas o en recipientes.', 'Pokémon Reactor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (225, 'Delibird', 15, 3, 16.0, 0.9, 45, 55, 45, 65, 45, 75, 'Transporta alimentos en su cola enrollada. Le gusta compartir su comida con los montañistas perdidos.', 'Pokémon Reparto');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (226, 'Mantine', 11, 3, 220.0, 2.1, 85, 40, 70, 80, 140, 70, 'Acumula velocidad nadando y puede saltar fuera del agua, alcanzando 100 metros de altura, para descender luego planeando.', 'Pokémon Milano');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (227, 'Skarmory', 9, 3, 50.5, 1.7, 65, 80, 140, 40, 70, 70, 'Después de anidar en zarzas, las alas de sus polluelos crecen fortalecidas por los rasguños.', 'Pokémon Coraza Ave');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (228, 'Houndour', 17, 10, 10.8, 0.6, 45, 60, 30, 80, 50, 65, 'Al amanecer, resuena por la zona su inquietante aullido para anunciar que es su territorio.', 'Pokémon Siniestro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (229, 'Houndoom', 17, 10, 35.0, 1.4, 75, 90, 50, 110, 80, 95, 'Hace mucho, la gente creía que sus horripilantes aullidos eran de la muerte personificada.', 'Pokémon Siniestro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (230, 'Kingdra', 11, 16, 152.0, 1.8, 75, 95, 95, 95, 95, 85, 'Dicen que suele esconderse en cuevas submarinas. puede crear torbellinos al bostezar.', 'Pokémon Dragón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (231, 'Phanpy', 5, NULL, 33.5, 0.5, 90, 60, 60, 40, 40, 40, 'Como muestra de afecto, golpea con su trompa. aunque es tan fuerte que te mandará lejos.', 'Pokémon Narizotas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (232, 'Donphan', 5, NULL, 120.0, 1.1, 90, 120, 120, 60, 60, 50, 'Tiene afilados y duros colmillos, y una gruesa piel. su placaje es tan fuerte que puede derribar una casa.', 'Pokémon Coraza');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (233, 'Porygon2', 1, NULL, 32.5, 0.6, 85, 80, 90, 105, 95, 60, 'La investigación mejoró sus habilidades. A veces, puede actuar de forma no programada.', 'Pokémon Virtual');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (234, 'Stantler', 1, NULL, 71.2, 1.4, 73, 95, 62, 85, 65, 85, 'Mirar a su cornamenta provoca una extraña sensación de pérdida de equilibrio.', 'Pokémon Gran Cuerno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (235, 'Smeargle', 1, NULL, 58.0, 1.2, 55, 20, 35, 20, 45, 75, 'Marca su territorio usando su cola a modo de pincel. ha hecho más de 5000 marcas diferentes.', 'Pokémon Pintor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (236, 'Tyrogue', 2, NULL, 21.0, 0.7, 35, 35, 35, 35, 35, 35, 'Ataca siempre con mucha energía. Para fortalecerse a sí mismo, sigue luchando aunque pierda.', 'Pokémon Peleón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (237, 'Hitmontop', 2, NULL, 48.0, 1.4, 50, 95, 95, 35, 110, 70, 'Gira a gran velocidad al luchar. La fuerza centrífuga multiplica su poder de destrucción por diez.', 'Pokémon Boca Abajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (238, 'Smoochum', 15, 14, 6.0, 0.4, 45, 30, 15, 85, 65, 65, 'Sus labios son la parte más sensible de su cuerpo. siempre los usa para examinar las cosas.', 'Pokémon Beso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (239, 'Elekid', 13, NULL, 23.5, 0.6, 45, 63, 37, 65, 55, 95, 'Genera electricidad girando los brazos. No obstante, es incapaz de almacenar la energía creada.', 'Pokémon Eléctrico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (240, 'Magby', 10, NULL, 21.4, 0.7, 45, 75, 37, 70, 55, 83, 'Es pequeño, pero su cuerpo puede llegar a alcanzar 600 °c. Cuando inspira y espira, expulsa llamas por la nariz y por la boca.', 'Pokémon Ascuas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (241, 'Miltank', 1, NULL, 75.5, 1.2, 95, 80, 105, 40, 70, 100, 'Si tiene crías, la leche que produce contiene muchos más nutrientes de lo normal.', 'Pokémon Lechera');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (242, 'Blissey', 1, NULL, 46.8, 1.5, 255, 10, 10, 75, 135, 55, 'Los huevos que pone están llenos de felicidad. Solo con probar un poquito de uno, cualquiera sonríe.', 'Pokémon Felicidad');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (243, 'Raikou', 13, NULL, 178.0, 1.9, 90, 85, 75, 115, 100, 115, 'Las nubes de lluvia que lleva le permiten lanzar rayos a voluntad. Dicen que apareció con un rayo.', 'Pokémon Trueno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (244, 'Entei', 10, NULL, 198.0, 2.1, 115, 115, 85, 90, 75, 100, 'Un pokémon que corre por la tierra. Dicen que siempre nace uno cuando aparece un nuevo volcán.', 'Pokémon Volcán');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (245, 'Suicune', 11, NULL, 187.0, 2.0, 100, 75, 115, 90, 115, 85, 'Recorre el mundo para purificar agua contaminada. corre veloz con el viento del norte.', 'Pokémon Aurora');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (246, 'Larvitar', 6, 5, 72.0, 0.6, 50, 64, 50, 45, 50, 41, 'Se alimenta de tierra. Después de comer una gran montaña, se duerme y empieza a crecer.', 'Pokémon Piel Roca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (247, 'Pupitar', 6, 5, 152.0, 1.2, 70, 84, 70, 65, 70, 51, 'Su cuerpo es duro como la roca. Se autopropulsa como un cohete soltando los gases de su interior.', 'Pokémon Caparazón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (248, 'Tyranitar', 6, 17, 202.0, 2.0, 100, 134, 110, 95, 100, 61, 'En una de sus poderosas garras tiene el poder de hacer temblar la tierra y las montañas.', 'Pokémon Coraza');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (249, 'Lugia', 14, 3, 216.0, 5.2, 106, 90, 130, 90, 154, 110, 'Duerme en una dorsal marina. Si bate sus alas, puede causar tormentas de 40 días.', 'Pokémon Buceo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (250, 'Ho-Oh', 10, 3, 199.0, 3.8, 106, 130, 90, 110, 154, 90, 'Cuenta la leyenda que brilla en siete colores. Dicen que deja un arcoíris tras de sí al volar.', 'Pokémon Arcoíris');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (251, 'Celebi', 14, 12, 5.0, 0.6, 100, 100, 100, 100, 100, 100, 'Este pokémon vaga por el tiempo. La hierba y los árboles crecen por los campos por donde pasa.', 'Pokémon Viajetiempo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (252, 'Treecko', 12, NULL, 5.0, 0.5, 40, 45, 35, 65, 55, 70, 'Escala superficies verticales muy rápido. Con la cola detecta la humedad y prevé el tiempo que va a hacer.', 'Pokémon Geco Bosque');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (253, 'Grovyle', 12, NULL, 21.6, 0.9, 50, 65, 45, 85, 65, 95, 'Los desarrollados músculos que tiene en las extremidades inferiores le hacen ser muy ágil y le permiten dar grandes saltos.', 'Pokémon Geco Bosque');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (254, 'Sceptile', 12, NULL, 52.2, 1.7, 70, 85, 65, 105, 85, 120, 'Las hojas de sus brazos pueden cortar gruesos árboles. Invencible en los combates en la jungla.', 'Pokémon Monte');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (255, 'Torchic', 10, NULL, 2.5, 0.4, 45, 60, 40, 70, 50, 45, 'En su interior, guarda una llama que arde sin cesar. si se le abraza, se nota que tiene una temperatura muy alta.', 'Pokémon Polluelo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (256, 'Combusken', 10, 2, 19.5, 0.9, 60, 85, 60, 85, 60, 55, 'Su habilidad le permite dar diez patadas por segundo. lanza potentes gritos para intimidar.', 'Pokémon Ave Corral');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (257, 'Blaziken', 10, 2, 52.0, 1.9, 80, 120, 70, 110, 70, 80, 'Puede saltar edificios de 30 plantas. Con sus golpes de fuego quema a sus rivales.', 'Pokémon Llameante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (258, 'Mudkip', 11, NULL, 7.6, 0.4, 50, 70, 50, 50, 50, 40, 'Tiene una larga cola que le propulsa por el agua con mucha potencia. A pesar de ser pequeño, tiene mucha fuerza.', 'Pokémon Pez Lodo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (259, 'Marshtomp', 11, 5, 28.0, 0.7, 70, 85, 70, 60, 70, 50, 'Sus piernas robustas impiden que pierda el equilibrio y caiga, incluso en el barro. Duerme bajo la arena.', 'Pokémon Pez Lodo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (260, 'Swampert', 11, 5, 81.9, 1.5, 100, 110, 90, 85, 90, 60, 'Con sus brazos duros como el acero puede partir rocas gigantescas en mil pedazos de un solo golpe.', 'Pokémon Pez Lodo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (261, 'Poochyena', 17, NULL, 13.6, 0.5, 35, 55, 35, 30, 30, 35, 'Es muy persistente. Si se fija en una presa, no la dejará hasta que caiga agotada.', 'Pokémon Mordisco');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (262, 'Mightyena', 17, NULL, 37.0, 1.0, 70, 90, 70, 60, 60, 70, 'Siempre obedece a los buenos entrenadores. Está acostumbrado a vivir en jaurías en estado salvaje.', 'Pokémon Mordisco');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (263, 'Zigzagoon', 1, NULL, 17.5, 0.4, 38, 30, 41, 30, 41, 60, 'Se mueve en zigzag. Se le da bien encontrar objetos ocultos en la hierba e incluso enterrados.', 'Pokémon Mapachito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (264, 'Linoone', 1, NULL, 32.5, 0.5, 78, 70, 61, 50, 61, 100, 'En línea recta corre a algo más de 100 km/h, pero no es tan hábil si hay curvas de por medio.', 'Pokémon Lanzado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (265, 'Wurmple', 7, NULL, 3.6, 0.3, 45, 45, 35, 20, 30, 20, 'Le encanta comer hojas. Si le ataca un starly, se defenderá con sus púas.', 'Pokémon Gusano');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (266, 'Silcoon', 7, NULL, 10.0, 0.6, 50, 35, 55, 25, 25, 15, 'Se agarra con su seda a las ramas de un árbol y espera su evolución bebiendo agua de lluvia.', 'Pokémon Capullo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (267, 'Beautifly', 7, 3, 28.4, 1.0, 60, 70, 50, 100, 50, 65, 'Los dibujos de sus coloridas alas son muy peculiares. liba el néctar de las flores con su fina trompa.', 'Pokémon Mariposa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (268, 'Cascoon', 7, NULL, 11.5, 0.7, 50, 35, 55, 25, 25, 15, 'Está formado por seda que se endurece poco a poco. Si se resquebraja el capullo, está a punto de evolucionar.', 'Pokémon Capullo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (269, 'Dustox', 7, 4, 31.6, 1.2, 60, 50, 70, 50, 90, 65, 'Pokémon nocturno. Atraído por las luces de la ciudad, se come las hojas y arbustos de las avenidas.', 'Pokémon Polilla Ven.');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (270, 'Lotad', 11, 12, 2.6, 0.5, 40, 30, 30, 40, 50, 30, 'Parece una planta acuática. Hace de ferry para los pokémon que no saben nadar.', 'Pokémon Lenteja Agua');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (271, 'Lombre', 11, 12, 32.5, 1.2, 60, 50, 50, 60, 70, 50, 'Vive en el agua y, cuando hace sol, se queda en la orilla. De día duerme sobre las algas y de noche entra en acción.', 'Pokémon Alegre');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (272, 'Ludicolo', 11, 12, 55.0, 1.5, 80, 70, 70, 90, 100, 70, 'Cuando oye música alegre, sus músculos se llenan de energía, y no puede evitar ponerse a bailar.', 'Pokémon Optimista');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (273, 'Seedot', 12, NULL, 4.0, 0.5, 40, 40, 50, 30, 30, 30, 'Se cuelga de las ramas con el extremo de su cabeza. a veces se cae cuando soplan vientos fuertes.', 'Pokémon Bellota');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (274, 'Nuzleaf', 12, 17, 28.0, 1.0, 70, 70, 40, 60, 40, 60, 'Cuando usa la hoja de su cabeza como flauta, asusta a la gente. Vive en lo más profundo de los bosques.', 'Pokémon Astuto');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (275, 'Shiftry', 12, 17, 59.6, 1.3, 90, 100, 60, 90, 60, 80, 'Habita los bosques y se le tiene miedo. Es capaz de leer los pensamientos del rival y actuar en consecuencia.', 'Pokémon Malvado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (276, 'Taillow', 1, 3, 2.3, 0.3, 40, 55, 30, 30, 30, 85, 'Es un pokémon valiente que no teme a los rivales fuertes. Vuela en busca de climas templados.', 'Pokémon Pequebuche');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (277, 'Swellow', 1, 3, 19.8, 0.7, 60, 85, 60, 75, 50, 125, 'Cuando se encuentra bien, pone firmes las plumas de la cola doble que tiene. Remonta el vuelo de forma muy elegante.', 'Pokémon Buche');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (278, 'Wingull', 11, 3, 9.5, 0.6, 40, 30, 30, 55, 30, 85, 'Sus largas alas aprovechan los vientos marinos para planear. Cuando descansa, las pliega.', 'Pokémon Gaviota');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (279, 'Pelipper', 11, 3, 28.0, 1.2, 60, 50, 100, 95, 70, 65, 'Hunde su gran pico en el mar para sacar numerosas presas junto con mucha agua.', 'Pokémon Ave Agua');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (280, 'Ralts', 14, 18, 6.6, 0.4, 28, 25, 25, 45, 35, 40, 'Si sus cuernos perciben emociones positivas de personas o pokémon, su cuerpo se calienta un poco.', 'Pokémon Sensible');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (281, 'Kirlia', 14, 18, 20.2, 0.8, 38, 35, 35, 65, 55, 50, 'Al percibir la alegría de su entrenador refuerza sus ataques psicoquinéticos. Cuando está contento, da vueltas y baila.', 'Pokémon Sensorio');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (282, 'Gardevoir', 14, 18, 48.4, 1.6, 68, 65, 65, 125, 115, 80, 'Para proteger a su entrenador, emplea todo su poder psíquico en crear un pequeño agujero negro.', 'Pokémon Envolvente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (283, 'Surskit', 7, 11, 1.7, 0.5, 40, 30, 32, 50, 52, 65, 'Vive en estanques, pero cuando se desata una tormenta puede aparecer en los charcos que se forman en las ciudades.', 'Pokémon Zapatero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (284, 'Masquerain', 7, 3, 3.6, 0.8, 70, 60, 62, 100, 82, 80, 'Sus antenas tienen dibujos de ojos, mientras que sus alas le permiten volar en cualquier dirección.', 'Pokémon Globocular');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (285, 'Shroomish', 12, NULL, 4.5, 0.4, 60, 40, 60, 40, 60, 35, 'Prefiere los sitios húmedos. De día está tranquilamente en el bosque, a la sombra. Expulsa polvo tóxico por la cabeza.', 'Pokémon Hongo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (286, 'Breloom', 12, 2, 39.2, 1.2, 60, 130, 80, 60, 60, 70, 'Sus cortos brazos se expanden cuando da puñetazos. tiene una técnica pugilística muy depurada.', 'Pokémon Hongo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (287, 'Slakoth', 1, NULL, 24.0, 0.8, 60, 60, 60, 35, 35, 30, 'Slakoth se pasa todo el día holgazaneando y solo con verlo a uno le entra mucha pereza.', 'Pokémon Gandul');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (288, 'Vigoroth', 1, NULL, 46.5, 1.4, 80, 80, 80, 55, 55, 90, 'Su corazón palpita a un ritmo frenético, haciendo que su sangre bulla, así que no puede estarse quieto ni un segundo.', 'Pokémon Mono Feroz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (289, 'Slaking', 1, NULL, 130.5, 2.0, 150, 160, 100, 95, 65, 100, 'El pokémon más perezoso del mundo. Cambia de lugar para coger la comida al alcance de su mano.', 'Pokémon Holgazán');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (290, 'Nincada', 7, 5, 5.5, 0.5, 31, 45, 90, 30, 30, 40, 'Como se ha pasado casi toda la vida viviendo bajo tierra, no ve bien. Las antenas que tiene le ayudan a orientarse.', 'Pokémon Aprendiz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (291, 'Ninjask', 7, 3, 12.0, 0.8, 61, 90, 45, 50, 50, 160, 'Escuchar su zumbido de forma continuada provoca jaquecas. Casi no se le ve de lo rápido que es.', 'Pokémon Ninja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (292, 'Shedinja', 7, 8, 1.2, 0.8, 1, 90, 45, 30, 30, 40, 'Es el caparazón de un bicho que cobró vida. Si alguien lo mira por la espalda, le robará el alma.', 'Pokémon Muda');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (293, 'Whismur', 1, NULL, 16.3, 0.6, 64, 51, 23, 51, 23, 28, 'Normalmente murmulla, pero cuando siente que hay peligro se pone a chillar. Solo para cuando se le cierran las orejas.', 'Pokémon Susurro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (294, 'Loudred', 1, NULL, 40.5, 1.0, 84, 71, 43, 71, 43, 48, 'Aspira profundamente y luego, usando sus fuertes abdominales, suelta un potente vozarrón.', 'Pokémon Chillón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (295, 'Exploud', 1, NULL, 84.0, 1.5, 104, 91, 63, 91, 73, 68, 'Su aullido se puede oír a 10 km. Emite todo tipo de sonidos desde los tubos de su cuerpo.', 'Pokémon Escandaloso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (296, 'Makuhita', 2, NULL, 86.4, 1.0, 72, 60, 30, 20, 30, 25, 'Endurece su cuerpo golpeando grandes árboles sin perder jamás el ánimo.', 'Pokémon Valiente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (297, 'Hariyama', 2, NULL, 253.8, 2.3, 144, 120, 60, 40, 60, 50, 'Para aumentar su fuerza, da un pisotón en el suelo. puede lanzar un camión de 10 toneladas por los aires de un puñetazo.', 'Pokémon Empuje');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (298, 'Azurill', 1, 18, 2.0, 0.2, 50, 20, 40, 20, 40, 20, 'Vive cerca del agua. En la tierra se mueve muy rápido botando sobre su gran cola.', 'Pokémon A Topos');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (299, 'Nosepass', 6, NULL, 97.0, 1.0, 30, 45, 135, 45, 90, 30, 'Siempre apunta al norte con su nariz imantada. Con ella reviste su cuerpo de objetos metálicos para protegerse.', 'Pokémon Brújula');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (300, 'Skitty', 1, NULL, 11.0, 0.6, 50, 45, 45, 35, 35, 50, 'No puede evitar ir tras las cosas que se mueven. hasta corre en círculos persiguiéndose la cola.', 'Pokémon Gatito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (301, 'Delcatty', 1, NULL, 32.6, 1.1, 70, 65, 65, 55, 55, 90, 'No soporta los lugares sucios. Le encanta acicalarse el pelaje cuando está a gusto en un sitio.', 'Pokémon Cursi');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (302, 'Sableye', 17, 8, 11.0, 0.5, 50, 75, 75, 65, 65, 50, 'Se oculta en cavernas oscuras. De tanto comer gemas, sus ojos son piedras preciosas.', 'Pokémon Oscuridad');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (303, 'Mawile', 9, 18, 11.5, 0.6, 50, 85, 85, 55, 55, 50, 'Engaña con su aspecto dócil; hace que el rival se confíe y le da un mordisco con sus enormes e implacables mandíbulas.', 'Pokémon Tramposo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (304, 'Aron', 9, 6, 60.0, 0.4, 50, 70, 100, 40, 40, 30, 'Suele vivir en lugares inaccesibles de las montañas, pero el hambre lo empuja a comer raíles y coches.', 'Pokémon Coraza Férrea');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (305, 'Lairon', 9, 6, 120.0, 0.9, 60, 90, 140, 50, 50, 40, 'Le encanta comer hierro. Lucha por su territorio embistiendo a sus rivales con su cuerpo de acero.', 'Pokémon Coraza Férrea');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (306, 'Aggron', 9, 6, 360.0, 2.1, 70, 110, 180, 60, 60, 50, 'Hacen de toda una montaña su territorio. Cuantas más cicatrices tienen, más curtidos en disputas. ¡mucha atención!', 'Pokémon Coraza Férrea');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (307, 'Meditite', 2, 14, 11.2, 0.6, 30, 40, 55, 40, 55, 60, 'Se entrena en la profundidad de las montañas. Cuando aumenta su poder espiritual con meditación, levita.', 'Pokémon Meditador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (308, 'Medicham', 2, 14, 31.5, 1.3, 60, 60, 75, 60, 75, 80, 'Evita los ataques moviéndose de forma elegante, como si bailara. Ataca dando un golpe devastador y moviéndose igual.', 'Pokémon Meditador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (309, 'Electrike', 13, NULL, 15.2, 0.6, 40, 45, 40, 65, 40, 65, 'Estimula sus músculos y aumenta sus reflejos con la electricidad que almacena su pelaje.', 'Pokémon Calambre');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (310, 'Manectric', 13, NULL, 40.2, 1.5, 70, 75, 60, 105, 60, 105, 'No suele aparecer ante la gente. Dicen que suele colocar su nido donde caen los rayos.', 'Pokémon Descarga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (311, 'Plusle', 13, NULL, 4.2, 0.4, 60, 50, 40, 85, 75, 95, 'Anima a sus compañeros con pompones de chispas. roba energía de los postes telefónicos.', 'Pokémon Ánimo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (312, 'Minun', 13, NULL, 4.2, 0.4, 60, 40, 50, 75, 85, 95, 'La electricidad de minun y plusle es buena para la circulación y para desentumecer músculos.', 'Pokémon Ánimo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (313, 'Volbeat', 7, NULL, 17.7, 0.7, 65, 73, 75, 47, 85, 85, 'Se comunica con otros iluminando su cola por la noche. Le encanta el suave aroma de illumise.', 'Pokémon Luciérnaga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (314, 'Illumise', 7, NULL, 17.7, 0.6, 65, 47, 75, 73, 85, 85, 'Con su dulce aroma guía a grupos de volbeat para que tracen hasta 200 dibujos distintos en el cielo nocturno.', 'Pokémon Luciérnaga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (315, 'Roselia', 12, 4, 2.0, 0.3, 50, 60, 45, 100, 80, 65, 'Ataca con sus manos, en las que tiene un tipo de veneno diferente en cada una. Cuanto más aroma, mejor salud tiene.', 'Pokémon Espina');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (316, 'Gulpin', 4, NULL, 10.3, 0.4, 70, 43, 53, 43, 53, 40, 'Sus sesos y corazón son diminutos; es casi todo estómago. Sus jugos gástricos disuelven lo que sea.', 'Pokémon Estómago');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (317, 'Swalot', 4, NULL, 80.0, 1.7, 100, 73, 83, 73, 83, 55, 'Se traga cualquier cosa de una pieza. Sus poros segregan fluidos tóxicos que dañan a sus enemigos.', 'Pokémon Bolsa Veneno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (318, 'Carvanha', 11, 17, 20.8, 0.8, 45, 90, 20, 65, 20, 65, 'Atacan en grupo a los barcos y los hunden a base de bocados. Habitan en los ríos de la jungla.', 'Pokémon Feroz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (319, 'Sharpedo', 11, 17, 88.8, 1.8, 70, 120, 40, 95, 40, 95, 'Con sus colmillos puede atravesar el acero. Nada a 120 km/h. Le llaman el terror de los mares.', 'Pokémon Voraz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (320, 'Wailmer', 11, NULL, 130.0, 2.0, 130, 70, 35, 70, 35, 60, 'Le gusta botar como una pelota de playa. Cuanta más agua de mar traga, más alto puede llegar.', 'Pokémon Ballenabola');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (321, 'Wailord', 11, NULL, 398.0, 14.5, 170, 90, 45, 90, 45, 60, 'Puede dejar fuera de combate a sus oponentes con el impacto de su enorme cuerpo al caer en el agua tras un salto.', 'Pokémon Ballenaflot');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (322, 'Numel', 10, 5, 24.0, 0.7, 60, 60, 40, 65, 45, 35, 'En su joroba almacena magma ardiente que se enfría cuando llueve y lo ralentiza.', 'Pokémon Torpe');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (323, 'Camerupt', 10, 5, 220.0, 1.9, 70, 100, 70, 105, 75, 40, 'Los volcanes de su joroba entran en erupción cada 10 años o cuando camerupt se enfada mucho.', 'Pokémon Erupción');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (324, 'Torkoal', 10, NULL, 80.4, 0.5, 70, 85, 140, 85, 70, 20, 'Trabaja sin descanso en minas en desuso localizando y extrayendo carbón.', 'Pokémon Carbón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (325, 'Spoink', 14, NULL, 30.6, 0.7, 60, 25, 35, 70, 80, 60, 'Bota continuamente usando su cola como muelle. salta para mantener el latido de su corazón.', 'Pokémon Saltarín');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (326, 'Grumpig', 14, NULL, 71.5, 0.9, 80, 45, 65, 90, 110, 80, 'Las perlas negras aumentan sus poderes psíquicos. controla las mentes de sus rivales con un extraño baile.', 'Pokémon Manipulador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (327, 'Spinda', 1, NULL, 5.0, 1.1, 60, 60, 60, 60, 60, 60, 'No hay dos spinda que tengan idénticos pelajes. su tambaleo al bailar confunde a los rivales.', 'Pokémon Panda Topos');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (328, 'Trapinch', 5, NULL, 15.0, 0.7, 45, 100, 45, 45, 45, 10, 'Habita desiertos áridos. Cava fosas profundas, en las que espera que caiga su presa.', 'Pokémon Hormiga León');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (329, 'Vibrava', 5, 16, 15.3, 1.1, 50, 70, 50, 50, 50, 70, 'Al agitar a gran velocidad ambas alas, emite ondas ultrasónicas que provocan fuertes dolores de cabeza.', 'Pokémon Vibrante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (330, 'Flygon', 5, 16, 82.0, 2.0, 80, 100, 80, 80, 80, 100, 'Su medio es el desierto. Para esconderse, levanta arena con las patas. Tiene los ojos protegidos con mallas rojas.', 'Pokémon Místico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (331, 'Cacnea', 12, NULL, 51.3, 0.4, 50, 85, 40, 85, 40, 35, 'Vive en regiones secas y con pocas lluvias. Una vez al año, le brota una flor amarilla.', 'Pokémon Cactus');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (332, 'Cacturne', 12, 17, 77.4, 1.3, 70, 115, 60, 115, 60, 55, 'Es un pokémon nocturno. Busca presas agotadas por el calor diurno del desierto.', 'Pokémon Espantajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (333, 'Swablu', 1, 3, 1.2, 0.4, 45, 40, 60, 40, 75, 50, 'Se desconoce el porqué, pero le encanta posarse sobre la cabeza de los humanos y fingir que es un gorro.', 'Pokémon Ave Algodón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (334, 'Altaria', 16, 3, 20.6, 1.1, 75, 70, 90, 70, 105, 80, 'Vuela pausadamente por el cielo azul. Su hermoso trino cautiva a todo aquel que lo escucha.', 'Pokémon Cantor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (335, 'Zangoose', 1, NULL, 40.3, 1.3, 73, 115, 60, 60, 60, 90, 'Hace muchos años que se enfrenta a seviper. Su arma más eficaz son sus zarpas afiladas.', 'Pokémon Gato Hurón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (336, 'Seviper', 4, NULL, 52.5, 2.7, 73, 100, 60, 100, 60, 65, 'Hace frente a zangoose con su agilidad y su afilada cola, que está cargada con un veneno letal.', 'Pokémon Colmillos');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (337, 'Lunatone', 6, 14, 168.0, 1.0, 90, 55, 65, 95, 85, 70, 'Se piensa que está muy influido por las fases lunares, ya que solo actúa en noches de luna llena.', 'Pokémon Meteorito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (338, 'Solrock', 6, 14, 154.0, 1.2, 90, 95, 85, 55, 65, 70, 'Usa el sol como fuente de energía, por lo que es más poderoso de día. Brilla al rotar.', 'Pokémon Meteorito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (339, 'Barboach', 11, 5, 1.9, 0.4, 50, 48, 43, 46, 41, 60, 'Recubre su cuerpo de una película viscosa que le permite escapar si alguien lo agarra.', 'Pokémon Bigotudo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (340, 'Whiscash', 11, 5, 23.6, 0.9, 110, 78, 73, 76, 71, 60, 'Habita en grandes pantanos. Si se acerca un enemigo, se alborota y causa temblores monumentales.', 'Pokémon Bigotudo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (341, 'Corphish', 11, NULL, 11.5, 0.6, 43, 80, 65, 50, 35, 35, 'Por sucio o contaminado que esté el río, se adaptan rápido y se multiplican. Poseen una gran fuerza vital.', 'Pokémon Rufián');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (342, 'Crawdaunt', 11, 17, 32.8, 1.1, 63, 120, 85, 90, 55, 55, 'Este malandrín usa sus pinzas para atosigar y expulsar a los otros pokémon que viven en su estanque.', 'Pokémon Granuja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (343, 'Baltoy', 5, 14, 21.5, 0.5, 40, 40, 55, 40, 70, 55, 'Se mueve girando sobre su eje. También se le puede ver girando cabeza abajo.', 'Pokémon Muñeca Barro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (344, 'Claydol', 5, 14, 108.0, 1.5, 60, 70, 105, 70, 120, 75, 'Este pokémon es una vieja figurilla de barro que cobró vida mediante la exposición a extraños rayos de luz.', 'Pokémon Muñeca Barro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (345, 'Lileep', 6, 12, 23.8, 1.0, 66, 41, 77, 61, 87, 23, 'Se extinguió hace 100 millones de años. Sus tentáculos parecen pétalos para atraer a sus presas.', 'Pokémon Lirio de Mar');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (346, 'Cradily', 6, 12, 60.4, 1.5, 86, 81, 97, 81, 107, 43, 'Vive en el fondo de los mares de aguas templadas. emerge para cazar cuando baja la marea.', 'Pokémon Percebe');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (347, 'Anorith', 6, 7, 12.5, 0.7, 45, 95, 50, 40, 50, 75, 'Es una especie de pokémon predecesor. Estira las pinzas para cazar a sus presas, que se esconden en las rocas del mar.', 'Pokémon Camarón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (348, 'Armaldo', 6, 7, 68.2, 1.5, 75, 125, 100, 70, 80, 45, 'Ensarta a sus presas con sus gigantescas garras elásticas. Una fuerte coraza cubre su cuerpo.', 'Pokémon Armadura');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (349, 'Feebas', 11, NULL, 7.4, 0.6, 20, 15, 20, 10, 55, 80, 'Come de todo, por lo que puede vivir en aguas contaminadas. Nadie le presta atención.', 'Pokémon Pez');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (350, 'Milotic', 11, NULL, 162.0, 6.2, 95, 60, 79, 100, 125, 81, 'Milotic es impresionantemente bello. Quienes lo ven, temen perder las ganas de luchar contra él.', 'Pokémon Tierno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (351, 'Castform', 1, NULL, 0.8, 0.3, 70, 70, 70, 70, 70, 70, 'Sus células son muy sensibles a los cambios de temperatura y humedad y se adapta sin problema.', 'Pokémon Clima');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (352, 'Kecleon', 1, NULL, 22.0, 1.0, 60, 90, 70, 60, 120, 40, 'Puede mudar el color de su cuerpo a voluntad, pero el dibujo en zigzag de su panza nunca desaparece.', 'Pokémon Camaleónico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (353, 'Shuppet', 8, NULL, 2.3, 0.6, 44, 75, 35, 63, 33, 45, 'Dicen que con su cuerno se alimenta de sentimientos de celos y envidia. Es muy activo a medianoche.', 'Pokémon Títere');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (354, 'Banette', 8, NULL, 12.5, 1.1, 64, 115, 65, 83, 63, 65, 'Este muñeco se convirtió en un pokémon cuando lo tiraron a la basura. Busca a su antiguo dueño.', 'Pokémon Marioneta');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (355, 'Duskull', 8, NULL, 15.0, 0.8, 20, 40, 90, 30, 90, 25, 'Persigue a su presa sin descanso allá donde vaya. no obstante, abandona la caza al amanecer.', 'Pokémon Réquiem');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (356, 'Dusclops', 8, NULL, 30.6, 1.6, 40, 70, 130, 60, 130, 25, 'Si alguien mira los fuegos fatuos del interior de dusclops, perderá su alma irremediablemente.', 'Pokémon Atrayente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (357, 'Tropius', 12, 3, 100.0, 2.0, 99, 68, 83, 72, 87, 51, 'Puede volar al agitar sus grandes hojas. Reparte entre los niños la codiciada fruta dulce que crece de su cuello.', 'Pokémon Fruto');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (358, 'Chimecho', 14, NULL, 1.0, 0.6, 75, 50, 80, 95, 90, 65, 'Se cuelga de ramas y aleros con la ventosa de su cabeza. Tiene siete tipos de gritos diferentes.', 'Pokémon Sonido Aire');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (359, 'Absol', 17, NULL, 47.0, 1.2, 65, 130, 60, 75, 60, 75, 'Presiente desastres inminentes y se aparece a la gente para advertir del peligro.', 'Pokémon Catástrofe');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (360, 'Wynaut', 14, NULL, 14.0, 0.6, 95, 23, 48, 23, 48, 23, 'Suelen ir en grupo y a la hora de dormir se resguardan en una cueva.', 'Pokémon Radiante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (361, 'Snorunt', 15, NULL, 16.8, 0.7, 50, 50, 50, 50, 50, 50, 'Se dice que se reúnen en grupos bajo hojas gigantescas y viven allí en total armonía.', 'Pokémon Gorro Nieve');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (362, 'Glalie', 15, NULL, 256.5, 1.5, 80, 80, 80, 80, 80, 80, 'Se protege con una armadura de hielo creada por congelación de la humedad del aire.', 'Pokémon Cara');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (363, 'Spheal', 15, 11, 39.5, 0.8, 70, 40, 50, 55, 50, 25, 'Como no saben nadar muy bien, se mueven rápidamente rodando. Cuando están alegres, dan palmadas con las aletas.', 'Pokémon Aplauso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (364, 'Sealeo', 15, 11, 87.6, 1.1, 90, 60, 70, 75, 70, 45, 'Su nariz es muy sensible. Cuando ve algo por primera vez, lo toca con ella para recordarlo después.', 'Pokémon Rodabola');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (365, 'Walrein', 15, 11, 150.6, 1.4, 110, 80, 90, 95, 90, 65, 'Rompe el hielo con sus colmillos. Su gruesa capa de grasa le protege del frío y de los enemigos.', 'Pokémon Rompehielo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (366, 'Clamperl', 11, NULL, 52.5, 0.4, 35, 64, 85, 74, 55, 32, 'Produce durante su vida una sola perla que aumenta los poderes psíquicos al evolucionar.', 'Pokémon Bivalvo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (367, 'Huntail', 11, NULL, 27.0, 1.7, 55, 104, 105, 94, 75, 52, 'Vive en los abismos marinos, donde no se filtra la luz. tiene una cola en forma de pez, que enciende para atraer a su presa.', 'Pokémon Abisal');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (368, 'Gorebyss', 11, NULL, 22.6, 1.8, 55, 84, 105, 114, 75, 52, 'Vive en el fondo del mar. En primavera, su cuerpo rosado adquiere colores más vivos.', 'Pokémon Mar del Sur');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (369, 'Relicanth', 11, 6, 23.4, 1.0, 100, 90, 130, 45, 65, 55, 'No ha sufrido ningún cambio desde hace 100 millones de años. Fue descubierto durante una expedición marina.', 'Pokémon Longevo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (370, 'Luvdisc', 11, NULL, 8.7, 0.6, 43, 30, 55, 40, 65, 97, 'Vive en mares cálidos. Se dice que, si una pareja lo encuentra, será bendecida con amor eterno.', 'Pokémon Cita');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (371, 'Bagon', 16, NULL, 42.1, 0.6, 45, 75, 60, 40, 30, 50, 'Su desarrollado cuello y su cabeza dura como el acero le permiten reducir a añicos rocas enormes.', 'Pokémon Cabeza Roca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (372, 'Shelgon', 16, NULL, 110.5, 1.1, 65, 95, 100, 60, 50, 50, 'Las células han empezado a cambiar dentro de su dura concha, que se separa cuando evoluciona.', 'Pokémon Resistencia');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (373, 'Salamence', 16, 3, 102.6, 1.5, 95, 135, 80, 110, 80, 100, 'Si se enfada, no hay forma de calmarlo; lo destruye todo lanzando arañazos y llamas.', 'Pokémon Dragón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (374, 'Beldum', 9, 14, 95.2, 0.6, 40, 55, 80, 35, 60, 30, 'Los pulsos magnéticos de su cuerpo repelen la gravedad, lo que le permite flotar en el aire.', 'Pokémon Bola Hierro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (375, 'Metang', 9, 14, 202.5, 1.2, 60, 75, 100, 55, 80, 50, 'Lo forman dos beldum unidos. Puede chocarse con un avión sin que su cuerpo de acero sufra ni un rasguño.', 'Pokémon Garra Hierro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (376, 'Metagross', 9, 14, 550.0, 1.6, 80, 135, 130, 95, 90, 70, 'Gracias a sus cuatro cerebros, es más inteligente que un superordenador y analiza con precisión al enemigo.', 'Pokémon Pata Hierro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (377, 'Regirock', 6, NULL, 230.0, 1.7, 80, 100, 200, 50, 100, 50, 'Su cuerpo está hecho de roca. Si se astilla durante el combate, emplea piedras para repararlo.', 'Pokémon Pico Roca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (378, 'Regice', 15, NULL, 175.0, 1.8, 80, 50, 100, 100, 200, 50, 'Dicen que ha yacido durmiendo en un glaciar durante milenios. Ni el magma puede derretir su cuerpo.', 'Pokémon Iceberg');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (379, 'Registeel', 9, NULL, 205.0, 1.9, 80, 75, 150, 75, 150, 50, 'Su cuerpo ha estado sometido a la presión subterránea durante miles de años y no puede arañarse.', 'Pokémon Hierro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (380, 'Latias', 16, 14, 40.0, 1.4, 80, 80, 90, 110, 130, 110, 'Se comunica por telepatía. Su plumaje refleja la luz, lo que le permite hacerse invisible.', 'Pokémon Eón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (381, 'Latios', 16, 14, 60.0, 2.0, 80, 90, 80, 130, 110, 110, 'Es un pokémon muy inteligente. Puede adelantar a aviones a reacción plegando las alas.', 'Pokémon Eón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (382, 'Kyogre', 11, NULL, 352.0, 4.5, 100, 100, 90, 150, 140, 90, 'Pokémon legendario que aumentó los mares con diluvios y maremotos. Fue un enemigo acérrimo de groudon.', 'Pokémon Cuenca Mar');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (383, 'Groudon', 5, NULL, 950.0, 3.5, 100, 150, 140, 100, 90, 90, 'Dicen que este pokémon legendario simboliza la tierra. tras batirse en duelo con kyogre se echó a dormir.', 'Pokémon Continente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (384, 'Rayquaza', 16, 3, 206.5, 7.0, 105, 150, 90, 150, 90, 95, 'Vive en la capa de ozono sobre las nubes y no puede ser visto desde el suelo.', 'Pokémon Cielo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (385, 'Jirachi', 9, 14, 1.1, 0.3, 100, 100, 100, 100, 100, 100, 'Desde antaño se dice que concederá los deseos escritos en las notas de su cabeza cuando se despierte.', 'Pokémon Deseo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (386, 'Deoxys', 14, NULL, 60.8, 1.7, 50, 150, 50, 150, 50, 150, 'Surgió a raíz de la mutación de adn de un virus alienígena que cayó a la tierra en un meteorito.', 'Pokémon ADN');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (387, 'Turtwig', 12, NULL, 10.2, 0.4, 55, 68, 64, 45, 55, 31, 'Realiza la fotosíntesis al bañarle los rayos de sol. su concha está formada por tierra endurecida.', 'Pokémon Hojita');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (388, 'Grotle', 12, NULL, 97.0, 1.1, 75, 89, 85, 55, 65, 36, 'Vive en los bosques cerca del agua. Por el día, sale a que les dé el sol a las plantas de su espalda.', 'Pokémon Arboleda');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (389, 'Torterra', 12, 5, 310.0, 2.2, 95, 109, 105, 75, 85, 56, 'Las gentes de antaño creían que el planeta se sustentaba en la espalda de un gran torterra.', 'Pokémon Continente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (390, 'Chimchar', 10, NULL, 6.2, 0.5, 44, 58, 44, 58, 44, 61, 'El gas de su panza alimenta el fuego de su parte trasera, que ni la lluvia puede extinguir.', 'Pokémon Chimpancé');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (391, 'Monferno', 10, 2, 22.0, 0.9, 64, 78, 52, 78, 52, 81, 'Controla hábilmente la intensidad del fuego de su cola para mantener al enemigo a una distancia ideal.', 'Pokémon Juguetón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (392, 'Infernape', 10, 2, 55.0, 1.2, 76, 104, 71, 104, 71, 108, 'Su corona de fuego demuestra su naturaleza ígnea. nadie le gana en velocidad.', 'Pokémon Llama');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (393, 'Piplup', 11, NULL, 5.2, 0.4, 53, 51, 53, 61, 56, 40, 'No le gusta que lo cuiden. Como no aprecia el apoyo de su entrenador, le cuesta coger confianza con él.', 'Pokémon Pingüino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (394, 'Prinplup', 11, NULL, 23.0, 0.8, 64, 66, 68, 81, 76, 50, 'Vive solo, alejado de los demás. Cada uno piensa que es el más importante.', 'Pokémon Pingüino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (395, 'Empoleon', 11, 9, 84.5, 1.7, 84, 86, 88, 111, 101, 60, 'Nada tan rápido como un fueraborda. Con los afilados bordes de sus alas puede cortar el hielo.', 'Pokémon Emperador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (396, 'Starly', 1, 3, 2.0, 0.3, 40, 55, 30, 30, 30, 60, 'Viaja en grandes bandadas. Aunque es pequeño, agita las alas con gran fuerza.', 'Pokémon Estornino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (397, 'Staravia', 1, 3, 15.5, 0.6, 55, 75, 50, 40, 40, 80, 'Vive siempre en grandes grupos y las disputas dentro de ellos son muy intensas.', 'Pokémon Estornino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (398, 'Staraptor', 1, 3, 24.9, 1.2, 85, 120, 70, 50, 60, 100, 'Sus alas y sus patas son muy fuertes. Puede llevarse volando pokémon pequeños.', 'Pokémon Depredador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (399, 'Bidoof', 1, NULL, 20.0, 0.5, 59, 45, 40, 35, 40, 31, 'Tiene nervios de acero y nada puede perturbarlo. es más ágil y activo de lo que aparenta.', 'Pokémon Gran Ratón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (400, 'Bibarel', 1, 11, 31.5, 1.0, 79, 85, 60, 55, 60, 71, 'Con sus incisivos roe ramas y raíces y las apila para construir sus madrigueras junto al río.', 'Pokémon Castor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (401, 'Kricketot', 7, NULL, 2.2, 0.3, 37, 25, 41, 25, 41, 25, 'Hablan entre sí chocando las antenas. Los sonidos producidos son muy característicos.', 'Pokémon Grillo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (402, 'Kricketune', 7, NULL, 25.5, 1.0, 77, 85, 51, 55, 51, 65, 'Cuando llora, cruza sus brazos con forma de cuchillo. puede improvisar melodías.', 'Pokémon Grillo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (403, 'Shinx', 13, NULL, 9.5, 0.5, 45, 65, 34, 40, 34, 45, 'La extensión y contracción de sus músculos genera electricidad. Brilla cuando está en peligro.', 'Pokémon Flash');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (404, 'Luxio', 13, NULL, 30.5, 0.9, 60, 85, 49, 60, 49, 60, 'Sus garras emiten electricidad que puede debilitar a sus rivales. Viven en grupos pequeños.', 'Pokémon Chispa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (405, 'Luxray', 13, NULL, 42.0, 1.4, 80, 120, 79, 95, 79, 70, 'Cuando sus ojos brillan como el oro, puede ver presas escondidas, incluso detrás de un muro.', 'Pokémon Chispa Ojos');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (406, 'Budew', 12, 4, 1.2, 0.2, 40, 30, 35, 50, 70, 55, 'Vive cerca de aguas cristalinas. Su cabello se abre y esparce polen cuando le bañan los rayos del sol.', 'Pokémon Brote');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (407, 'Roserade', 12, 4, 14.5, 0.9, 60, 70, 65, 125, 105, 90, 'Atrae a sus presas con una dulce fragancia y usa sus cepas venenosas para atenazarlas y acabar con ellas.', 'Pokémon Ramillete');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (408, 'Cranidos', 6, NULL, 31.5, 0.9, 67, 125, 40, 30, 30, 58, 'Fue recreado a partir de un fósil con forma esférica. derriba a las presas a cabezazos.', 'Pokémon Cabezazo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (409, 'Rampardos', 6, NULL, 102.5, 1.6, 97, 165, 60, 65, 50, 58, 'Tiene sesos pequeños porque su duro cráneo, que soporta cualquier golpe, les impide desarrollarse.', 'Pokémon Cabezazo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (410, 'Shieldon', 6, 9, 57.0, 0.5, 30, 42, 118, 42, 88, 30, 'Se abrillanta la cara frotándola contra los árboles. es vulnerable a los ataques por la espalda.', 'Pokémon Escudo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (411, 'Bastiodon', 6, 9, 149.5, 1.3, 60, 52, 168, 47, 138, 30, 'Se alineaban y creaban una barrera que ningún enemigo podía cruzar. Así protegían a sus crías.', 'Pokémon Escudo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (412, 'Burmy', 7, NULL, 3.4, 0.2, 40, 29, 45, 29, 45, 36, 'Para protegerse de los vientos fríos invernales, se cubre con un caparazón de ramas y hojas.', 'Pokémon Larva');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (413, 'Wormadam', 7, 12, 6.5, 0.5, 60, 59, 85, 79, 105, 36, 'Su apariencia varía según dónde evolucione. Los materiales que haya pasan a formar parte de su cuerpo.', 'Pokémon Larva');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (414, 'Mothim', 7, 3, 23.3, 0.9, 70, 94, 50, 94, 50, 66, 'Le encanta la miel de las flores y roba la que recoge combee.', 'Pokémon Polilla');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (415, 'Combee', 7, 3, 5.5, 0.3, 30, 30, 42, 30, 42, 70, 'El trío se forma al nacer y se pasa la vida recogiendo miel de las flores para satisfacer a vespiquen.', 'Pokémon Abejita');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (416, 'Vespiquen', 7, 3, 38.5, 1.2, 70, 80, 102, 80, 102, 40, 'Cría larvas en los panales de su cuerpo. Segrega diversas feromonas de su cuerpo con las cuales controla a sus crías.', 'Pokémon Colmena');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (417, 'Pachirisu', 13, NULL, 3.9, 0.4, 60, 45, 70, 45, 90, 95, 'Crea bolas de pelo que crepitan con electricidad estática. Las almacena en agujeros de árboles.', 'Pokémon Ardillalec');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (418, 'Buizel', 11, NULL, 29.5, 0.7, 55, 65, 35, 60, 30, 85, 'Nada rotando sus dos colas a modo de tornillo. cuando bucea, el flotador se pliega.', 'Pokémon Nutria Mar.');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (419, 'Floatzel', 11, NULL, 33.5, 1.1, 85, 105, 55, 85, 50, 115, 'Nada gracias a su flotador. Ayuda en los rescates de gente que se está ahogando.', 'Pokémon Nutria Mar.');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (420, 'Cherubi', 12, NULL, 3.3, 0.4, 45, 35, 45, 62, 53, 35, 'Bajo la luz solar es rojo. La esfera pequeña pierde sus nutrientes y se seca para anunciar la evolución.', 'Pokémon Cereza');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (421, 'Cherrim', 12, NULL, 9.3, 0.5, 70, 60, 70, 87, 78, 85, 'Cuando los rayos de sol inundan su capullo, abre de par en par sus pétalos y se vuelve muy dinámico.', 'Pokémon Floración');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (422, 'Shellos', 11, NULL, 6.3, 0.3, 76, 48, 48, 57, 62, 34, 'No hay que apretarle muy fuerte, ya que, si lo haces, secreta un misterioso fluido morado.', 'Pokémon Babosa Marina');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (423, 'Gastrodon', 11, 5, 29.9, 0.9, 111, 83, 68, 92, 82, 39, 'Si se ve amenazado por sus enemigos naturales, segrega un líquido morado y huye rápidamente.', 'Pokémon Babosa Marina');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (424, 'Ambipom', 1, NULL, 20.3, 1.2, 75, 100, 66, 60, 66, 115, 'Habitan en grandes colonias y forman anillos uniendo sus colas, aparentemente en signo de amistad.', 'Pokémon Cola Larga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (425, 'Drifloon', 8, 3, 1.2, 0.4, 90, 50, 34, 60, 44, 70, 'Está formado por los espíritus de personas y pokémon. le encantan las estaciones húmedas.', 'Pokémon Globo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (426, 'Drifblim', 8, 3, 15.0, 1.2, 150, 80, 44, 90, 54, 80, 'Transporta a gente y a pokémon volando, pero, como se deja llevar, puede acabar en cualquier sitio.', 'Pokémon Dirigible');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (427, 'Buneary', 1, NULL, 5.5, 0.4, 55, 66, 44, 44, 56, 85, 'Lleva las orejas enrolladas. Cuando las extiende, golpea con tal fuerza que incluso puede romper rocas.', 'Pokémon Conejo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (428, 'Lopunny', 1, NULL, 33.3, 1.2, 65, 76, 84, 54, 96, 105, 'Este pokémon resulta muy precavido. Si siente peligro, escapa corriendo a grandes saltos.', 'Pokémon Conejo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (429, 'Mismagius', 8, NULL, 4.4, 0.9, 60, 60, 60, 105, 105, 105, 'Recita cánticos. Algunos de ellos atormentan a sus rivales, mientras que otros producen felicidad.', 'Pokémon Mágico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (430, 'Honchkrow', 17, 3, 27.3, 0.9, 100, 125, 52, 105, 52, 71, 'Si uno lanza un grito profundo, los demás acuden. por eso se le llama también invocador de la noche.', 'Pokémon Gran Jefe');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (431, 'Glameow', 1, NULL, 3.9, 0.5, 49, 55, 42, 42, 37, 85, 'Araña cuando está enfadado y ronronea cuando está cariñoso. Su imprevisibilidad es famosa.', 'Pokémon Gastuto');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (432, 'Purugly', 1, NULL, 43.8, 1.0, 71, 82, 64, 64, 59, 112, 'Si se siente a gusto, no tiene ningún reparo en apropiarse de las moradas de otros pokémon.', 'Pokémon Tigre Gato');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (433, 'Chingling', 14, NULL, 0.6, 0.2, 45, 30, 50, 65, 50, 45, 'Emite un tintineo cada vez que salta. Ensordece a sus rivales con sonidos de alta frecuencia.', 'Pokémon Campana');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (434, 'Stunky', 4, 17, 19.2, 0.4, 63, 63, 47, 41, 41, 74, 'Expulsa un fluido maloliente por sus cuartos traseros. el hedor repele pokémon en un radio de 2 km.', 'Pokémon Mofeta');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (435, 'Skuntank', 4, 17, 38.0, 1.0, 103, 93, 67, 71, 61, 84, 'Para atacar, emite un fluido maloliente por la punta de su cola que puede llegar a más de 50 m.', 'Pokémon Mofeta');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (436, 'Bronzor', 9, 14, 60.5, 0.5, 57, 24, 86, 24, 86, 23, 'Se dice que brilla y refleja la verdad al pulirlo, aunque es algo que detesta.', 'Pokémon Bronce');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (437, 'Bronzong', 9, 14, 187.0, 1.3, 67, 89, 116, 79, 116, 33, 'Desde antaño se adora a este pokémon para que propicie lluvias abundantes. A veces se esconde bajo tierra.', 'Pokémon Campana Bronce');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (438, 'Bonsly', 6, NULL, 15.0, 0.5, 50, 80, 95, 10, 45, 10, 'Prefiere entornos áridos. Sus ojos expulsan agua para ajustar los niveles de fluido corporal.', 'Pokémon Bonsái');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (439, 'Mime Jr.', 14, 18, 13.0, 0.6, 20, 25, 45, 70, 90, 60, 'Desconcierta a sus rivales imitando sus movimientos a la perfección y aprovecha para darse a la fuga.', 'Pokémon Mimo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (440, 'Happiny', 1, NULL, 24.4, 0.6, 100, 5, 5, 15, 65, 30, 'Les da a sus amigos una roca con forma de huevo que transporta en la bolsa de su panza.', 'Pokémon Casita');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (441, 'Chatot', 1, 3, 1.9, 0.5, 76, 65, 45, 92, 42, 91, 'Imita los sonidos de sus rivales, haciéndoles creer que son amigos e impidiendo que le ataquen.', 'Pokémon Corchea');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (442, 'Spiritomb', 8, 17, 108.0, 1.0, 50, 92, 108, 92, 108, 35, 'Se formó a partir de 108 espíritus. Está unido a la fisura de una piedra angular mística.', 'Pokémon Prohibido');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (443, 'Gible', 16, 5, 20.5, 0.7, 58, 70, 45, 40, 45, 42, 'Vive en agujeros en las cuevas, al amparo del calor geotérmico. Si se acerca un enemigo, le muerde.', 'Pokémon Terrascualo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (444, 'Gabite', 16, 5, 56.0, 1.4, 68, 90, 65, 50, 55, 82, 'Le gustan las cosas brillantes. Busca tesoros en cuevas y se lleva el botín a su nido.', 'Pokémon Cueva');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (445, 'Garchomp', 16, 5, 95.0, 1.9, 108, 130, 95, 80, 85, 102, 'Cuando despliega su cuerpo y sus alas, parece un avión a reacción. Vuela a velocidad sónica.', 'Pokémon Mach');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (446, 'Munchlax', 1, NULL, 105.0, 0.6, 135, 85, 40, 40, 85, 5, 'Esconde comida bajo el largo pelo de su cuerpo, pero más tarde lo olvida.', 'Pokémon Comilón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (447, 'Riolu', 2, NULL, 20.2, 0.7, 40, 70, 40, 35, 40, 60, 'Se comunica con los suyos mediante la emisión de unas ondas que cambian de forma en función de los sentimientos.', 'Pokémon Emanación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (448, 'Lucario', 2, 9, 54.0, 1.2, 70, 110, 70, 115, 70, 90, 'Puede leer los pensamientos y movimientos de su adversario a través de su aura.', 'Pokémon Aura');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (449, 'Hippopotas', 5, NULL, 49.5, 0.8, 68, 72, 78, 38, 42, 32, 'Vive en zonas áridas. En lugar de sudar, su cuerpo expulsa granos de arena.', 'Pokémon Hipo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (450, 'Hippowdon', 5, NULL, 300.0, 2.0, 108, 112, 118, 68, 72, 47, 'Hace gala de su fuerza abriendo su enorme boca. para atacar levanta gran cantidad de arena.', 'Pokémon Peso Pesado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (451, 'Skorupi', 4, 7, 12.0, 0.8, 40, 50, 90, 30, 55, 65, 'Agarra a sus presas con las pinzas de la cola y les inyecta veneno. Espera hasta que hace efecto.', 'Pokémon Escorpión');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (452, 'Drapion', 4, 17, 61.5, 1.3, 70, 90, 110, 60, 75, 95, 'Aunque sabe que puede aplastar al rival con su gran fuerza, le gusta dar el golpe de gracia con su virulento veneno.', 'Pokémon Escorpiogro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (453, 'Croagunk', 4, 2, 23.0, 0.7, 48, 61, 40, 61, 40, 50, 'Tiene bolsas venenosas en las mejillas. Pilla desprevenidos a sus rivales para pincharles.', 'Pokémon Boca Tóxica');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (454, 'Toxicroak', 4, 2, 44.4, 1.3, 83, 106, 65, 86, 65, 85, 'Almacena veneno en una bolsa de la garganta. Cuando croa, este se agita para lograr mayor potencia.', 'Pokémon Boca Tóxica');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (455, 'Carnivine', 12, NULL, 27.0, 1.4, 74, 100, 72, 90, 72, 46, 'Atrae a sus presas con saliva de olor dulce y las mastica. Tarda todo un día en comérselas.', 'Pokémon Caza Bichos');
