INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (457, 'Lumineon', 11, NULL, 24.0, 1.2, 69, 69, 76, 69, 86, 91, 'Vive en las profundidades marinas. Atrae a sus presas con parpadeos del dibujo de sus cuatro aletas.', 'Pokémon Neón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (458, 'Mantyke', 11, 3, 65.0, 1.0, 45, 20, 50, 60, 120, 50, 'Los símbolos de su espalda cambian según la región. a veces se mezcla con bancos de remoraid y nada junto a ellos.', 'Pokémon Milano');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (459, 'Snover', 12, 15, 50.5, 1.0, 60, 62, 50, 62, 60, 40, 'En las estaciones frías baja al pie de la montaña, pero regresa a las cimas nevadas en primavera.', 'Pokémon Árbol Nieve');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (460, 'Abomasnow', 12, 15, 135.5, 2.2, 90, 92, 75, 92, 85, 60, 'Genera ventiscas con las que cubre de nieve grandes áreas. Se le llama el monstruo de hielo.', 'Pokémon Árbol Nieve');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (461, 'Weavile', 17, 15, 34.0, 1.1, 70, 120, 65, 45, 85, 125, 'Vive en zonas nevadas. Deja marcas en los árboles con sus garras para comunicarse con sus compañeros.', 'Pokémon Garra Filo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (462, 'Magnezone', 13, 9, 180.0, 1.2, 70, 70, 115, 130, 90, 60, 'Emiten campos magnéticos tan potentes que se atraen entre sí e incluso pueden llegar a inmovilizarse.', 'Pokémon Magnético');
