INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (464, 'Rhyperior', 5, 6, 282.8, 2.4, 115, 140, 130, 55, 55, 40, 'Lanza geodude por los agujeros de las manos. gracias a protector resiste erupciones volcánicas.', 'Pokémon Taladro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (465, 'Tangrowth', 12, NULL, 128.6, 2.0, 100, 100, 125, 110, 50, 50, 'Atrapa a su presa desplegando las ramas de sus brazos, que no le importa perder ante depredadores.', 'Pokémon Enredadera');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (466, 'Electivire', 13, NULL, 138.6, 1.8, 75, 123, 67, 95, 85, 95, 'Al almacenar mucha electricidad, desprende chispas de una claridad increíble entre sus dos cuernos.', 'Pokémon Rayo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (467, 'Magmortar', 10, NULL, 68.0, 1.6, 75, 95, 67, 125, 95, 83, 'Sus brazos disparan bolas de fuego de más de 2000 °c. Vive en cráteres volcánicos.', 'Pokémon Explosión');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (468, 'Togekiss', 18, 3, 38.0, 1.5, 85, 50, 95, 120, 115, 80, 'Se dice que aparece en zonas pacíficas y las bendice con sus dones.', 'Pokémon Festejo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (469, 'Yanmega', 7, 3, 51.5, 1.9, 86, 76, 86, 116, 56, 95, 'Su técnica favorita consiste en volar a gran velocidad y despedazar con su mandíbula a su oponente en un instante.', 'Pokémon Libélula');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (470, 'Leafeon', 12, NULL, 25.5, 1.0, 65, 110, 130, 60, 65, 95, 'En días soleados duerme y hace la fotosíntesis, emitiendo aire puro a su alrededor.', 'Pokémon Verdor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (471, 'Glaceon', 15, NULL, 25.9, 0.8, 65, 60, 110, 130, 95, 65, 'Controla su temperatura corporal para congelar el ambiente y causar ráfagas de nieve en polvo.', 'Pokémon Nieve Fresca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (472, 'Gliscor', 5, 3, 42.5, 2.0, 75, 95, 125, 45, 75, 95, 'Espera a sus presas colgado de una rama bocabajo. cuando llega su oportunidad, se lanza en picado.', 'Pokémon Colmicorpio');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (473, 'Mamoswine', 15, 5, 291.0, 2.5, 110, 130, 80, 70, 60, 80, 'Ya existía en la antigüedad. Se encontró uno bajo hielo de hace más de 10 000 años.', 'Pokémon Doscolmillos');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (474, 'Porygon-Z', 1, NULL, 34.0, 0.9, 85, 80, 70, 135, 75, 90, 'Se le instaló un software para mejorarlo. Sin embargo, comenzó a comportarse de manera extraña.', 'Pokémon Virtual');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (475, 'Gallade', 14, 2, 52.0, 1.6, 68, 125, 65, 65, 115, 80, 'Puede presentir los movimientos de sus rivales y por eso ataca siempre antes que ellos.', 'Pokémon Cuchilla');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (476, 'Probopass', 6, 9, 340.0, 1.4, 60, 55, 145, 75, 150, 40, 'Emana un fuerte magnetismo. Controla tres pequeñas unidades llamadas mininarices.', 'Pokémon Brújula');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (477, 'Dusknoir', 8, NULL, 106.6, 2.2, 45, 100, 135, 65, 135, 45, 'Se dice que recoge en su cuerpo flexible a los espíritus extraviados y los guía hasta casa.', 'Pokémon Grilletes');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (478, 'Froslass', 15, 8, 26.6, 1.3, 70, 80, 70, 80, 70, 110, 'Congela a sus enemigos con un aliento gélido de -50 °c. Su cuerpo está hueco.', 'Pokémon Tierra Fría');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (479, 'Rotom', 13, 8, 0.3, 0.3, 50, 50, 77, 95, 77, 91, 'Desde hace mucho tiempo se estudia su singular motor como fuente de energía motriz.', 'Pokémon Plasma');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (480, 'Uxie', 14, NULL, 0.3, 0.3, 75, 75, 130, 75, 130, 95, 'Se le conoce como el ser de la sabiduría. Se dice que puede borrar la memoria con una mirada.', 'Pokémon Sabiduría');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (481, 'Mesprit', 14, NULL, 0.3, 0.3, 80, 105, 105, 105, 105, 80, 'Se le conoce como el ser de la emoción. Enseñó a los humanos la nobleza del dolor y la alegría.', 'Pokémon Sensorio');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (482, 'Azelf', 14, NULL, 0.3, 0.3, 75, 125, 70, 125, 70, 115, 'Se le conoce como el ser de la voluntad. Duerme en el fondo de un lago para equilibrar el mundo.', 'Pokémon Voluntad');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (483, 'Dialga', 9, 16, 683.0, 5.4, 100, 120, 120, 150, 100, 90, 'Un pokémon de leyenda. Se dice que el tiempo comenzó a avanzar cuando dialga nació.', 'Pokémon Temporal');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (484, 'Palkia', 11, 16, 336.0, 4.2, 90, 120, 100, 150, 120, 100, 'Se dice que vive en una dimensión espacial paralela. aparece en la mitología.', 'Pokémon Espacial');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (485, 'Heatran', 10, 9, 430.0, 1.7, 91, 90, 106, 130, 106, 77, 'Habita cuevas volcánicas. Cava con sus pies en forma de cruz para trepar por techos y paredes.', 'Pokémon Domo Lava');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (486, 'Regigigas', 1, NULL, 420.0, 3.7, 110, 160, 110, 80, 110, 100, 'Se dice que usó rocas, magma y un hielo especial de montaña para crear pokémon con su aspecto.', 'Pokémon Colosal');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (487, 'Giratina', 8, 16, 750.0, 4.5, 150, 100, 120, 100, 120, 90, 'Vive en el mundo distorsión, un mundo opuesto al nuestro y cuyas leyes desafían el sentido común.', 'Pokémon Renegado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (488, 'Cresselia', 14, NULL, 85.6, 1.5, 120, 70, 110, 75, 120, 85, 'Sus alas emiten un velo de partículas brillantes. Se dice que representa un cuarto lunar.', 'Pokémon Lunar');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (489, 'Phione', 11, NULL, 3.1, 0.4, 80, 80, 80, 80, 80, 80, 'Si sube la temperatura del mar, infla el flotador de su cabeza y flota en grupo en la superficie.', 'Pokémon Marino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (490, 'Manaphy', 11, NULL, 1.4, 0.3, 100, 100, 100, 100, 100, 100, 'Nace con un maravilloso poder que le permite establecer vínculos con cualquier tipo de pokémon.', 'Pokémon Náutico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (491, 'Darkrai', 17, NULL, 50.5, 1.5, 70, 90, 90, 135, 90, 125, 'Defiende su territorio de personas y pokémon haciéndoles dormir y provocándoles pesadillas.', 'Pokémon Oscuridad');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (492, 'Shaymin', 12, NULL, 2.1, 0.2, 100, 100, 100, 100, 100, 100, 'Puede disolver las toxinas del aire para transformar tierra yerma en campos de flores.', 'Pokémon Gratitud');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (493, 'Arceus', 1, NULL, 320.0, 3.2, 120, 120, 120, 120, 120, 120, 'Según la mitología de sinnoh, arceus surgió de un huevo y después creó todo el mundo.', 'Pokémon Alfa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (494, 'Victini', 14, 10, 4.0, 0.4, 100, 100, 100, 100, 100, 100, 'Pokémon que atrae la victoria. Dicen que el entrenador que lo lleve saldrá vencedor de cualquier batalla.', 'Pokémon Victoria');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (495, 'Snivy', 12, NULL, 8.1, 0.6, 45, 45, 55, 45, 55, 63, 'Cuando recibe los rayos de sol, se mueve mucho más rápido que de costumbre. Usa mejor sus lianas que sus manos.', 'Pokémon Serp. Hierba');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (496, 'Servine', 12, NULL, 16.0, 0.8, 60, 60, 75, 60, 75, 83, 'Parece que se desliza al correr. Engaña a sus rivales con su velocidad y los fustiga con su látigo.', 'Pokémon Serp. Hierba');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (497, 'Serperior', 12, NULL, 63.0, 3.3, 75, 75, 95, 75, 95, 113, 'Tan solo muestra su verdadero poder a quienes no se amedrentan ante su noble pero inquisitoria mirada.', 'Pokémon Realeza');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (498, 'Tepig', 10, NULL, 9.9, 0.5, 65, 63, 45, 45, 45, 45, 'Evita con agilidad los ataques enemigos. Lanza bolas de fuego por su hocico y tuesta bayas del bosque para comer.', 'Pokémon Cerdo Fuego');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (499, 'Pignite', 10, 2, 55.5, 1.0, 90, 93, 55, 70, 55, 55, 'Cuanto más come, más combustible tiene en su interior para avivar sus llamas, con lo que también aumenta su poder.', 'Pokémon Cerdo Fuego');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (500, 'Emboar', 10, 2, 150.0, 1.6, 110, 123, 65, 100, 65, 65, 'Calienta sus puños con las llamas de su barbilla para propinar puñetazos ardientes. Es muy fiel a sus compañeros.', 'Pokémon Cerdo Ígneo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (501, 'Oshawott', 11, NULL, 5.9, 0.5, 55, 55, 45, 63, 45, 45, 'La vieira de su ombligo no solo sirve como arma, sino también como instrumento para cortar las bayas que estén duras.', 'Pokémon Nutria');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (502, 'Dewott', 11, NULL, 24.5, 0.8, 75, 75, 60, 83, 60, 60, 'Gran espadachín. Utiliza sus dos vieiras con soltura al ejecutar las técnicas que aprende con una disciplina espartana.', 'Pokémon Superación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (503, 'Samurott', 11, NULL, 94.6, 1.5, 95, 100, 85, 108, 70, 70, 'En un abrir y cerrar de ojos, puede desenvainar y envainar los sables que hay en sus patas delanteras.', 'Pokémon Majestuoso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (504, 'Patrat', 1, NULL, 11.6, 0.5, 45, 55, 39, 35, 39, 42, 'Almacena víveres en unas bolsas en sus mofletes y vigila durante días. Hace señales con la cola a sus compañeros.', 'Pokémon Explorador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (505, 'Watchog', 1, NULL, 27.0, 1.1, 60, 85, 69, 60, 69, 77, 'Los componentes luminiscentes de su interior hacen que sus ojos y su cuerpo brillen, y así intimida a sus rivales.', 'Pokémon Alerta');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (506, 'Lillipup', 1, NULL, 4.1, 0.4, 45, 60, 45, 25, 45, 55, 'Los mechones que cubren su cara actúan como un magnífico radar para detectar con precisión lo que hay a su alrededor.', 'Pokémon Perrito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (507, 'Herdier', 1, NULL, 14.7, 0.9, 65, 80, 65, 35, 65, 60, 'Este pokémon es muy leal. No solo ayuda a entrenadores, sino que también se preocupa por otros pokémon.', 'Pokémon Leal');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (508, 'Stoutland', 1, NULL, 61.0, 1.2, 85, 110, 90, 45, 90, 80, 'Socorre a humanos perdidos en montañas azotadas por ventiscas. Su largo pelaje le protege del frío.', 'Pokémon Magnánimo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (509, 'Purrloin', 17, NULL, 10.1, 0.4, 41, 50, 37, 50, 37, 66, 'Distrae a su oponente con movimientos gráciles y sustrae sus pertenencias. Cuando se enfada, ataca con las garras.', 'Pokémon Malicioso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (510, 'Liepard', 17, NULL, 37.5, 1.1, 64, 88, 50, 88, 50, 106, 'Su bella y grácil silueta le viene dada por su desarrollada musculatura, que le permite andar de noche sin hacer ruido.', 'Pokémon Calculador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (511, 'Pansage', 12, NULL, 10.5, 0.6, 50, 53, 48, 53, 48, 64, 'Comparte las hojas de su cabeza con pokémon agotados. Posee la facultad de aliviar el cansancio.', 'Pokémon Mono Hierba');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (512, 'Simisage', 12, NULL, 30.5, 1.1, 75, 98, 63, 98, 63, 101, 'Ataca estampando las espinas de su cola en el enemigo. Es un pokémon con mucho temperamento.', 'Pokémon Mono Pincho');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (513, 'Pansear', 10, NULL, 11.0, 0.6, 50, 53, 48, 53, 48, 64, 'Pokémon muy inteligente que tiene la costumbre de tostar las bayas antes de comérselas. Ayuda mucho a los humanos.', 'Pokémon Ardiente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (514, 'Simisear', 10, NULL, 28.0, 1.0, 75, 98, 63, 98, 63, 101, 'Achicharra a sus enemigos desprendiendo llamas por la cola y la cabeza, fruto de su desmesurado calor interno.', 'Pokémon Lumbre');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (515, 'Panpour', 11, NULL, 13.5, 0.6, 50, 53, 48, 53, 48, 64, 'El agua acumulada en su mata de pelo es rica en nutrientes. Usa su cola para regar plantas con esa misma agua.', 'Pokémon Salpicadura');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (516, 'Simipour', 11, NULL, 29.0, 1.0, 75, 98, 63, 98, 63, 101, 'Cuando lanza agua a alta presión con su cola, puede incluso resquebrajar una pared de cemento.', 'Pokémon Drenaje');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (517, 'Munna', 14, NULL, 23.3, 0.6, 76, 25, 45, 67, 55, 24, 'Cuando un humano o un pokémon sufre una pesadilla, munna se aparece ante ellos y la engulle.', 'Pokémon Comesueños');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (518, 'Musharna', 14, NULL, 60.5, 1.1, 116, 55, 85, 107, 95, 29, 'Multitud de sueños de humanos y pokémon se encuentran atrapados en el humo que despide por su frente.', 'Pokémon Duermevela');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (519, 'Pidove', 1, 3, 2.1, 0.3, 50, 55, 50, 36, 30, 43, 'Es un pokémon muy despistado. Aunque reciba órdenes de su entrenador, se le olvidan, y se queda esperando.', 'Pokémon Pichón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (520, 'Tranquill', 1, 3, 15.0, 0.6, 62, 77, 62, 50, 42, 65, 'Según los rumores, en lo más profundo del bosque donde mora tranquill, hay un mundo pacífico y exento de luchas.', 'Pokémon Torcaz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (521, 'Unfezant', 1, 3, 29.0, 1.2, 80, 115, 80, 65, 55, 93, 'El macho amenaza al enemigo meciendo el adorno de su cabeza. Al volar, la hembra es mucho más diestra que el macho.', 'Pokémon Altanero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (522, 'Blitzle', 13, NULL, 29.8, 0.8, 45, 60, 32, 50, 32, 76, 'Aparece cuando nubes envueltas en rayos encapotan el cielo. Atrapa rayos con su crin y almacena electricidad.', 'Pokémon Electrizado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (523, 'Zebstrika', 13, NULL, 79.5, 1.6, 75, 100, 63, 80, 63, 116, 'Es un pokémon con mucho temperamento. Cuando se enoja, lanza rayos desde su crin en todas direcciones.', 'Pokémon Rayo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (524, 'Roggenrola', 6, NULL, 18.0, 0.4, 55, 75, 85, 25, 25, 15, 'Su oreja tiene forma hexagonal. Su cuerpo fue comprimido bajo tierra. Por eso, es duro como el acero.', 'Pokémon Manto');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (525, 'Boldore', 6, NULL, 102.0, 0.9, 70, 105, 105, 50, 40, 20, 'La energía que no puede contener en el interior de su cuerpo se escapa formando cristales naranjas.', 'Pokémon Mineral');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (526, 'Gigalith', 6, NULL, 260.0, 1.7, 85, 135, 130, 60, 80, 25, 'Absorbe los rayos solares, procesa esa energía en su núcleo y lanza poderosas bolas de luz a sus enemigos.', 'Pokémon Presurizado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (527, 'Woobat', 14, 3, 2.1, 0.4, 65, 45, 43, 55, 43, 72, 'Vive en bosques oscuros y cavernas. Emite ultrasonidos por la nariz para saber todo lo que pasa a su alrededor.', 'Pokémon Murciélago');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (528, 'Swoobat', 14, 3, 10.5, 0.9, 67, 57, 55, 77, 55, 114, 'Cuando emite ondas ultrasónicas, que incluso pueden reventar el cemento, agita su cola con vehemencia.', 'Pokémon Galante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (529, 'Drilbur', 5, NULL, 8.5, 0.3, 60, 85, 40, 30, 45, 68, 'Cuando junta las garras y comienza a dar vueltas frenéticas, avanza bajo tierra a gran velocidad.', 'Pokémon Topo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (530, 'Excadrill', 5, 9, 40.4, 0.7, 110, 135, 60, 50, 65, 88, 'Su taladro ha evolucionado y es capaz de atravesar una lámina de hierro. Es muy útil para construir túneles.', 'Pokémon Subterráneo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (531, 'Audino', 1, NULL, 31.0, 1.1, 103, 60, 86, 60, 86, 50, 'Con las antenas de sus oídos, perciben el estado de un rival o predicen cuándo va a eclosionar un huevo pokémon.', 'Pokémon Escucha');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (532, 'Timburr', 2, NULL, 12.5, 0.6, 75, 80, 55, 25, 35, 35, 'Siempre lleva vigas de madera. Normalmente ayuda en obras de construcción.', 'Pokémon Musculoso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (533, 'Gurdurr', 2, NULL, 40.0, 1.2, 85, 105, 85, 40, 50, 40, 'Gracias a su entrenada musculatura, puede blandir vigas de acero sin problema para así derribar edificios enteros.', 'Pokémon Musculoso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (534, 'Conkeldurr', 2, NULL, 87.0, 1.4, 105, 140, 95, 55, 65, 45, 'Se cree que conkeldurr enseñó a los humanos la técnica para elaborar cemento hace más de 2000 años.', 'Pokémon Musculoso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (535, 'Tympole', 11, NULL, 4.5, 0.5, 50, 50, 40, 50, 40, 64, 'Emite sonidos imperceptibles para los humanos con la vibración de sus mejillas y advierte de algún peligro a los suyos.', 'Pokémon Renacuajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (536, 'Palpitoad', 11, 5, 17.0, 0.8, 75, 65, 55, 65, 55, 69, 'Cuando hace vibrar el bulto de su cabeza, no solo se embravece el mar, sino que la tierra también tiembla.', 'Pokémon Vibrante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (537, 'Seismitoad', 11, 5, 62.0, 1.5, 105, 95, 75, 85, 75, 74, 'Aumenta la potencia de sus puñetazos haciendo vibrar sus puños. Es capaz de quebrar una roca de un solo golpe.', 'Pokémon Vibrante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (538, 'Throh', 2, NULL, 55.5, 1.3, 120, 100, 85, 30, 85, 45, 'Aumenta su potencia si se aprieta el cinturón. Un throh salvaje teje su propio cinturón usando lianas.', 'Pokémon Judo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (539, 'Sawk', 2, NULL, 51.0, 1.4, 75, 125, 75, 30, 75, 85, 'Para lograr dominar las técnicas de kárate más letales, se retiran a las montañas y entrenan día y noche.', 'Pokémon Kárate');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (540, 'Sewaddle', 7, 12, 2.5, 0.3, 45, 53, 70, 40, 60, 42, 'Recorta hojas y las teje con el líquido viscoso que segrega por la boca para hacerse su propia ropa.', 'Pokémon Sastre');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (541, 'Swadloon', 7, 12, 7.3, 0.5, 55, 63, 90, 50, 80, 42, 'La vegetación abunda en los bosques que habita. Este pokémon transforma las hojas caídas en nutrientes.', 'Pokémon Arropado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (542, 'Leavanny', 7, 12, 20.5, 1.2, 75, 103, 80, 70, 80, 92, 'Si se encuentra con un pokémon joven, segrega por la boca hilos pegadizos con los que le teje una prenda de ropa.', 'Pokémon Cuidador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (543, 'Venipede', 7, 4, 5.3, 0.4, 30, 45, 59, 30, 39, 57, 'Percibe su entorno con su sentido del tacto, residente en su cabeza y cola. Su carácter es violento por naturaleza.', 'Pokémon Ciempiés');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (544, 'Whirlipede', 7, 4, 58.5, 1.2, 40, 55, 99, 40, 79, 47, 'Lo protege un duro caparazón. Gira como una rueda y realiza brutales placajes contra sus enemigos.', 'Pokémon Pupaciempiés');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (545, 'Scolipede', 7, 4, 200.5, 2.5, 60, 100, 89, 55, 69, 112, 'Atenaza a sus presas con las garras que sobresalen de su cuello hasta que no se pueden mover y las remata con veneno.', 'Pokémon Megaciempiés');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (546, 'Cottonee', 12, 18, 0.6, 0.3, 40, 27, 60, 37, 50, 66, 'Despide motas de algodón cuando lo atacan para confundir a los enemigos y poner pies en polvorosa.', 'Pokémon Bolalgodón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (547, 'Whimsicott', 12, 18, 6.6, 0.7, 60, 67, 85, 77, 75, 116, 'Aparecen en medio de un torbellino y entran en las casas para descolocar los muebles o esparcir algodón por doquier.', 'Pokémon Vuelalviento');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (548, 'Petilil', 12, NULL, 6.6, 0.5, 45, 35, 50, 70, 50, 30, 'Mordisquear las hojas de su cabeza permite recuperar toda la vitalidad, a pesar de que están muy amargas.', 'Pokémon Bulbo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (549, 'Lilligant', 12, NULL, 16.3, 1.1, 70, 60, 75, 110, 75, 90, 'El aroma de la flor de su tocado relaja a cualquiera a su alrededor. Hay que cuidarle mucho para que no se marchite.', 'Pokémon Adornofloral');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (550, 'Basculin', 11, NULL, 18.0, 1.0, 70, 92, 65, 80, 55, 98, 'Los de color azul y los de color rojo siempre se pelean. Son muy violentos, pero su sabor es excepcional.', 'Pokémon Violento');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (551, 'Sandile', 5, 17, 15.2, 0.7, 50, 72, 35, 35, 35, 65, 'Se desplaza hundido en la arena, dejando al descubierto solo los ojos y la nariz. Una membrana negra le protege los ojos.', 'Pokémon Desierdrilo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (552, 'Krokorok', 5, 17, 33.4, 1.0, 60, 82, 45, 45, 45, 74, 'Viven en manadas. La membrana que cubre sus ojos los protege de la arena.', 'Pokémon Desierdrilo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (553, 'Krookodile', 5, 17, 96.3, 1.5, 95, 117, 80, 65, 70, 92, 'Un pokémon bastante violento. Ataca todo lo que se mueva delante de él con sus mortíferas mandíbulas.', 'Pokémon Amenazador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (554, 'Darumaka', 10, NULL, 37.5, 0.6, 70, 90, 45, 15, 45, 50, 'Como sus excrementos están muy calientes, la gente de antaño se los metía en los bolsillos para calentarse.', 'Pokémon Daruma');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (555, 'Darmanitan', 10, NULL, 92.9, 1.3, 105, 140, 55, 30, 55, 95, 'Cuando resulta gravemente herido, se endurece como una piedra y medita para agudizar la mente.', 'Pokémon Candente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (556, 'Maractus', 12, NULL, 28.0, 1.0, 75, 86, 67, 106, 67, 60, 'Espanta a los pokémon pájaro con un baile rápido y haciendo ruido para evitar que picoteen las semillas de sus flores.', 'Pokémon Cactus');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (557, 'Dwebble', 7, 6, 14.5, 0.3, 50, 65, 85, 35, 35, 55, 'Abre un agujero en la roca y vive en él. Si la roca se resquebraja, no se queda tranquilo hasta que no encuentra otra.', 'Pokémon Casapiedra');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (558, 'Crustle', 7, 6, 200.0, 1.4, 70, 105, 125, 65, 75, 45, 'Su inmensa capacidad motriz le permite desplazarse cargando a sus espaldas rocas enormes durante días.', 'Pokémon Casarroca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (559, 'Scraggy', 17, 2, 11.8, 0.6, 50, 75, 70, 35, 70, 48, 'Se sube la piel hasta el cuello para defenderse. Su elasticidad, similar a la de una goma, reduce el daño recibido.', 'Pokémon Mudapiel');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (560, 'Scrafty', 17, 2, 30.0, 1.1, 65, 90, 115, 45, 115, 58, 'Se protege de los ataques con la muda de su piel mientras reparte patadas. Cuanto mayor es su cresta, más se le respeta.', 'Pokémon Gamberro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (561, 'Sigilyph', 14, 3, 14.0, 1.4, 72, 58, 80, 103, 80, 97, 'Fue la deidad protectora de una ciudad milenaria. ataca con su poder psíquico a los enemigos que invaden su territorio.', 'Pokémon Pseudopájaro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (562, 'Yamask', 8, NULL, 1.5, 0.5, 38, 30, 85, 55, 65, 30, 'Almas de humanos sepultados en tumbas que han adquirido forma de pokémon. Recuerdan su vida antes de la muerte.', 'Pokémon Espíritu');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (563, 'Cofagrigus', 8, NULL, 76.5, 1.7, 58, 50, 145, 95, 105, 30, 'Se comenta que engulle a todo humano que se le acerca y lo momifica. Su comida favorita son las pepitas de oro.', 'Pokémon Sepultura');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (564, 'Tirtouga', 11, 6, 16.5, 0.7, 54, 78, 103, 53, 45, 22, 'Ya pululaba por las aguas hace 100 millones de años. también va a tierra a veces en busca de alimento.', 'Pokémon Pretortuga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (565, 'Carracosta', 11, 6, 81.0, 1.2, 74, 108, 133, 83, 65, 32, 'Vive en el mar y en la superficie. Sus garras pueden abrir un agujero en el fondo de un petrolero.', 'Pokémon Pretortuga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (566, 'Archen', 6, 3, 9.5, 0.5, 55, 112, 45, 74, 45, 70, 'Pokémon que renació de un fósil. Dicen que es el antepasado de todos los pokémon pájaro.', 'Pokémon Protopájaro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (567, 'Archeops', 6, 3, 32.0, 1.4, 75, 140, 65, 112, 65, 110, 'Levanta el vuelo tras coger carrerilla. Coopera con sus compañeros de bandada para atrapar a sus presas.', 'Pokémon Protopájaro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (568, 'Trubbish', 4, NULL, 31.0, 0.6, 50, 50, 62, 40, 62, 65, 'Pokémon nacido de la reacción química entre una bolsa de basura y residuos industriales.', 'Pokémon Bolsa Basura');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (569, 'Garbodor', 4, NULL, 107.3, 1.9, 80, 95, 82, 60, 82, 75, 'Apresa a su rival con el brazo izquierdo y le da el golpe de gracia exhalando por la boca un apestoso gas venenoso.', 'Pokémon Vertedero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (570, 'Zorua', 17, NULL, 12.5, 0.7, 40, 65, 40, 80, 40, 65, 'Adopta la apariencia de su enemigo para engañarlo vilmente y aprovecha su estupor para poner tierra por medio.', 'Pokémon Zorro Pillo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (571, 'Zoroark', 17, NULL, 81.1, 1.6, 60, 105, 60, 120, 60, 105, 'Tiene la facultad de confundir a muchos humanos a la vez. Protege su guarida creando un paisaje ilusorio.', 'Pokémon Disfrazorro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (572, 'Minccino', 1, NULL, 5.8, 0.4, 55, 50, 40, 40, 40, 75, 'Se saludan entre sí frotándose con la cola y limpiándose a conciencia.', 'Pokémon Chinchilla');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (573, 'Cinccino', 1, NULL, 7.5, 0.5, 75, 95, 60, 65, 60, 115, 'El pelo que cubre su cuerpo proporciona una agradable sensación al tocarlo. No acumula ni polvo ni electricidad estática.', 'Pokémon Estola');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (574, 'Gothita', 14, NULL, 5.8, 0.4, 45, 30, 50, 55, 65, 45, 'Amplifica su poder psíquico con sus antenitas en forma de lazo. Siempre está mirando algo fijamente.', 'Pokémon Inquisitivo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (575, 'Gothorita', 14, NULL, 18.0, 0.7, 60, 45, 70, 75, 85, 55, 'La luz de las estrellas origina su poder. De noche, marca su posición haciendo levitar piedras con su poder psíquico.', 'Pokémon Manipulador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (576, 'Gothitelle', 14, NULL, 44.0, 1.5, 70, 55, 95, 95, 110, 65, 'Su inmenso poder psíquico distorsiona el espacio y le permite reflejar incluso estrellas a años luz de distancia.', 'Pokémon Astro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (577, 'Solosis', 14, NULL, 1.0, 0.3, 45, 30, 40, 105, 50, 20, 'Gracias a la sustancia viscosa que envuelve su cuerpo, permanece tranquilo por dura o rigurosa que sea la situación.', 'Pokémon Célula');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (578, 'Duosion', 14, NULL, 8.0, 0.6, 65, 40, 50, 125, 60, 30, 'Cuando las dos partes de su cerebro piensan lo mismo, saca todo su poder a relucir.', 'Pokémon Mitosis');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (579, 'Reuniclus', 14, NULL, 20.1, 1.0, 110, 65, 75, 125, 85, 30, 'Hacen gala de su poder psíquico y mueven sus brazos compuestos de líquido para controlar rocas y hacerlas añicos.', 'Pokémon Ampliación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (580, 'Ducklett', 11, 3, 5.5, 0.5, 62, 44, 50, 44, 50, 55, 'Cuando se ve atacado, las plumas que lo recubren despiden ráfagas de agua que lo ayudan a escapar.', 'Pokémon Ave Agua');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (581, 'Swanna', 11, 3, 24.2, 1.3, 75, 87, 63, 87, 63, 98, 'Aunque no lo parezca por su grácil elegancia, puede batir con fuerza las alas y volar durante miles de kilómetros.', 'Pokémon Cisne');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (582, 'Vanillite', 15, NULL, 5.7, 0.4, 36, 50, 50, 65, 60, 44, 'Lanza un aliento de -50 °c. Crea bloques de nieve y es capaz de lograr que nieve a su alrededor.', 'Pokémon Nieve Fresca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (583, 'Vanillish', 15, NULL, 41.0, 1.1, 51, 65, 65, 80, 75, 59, 'Enfrían el aire a su alrededor y crean partículas de hielo mediante las cuales congelan a sus oponentes.', 'Pokémon Nieve Helada');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (584, 'Vanilluxe', 15, NULL, 57.5, 1.3, 71, 95, 85, 110, 95, 79, 'Si sus dos cabezas se irritan al mismo tiempo, genera una ventisca desde su cuerno cubriendo su entorno con nieve.', 'Pokémon Nieve Gélida');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (585, 'Deerling', 1, 12, 19.5, 0.6, 60, 60, 50, 40, 50, 75, 'El color de su cuerpo cambia no solo con las estaciones, sino también con los cambios de humedad y temperatura.', 'Pokémon Estacional');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (586, 'Sawsbuck', 1, 12, 92.5, 1.9, 80, 100, 70, 60, 70, 95, 'Cambia de morada dependiendo de las estaciones. observando sus cuernos, la gente sabe cuándo estas van a cambiar.', 'Pokémon Estacional');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (587, 'Emolga', 13, 3, 5.0, 0.4, 55, 75, 60, 75, 60, 103, 'Vive en la copa de los árboles. Produce descargas mientras vuela con el interior de la membrana que hace de capa.', 'Pokémon Vuelardilla');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (588, 'Karrablast', 7, NULL, 5.9, 0.5, 50, 75, 45, 40, 45, 60, 'Si recibe un estímulo eléctrico al estar en contacto con un shelmet, evoluciona.', 'Pokémon Bocado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (589, 'Escavalier', 7, 9, 33.0, 1.0, 70, 135, 105, 60, 105, 20, 'Se defienden de los ataques con el caparazón que roban a los shelmet. Atacan con sus dos mortíferas lanzas.', 'Pokémon Soldado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (590, 'Foongus', 12, 4, 1.0, 0.2, 69, 55, 45, 55, 55, 15, 'Gracias a su misterioso parecido a una poké ball, atrae a sus presas y lanza rápidamente esporas de veneno.', 'Pokémon Hongo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (591, 'Amoonguss', 12, 4, 10.5, 0.6, 114, 85, 70, 85, 80, 30, 'Aunque intenta pasar por parte superior de una poké ball para atraer a sus presas, consigue engañar a pocos.', 'Pokémon Hongo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (592, 'Frillish', 11, 8, 33.0, 1.2, 55, 40, 50, 65, 85, 40, 'Paraliza a sus presas con veneno y las lleva a su morada, que se encuentra a 8000 m en el fondo del mar.', 'Pokémon Ingrávido');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (593, 'Jellicent', 11, 8, 135.0, 2.2, 100, 60, 70, 85, 105, 60, 'Su cuerpo se compone de agua salada. Dicen que ha construido un palacio en el fondo del mar con restos de barcos.', 'Pokémon Ingrávido');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (594, 'Alomomola', 11, NULL, 31.6, 1.2, 165, 75, 80, 40, 45, 65, 'Vive flotando a la deriva en mar abierto. Siempre que encuentra un pokémon herido, lo abraza y lo lleva a la costa.', 'Pokémon Socorrista');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (595, 'Joltik', 7, 13, 0.6, 0.1, 50, 47, 50, 57, 50, 65, 'No puede generar electricidad por sí mismo, así que se encarama a pokémon grandes y absorbe su energía estática.', 'Pokémon Lapa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (596, 'Galvantula', 7, 13, 14.3, 0.8, 70, 77, 60, 97, 60, 108, 'Pone trampas con hilos electrificados. De este modo, electrocuta a sus enemigos y los inmoviliza por completo.', 'Pokémon Electroaraña');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (597, 'Ferroseed', 12, 9, 18.8, 0.6, 44, 50, 91, 24, 86, 10, 'Lanza numerosas púas al detectar peligro. En cuanto tiene ocasión, echa a rodar y se escapa.', 'Pokémon Fruto Espina');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (598, 'Ferrothorn', 12, 9, 110.0, 1.0, 74, 94, 131, 54, 116, 20, 'Azota al enemigo con sus tres tentáculos y después dispara púas hasta que no deja ni rastro del rival.', 'Pokémon Bola Espina');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (599, 'Klink', 9, NULL, 21.0, 0.3, 40, 55, 70, 45, 60, 30, 'Genera la energía vital que necesita haciendo girar sus dos cuerpos.', 'Pokémon Engranaje');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (600, 'Klang', 9, NULL, 51.0, 0.6, 60, 80, 95, 70, 85, 50, 'Muestra sus sentimientos cambiando la dirección de su giro. Cuanto más se enfade, más rápido girará.', 'Pokémon Engranaje');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (601, 'Klinklang', 9, NULL, 81.0, 0.6, 60, 100, 115, 70, 85, 90, 'Recarga su energía rápidamente haciendo girar con gran celeridad el engranaje equipado con un núcleo rojo.', 'Pokémon Engranaje');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (602, 'Tynamo', 13, NULL, 0.3, 0.2, 35, 55, 40, 45, 40, 60, 'La electricidad de uno solo es diminuta, pero, si multitud de tynamo se conectan, consiguen la potencia de un rayo.', 'Pokémon Electropez');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (603, 'Eelektrik', 13, NULL, 22.0, 1.2, 65, 85, 70, 75, 70, 40, 'Se enrolla alrededor de su presa, la paraliza con la electricidad de sus marcas circulares y le asesta mordiscos.', 'Pokémon Electropez');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (604, 'Eelektross', 13, NULL, 80.5, 2.1, 85, 115, 80, 105, 80, 50, 'Pega la ventosa de su boca en su presa y la electrocuta con sus colmillos.', 'Pokémon Electropez');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (605, 'Elgyem', 14, NULL, 9.0, 0.5, 55, 55, 55, 85, 55, 30, 'Posee un gran poder psíquico. Lo usa para comprimir el cerebro del enemigo y ocasionarle dolores de cabeza.', 'Pokémon Cerebro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (606, 'Beheeyem', 14, NULL, 34.5, 1.0, 75, 75, 75, 125, 95, 40, 'Parece que se comunica haciendo brillar sus dedos tricolores, pero nadie ha conseguido descifrar aún su patrón.', 'Pokémon Cerebro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (607, 'Litwick', 8, 10, 3.1, 0.3, 50, 30, 55, 65, 55, 20, 'Finge ser una luz guía en el camino, pero en realidad solo quiere absorber la energía vital de quien lo sigue.', 'Pokémon Vela');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (608, 'Lampent', 8, 10, 13.0, 0.6, 60, 40, 60, 95, 60, 55, 'Al absorber almas, su llama se enciende. Espera a que la gente fallezca, así que suele deambular por los hospitales.', 'Pokémon Farolillo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (609, 'Chandelure', 8, 10, 34.3, 1.0, 60, 55, 90, 145, 90, 80, 'Sus llamas absorben el alma del enemigo y la abrasan, dejando solo su cuerpo inerte.', 'Pokémon Señuelo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (610, 'Axew', 16, NULL, 18.0, 0.6, 46, 87, 60, 30, 40, 57, 'Usa sus colmillos para romper las bayas que come. estos se regeneran una y otra vez, por eso son tan fuertes y afilados.', 'Pokémon Colmillo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (611, 'Fraxure', 16, NULL, 36.0, 1.0, 66, 117, 70, 40, 50, 67, 'Sus colmillos pueden hacer añicos rocas inmensas. Las luchas territoriales de los fraxure son encarnizadas.', 'Pokémon Boca Hacha');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (612, 'Haxorus', 16, NULL, 105.5, 1.8, 76, 147, 90, 60, 70, 97, 'Es gentil, pero no perdona a todo el que ose invadir su territorio. Su mayor baza son sus colmillos, que cortan el acero.', 'Pokémon Boca Hacha');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (613, 'Cubchoo', 15, NULL, 8.5, 0.5, 55, 70, 40, 60, 40, 40, 'Siempre está con mucosidades colgando. Las aspira para hacer un movimiento, ya que son su materia prima para esto.', 'Pokémon Congelación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (614, 'Beartic', 15, NULL, 260.0, 2.6, 95, 130, 80, 70, 80, 50, 'Adoran los mares gélidos del norte. Congelan su aliento para crear sendas por las que caminar sobre las aguas.', 'Pokémon Glaciación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (615, 'Cryogonal', 15, NULL, 148.0, 1.1, 80, 50, 50, 95, 135, 105, 'Nació entre nubes de nieve. Atrapa a sus presas con una cadena hecha de hielo cristalizado.', 'Pokémon Cristal');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (616, 'Shelmet', 7, NULL, 7.7, 0.4, 50, 40, 85, 40, 65, 25, 'Si está junto a karrablast, y ambos reciben una descarga eléctrica, evolucionan los dos a la vez.', 'Pokémon Caracol');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (617, 'Accelgor', 7, NULL, 25.3, 0.8, 80, 70, 40, 100, 60, 145, 'Se siente ligero una vez liberado de su caparazón. lucha y se mueve como un ninja.', 'Pokémon Sincaparazón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (618, 'Stunfisk', 5, 13, 11.0, 0.7, 109, 66, 84, 81, 99, 32, 'Ni un pisotón de un luchador de sumo lo aplastaría gracias a su piel sólida. Sonríe al soltar descargas eléctricas.', 'Pokémon Trampa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (619, 'Mienfoo', 2, NULL, 20.0, 0.9, 45, 85, 50, 55, 50, 65, 'La velocidad de sus ataques es su gran orgullo. Lo que le falta de fuerza bruta lo compensa en cantidad de golpes.', 'Pokémon Arte Marcial');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (620, 'Mienshao', 2, NULL, 35.5, 1.4, 65, 125, 60, 95, 60, 105, 'Usa el pelaje de sus brazos a manera de látigos. sus ataques alcanzan velocidades de vértigo.', 'Pokémon Arte Marcial');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (621, 'Druddigon', 16, NULL, 139.0, 1.6, 77, 120, 90, 60, 90, 48, 'Corre por estrechas cavernas y atrapa a sus presas con sus afiladas garras. La piel de la cara es más dura que una roca.', 'Pokémon Cueva');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (622, 'Golett', 5, 8, 92.0, 1.0, 59, 74, 50, 35, 50, 35, 'Se mueve gracias a la energía que quema dentro de su cuerpo, pero el origen de esta energía es incierto.', 'Pokémon Autómata');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (623, 'Golurk', 5, 8, 330.0, 2.8, 89, 124, 80, 55, 80, 55, 'Al parecer, aquel que antaño lo creó le ordenó proteger a los pokémon y a los humanos.', 'Pokémon Autómata');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (624, 'Pawniard', 17, 9, 10.2, 0.5, 45, 85, 70, 40, 40, 60, 'Su cuerpo está provisto de hojas afiladas. Cuando se desgastan, las afila con cantos rodados a la orilla del río.', 'Pokémon Tajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (625, 'Bisharp', 17, 9, 70.0, 1.6, 65, 125, 100, 60, 70, 70, 'Un pokémon temible que remata a su presa una vez que su séquito de pawniard la ha debilitado e inmovilizado.', 'Pokémon Filo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (626, 'Bouffalant', 1, NULL, 94.6, 1.6, 95, 110, 95, 40, 95, 55, 'Embiste con la cabeza sin distinción. Su potencia es capaz de hacer descarrilar un tren en marcha.', 'Pokémon Torocabezazo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (627, 'Rufflet', 1, 3, 10.5, 0.5, 70, 83, 50, 37, 50, 60, 'Quiebra bayas con sus extremidades. Se enfrenta con valentía a todo enemigo por fuerte que este sea.', 'Pokémon Aguilucho');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (628, 'Braviary', 1, 3, 41.0, 1.5, 100, 123, 75, 57, 75, 80, 'Un valiente guerrero del cielo que no duda en luchar por sus compañeros por graves que sean sus heridas.', 'Pokémon Aguerrido');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (629, 'Vullaby', 17, 3, 9.0, 0.5, 70, 55, 75, 45, 65, 60, 'No puede volar porque sus alas son muy pequeñas. cuando va a evolucionar, se deshace de su calavera.', 'Pokémon Pañal');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (630, 'Mandibuzz', 17, 3, 39.5, 1.2, 110, 65, 105, 55, 95, 80, 'Vuela en círculos. Cuando avista una presa, la ataca y la transporta a su nido con suma facilidad.', 'Pokémon Buitre Hueso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (631, 'Heatmor', 10, NULL, 58.0, 1.4, 85, 97, 66, 105, 66, 65, 'Usa su lengua de fuego para derretir la armadura de acero de los durant y llegar así hasta su interior.', 'Pokémon Hormiguero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (632, 'Durant', 7, 9, 33.0, 0.3, 58, 109, 112, 48, 48, 109, 'Actúan en grupo para expulsar de sus colonias a los heatmor, sus depredadores naturales.', 'Pokémon Hormigacero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (633, 'Deino', 17, 16, 17.3, 0.8, 52, 65, 50, 45, 50, 38, 'No ve, por eso va haciendo placajes y dando mordiscos a diestro y siniestro. Su cuerpo está plagado de heridas.', 'Pokémon Tosco');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (634, 'Zweilous', 17, 16, 50.0, 1.4, 72, 85, 70, 65, 70, 58, 'Sus dos cabezas se llevan fatal, y la que consigue comer más que la otra es la que lleva la voz cantante.', 'Pokémon Violento');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (635, 'Hydreigon', 17, 16, 160.0, 1.8, 92, 105, 90, 125, 90, 98, 'Las cabezas de sus dos brazos no tienen cerebro. Con sus tres cabezas destruye y devora todo a su paso.', 'Pokémon Voraz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (636, 'Larvesta', 7, 10, 28.8, 1.1, 55, 85, 55, 50, 55, 60, 'Se dice que nació del sol. Al evolucionar, cubre su cuerpo con llamas que brotan de sus cuernos.', 'Pokémon Antorcha');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (637, 'Volcarona', 7, 10, 46.0, 1.6, 85, 60, 65, 135, 105, 100, 'Cuando combate, despide escamas ardientes de sus seis alas y genera un mar ígneo a su alrededor.', 'Pokémon Sol');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (638, 'Cobalion', 9, 2, 250.0, 2.1, 91, 90, 129, 90, 72, 108, 'Posee un cuerpo y un corazón de acero. Basta una mirada suya para que pokémon violentos le obedezcan.', 'Pokémon Tesón Acero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (639, 'Terrakion', 6, 2, 260.0, 1.9, 91, 129, 90, 72, 90, 108, 'Su potencia al embestir es capaz de derribar las murallas de un castillo. Las leyendas hablan de sus proezas.', 'Pokémon Gruta');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (640, 'Virizion', 12, 2, 200.0, 2.0, 91, 90, 72, 90, 129, 108, 'Sus cuernos son hojas afiladas. Sacude a sus enemigos con sus relampagueantes movimientos y los reduce.', 'Pokémon Prado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (641, 'Tornadus', 3, NULL, 63.0, 1.5, 79, 115, 70, 125, 80, 111, 'La parte inferior de su cuerpo, similar a una nube, está recubierta de un campo de energía. Vuela a 300 km/h.', 'Pokémon Torbellino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (642, 'Thundurus', 13, 3, 61.0, 1.5, 79, 115, 70, 125, 80, 111, 'Lanza descargas eléctricas por las púas de su cola. sobrevuela teselia dejando caer rayos.', 'Pokémon Centella');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (643, 'Reshiram', 16, 10, 330.0, 3.2, 100, 120, 100, 150, 120, 90, 'Pokémon legendario capaz de abrasar el mundo con sus llamas. Ayuda a quienes persiguen un mundo veraz.', 'Pokémon Blanco Veraz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (644, 'Zekrom', 16, 13, 345.0, 2.9, 100, 150, 120, 120, 100, 90, 'Pokémon legendario capaz de chamuscar el mundo con sus rayos. Ayuda a quienes persiguen un mundo de ideales.', 'Pokémon Negro Puro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (645, 'Landorus', 5, 3, 68.0, 1.5, 89, 125, 90, 115, 80, 101, 'Lo consideran una deidad de las cosechas porque los cultivos crecen en abundancia en las tierras por las que pasa.', 'Pokémon Fertilidad');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (646, 'Kyurem', 16, 15, 325.0, 3.0, 125, 130, 90, 130, 90, 95, 'Pokémon legendario que aguarda al héroe que compense el vacío de su cuerpo de hielo con verdad e ideales.', 'Pokémon Frontera');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (647, 'Keldeo', 11, 2, 48.5, 1.4, 91, 72, 90, 129, 90, 108, 'Corre por mares, ríos o cualquier superficie acuática de todo el mundo. Aparece en aguas límpidas.', 'Pokémon Potro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (648, 'Meloetta', 1, 14, 6.5, 0.6, 100, 77, 77, 128, 128, 90, 'Las melodías que canta meloetta tienen el poder de hacer sentir felicidad a los pokémon que hay a su alrededor.', 'Pokémon Melodía');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (649, 'Genesect', 7, 9, 82.5, 1.5, 71, 120, 95, 120, 95, 99, 'Pokémon de tipo bicho de la antigüedad, modificado por el equipo plasma. El cañón de su lomo ha sido mejorado.', 'Pokémon Paleozoico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (650, 'Chespin', 12, NULL, 9.0, 0.4, 56, 61, 65, 48, 45, 38, 'Cuando acumula energía, las suaves púas de su cabeza se vuelven tan duras y afiladas que hasta pueden atravesar rocas.', 'Pokémon Erizo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (651, 'Quilladin', 12, NULL, 29.0, 0.7, 61, 78, 95, 56, 58, 57, 'Desvía los ataques de los enemigos con la robusta coraza que cubre su cuerpo y contraataca con sus dos afilados cuernos.', 'Pokémon Corazaespín');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (652, 'Chesnaught', 12, 2, 90.0, 1.6, 88, 107, 122, 74, 75, 64, 'Su fuerza es tal que, de un empellón, puede hacer volcar un tanque de 50 toneladas. Protege a sus aliados cuando hace de escudo.', 'Pokémon Corazaespín');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (653, 'Fennekin', 10, NULL, 9.4, 0.4, 40, 45, 40, 62, 60, 60, 'Tras masticar e ingerir pequeñas ramas se siente pletórico y expulsa aire caliente por sus grandes orejas a temperaturas superiores a los 200 ºc.', 'Pokémon Zorro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (654, 'Braixen', 10, NULL, 14.5, 1.0, 59, 59, 58, 90, 70, 73, 'Tiene una rama enredada en la cola. Cuando la saca, la rama prende fuego al hacer fricción con su pelaje y se lanza al ataque con ella.', 'Pokémon Zorro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (655, 'Delphox', 10, 14, 39.0, 1.5, 75, 69, 72, 114, 100, 104, 'Fija la mirada en la llama que arde en la punta de su bastón para concentrarse y prever sucesos que tienen lugar en el futuro.', 'Pokémon Zorro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (656, 'Froakie', 11, NULL, 7.0, 0.3, 41, 56, 40, 62, 44, 71, 'Secreta burbujas tanto por la espalda como por el pecho. Gracias a la elasticidad de estas, puede parar ataques y reducir el daño recibido.', 'Pokémon Burburrana');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (657, 'Frogadier', 11, NULL, 10.9, 0.6, 54, 63, 52, 83, 56, 97, 'Puede lanzar piedras envueltas en burbujas con tal puntería, que acierta latas vacías a 30 metros de distancia.', 'Pokémon Burburrana');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (658, 'Greninja', 11, 17, 40.0, 1.5, 72, 95, 67, 103, 71, 122, 'Comprime el agua y crea estrellas ninja con las que ataca al enemigo. Cuando las hace girar a gran velocidad cortan en dos hasta el metal.', 'Pokémon Ninja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (659, 'Bunnelby', 1, NULL, 5.0, 0.4, 38, 36, 38, 32, 36, 57, 'Usa sus grandes orejas para excavar en la tierra y así crear madrigueras. Puede pasarse una noche entera cavando sin parar.', 'Pokémon Excavador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (660, 'Diggersby', 1, 5, 42.4, 1.0, 85, 56, 77, 50, 77, 78, 'Sus grandes orejas tienen tal fuerza que con ellas puede levantar con facilidad rocas de más de una tonelada. Es de una ayuda inestimable en las obras.', 'Pokémon Excavador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (661, 'Fletchling', 1, 3, 1.7, 0.3, 45, 50, 43, 40, 38, 62, 'Es muy amigable y se comunica con sus aliados mediante melodiosos gorjeos y el movimiento de su cola y sus gráciles alas.', 'Pokémon Petirrojo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (662, 'Fletchinder', 10, 3, 16.0, 0.7, 62, 73, 55, 56, 52, 84, 'Lanza chispas por su pico y prende fuego a la hierba alta para así sorprender a sus presas y atraparlas cuando intentan escapar.', 'Pokémon Lumbre');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (663, 'Talonflame', 10, 3, 24.5, 1.2, 78, 81, 71, 74, 69, 126, 'Si se exalta durante un combate reñido, arroja chispas y ascuas de los intersticios entre su plumaje mientras vuela.', 'Pokémon Flamígero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (664, 'Scatterbug', 7, NULL, 2.5, 0.3, 38, 35, 40, 27, 25, 35, 'Cuando recibe el ataque de un pokémon pájaro, expulsa unas escamas negras que provocan parálisis si se entra en contacto con ellas.', 'Pokémon Tiraescamas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (665, 'Spewpa', 7, NULL, 8.4, 0.3, 45, 22, 60, 27, 30, 29, 'Habita en la sombra de la espesura. Si percibe alguna amenaza, su pelaje se eriza y se vuelve muy afilado para así intimidar al enemigo.', 'Pokémon Tiraescamas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (666, 'Vivillon', 7, 3, 17.0, 1.2, 80, 52, 50, 90, 50, 89, 'En el mundo existen vivillon con diferentes motivos en sus alas. El clima de cada región influye en sus rasgos.', 'Pokémon Escamaposa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (667, 'Litleo', 10, 1, 13.5, 0.6, 62, 50, 58, 73, 54, 72, 'Cuanto más poderoso es el enemigo al que se enfrenta, más calor desprende su mechón y más energía recorre todo su cuerpo.', 'Pokémon Leoncito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (668, 'Pyroar', 10, 1, 81.5, 1.5, 86, 68, 72, 109, 66, 106, 'El macho con la melena de fuego más grande de toda la manada es el líder de sus compañeros.', 'Pokémon Regio');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (669, 'Flabébé', 18, NULL, 0.1, 0.1, 44, 38, 39, 61, 79, 42, 'Acumula el polen de las flores para confeccionar la tiara que lleva en la cabeza, que cuenta con propiedades curativas.', 'Pokémon Monoflor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (670, 'Floette', 18, NULL, 0.9, 0.2, 54, 45, 47, 75, 98, 52, 'Revolotea alrededor de campos de flores y cuida de aquellas que empiezan a marchitarse. Usa en combate el poder oculto de las flores.', 'Pokémon Monoflor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (671, 'Florges', 18, NULL, 10.0, 1.1, 78, 65, 68, 112, 154, 75, 'Habita en preciosos jardines floridos. Absorbe la energía que emiten las plantas y la convierte en su fuente de poder.', 'Pokémon Jardín');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (672, 'Skiddo', 12, NULL, 31.0, 0.9, 66, 65, 48, 62, 57, 52, 'Se dice que fue la primera especie de pokémon que se acostumbró a vivir con los seres humanos. Tiene una naturaleza muy afable.', 'Pokémon Montura');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (673, 'Gogoat', 12, NULL, 91.0, 1.7, 123, 100, 62, 97, 81, 68, 'Percibe los sentimientos del entrenador por la forma en la que agarra sus cuernos, aunque sean variaciones sutiles, y así avanzan como si fueran un solo ente.', 'Pokémon Montura');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (674, 'Pancham', 2, NULL, 8.0, 0.6, 67, 82, 62, 46, 48, 43, 'Se esfuerza mucho para que el enemigo no lo menosprecie, pero su mirada no intimida demasiado. se caracteriza por mascar siempre una hoja.', 'Pokémon Juguetón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (675, 'Pangoro', 2, 17, 136.0, 2.1, 95, 124, 78, 69, 71, 58, 'Es muy temperamental, pero no permite que nadie se meta con los débiles. Mediante su hoja, predice los movimientos del enemigo.', 'Pokémon Rostro Fiero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (676, 'Furfrou', 1, NULL, 28.0, 1.2, 75, 80, 60, 65, 90, 102, 'Cuando le esquilan su mullido pelaje, no solo mejora su apariencia, sino que aumenta su agilidad.', 'Pokémon Caniche');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (677, 'Espurr', 14, NULL, 3.5, 0.3, 62, 48, 54, 63, 60, 68, 'Protege con las orejas el órgano con el que irradia su intenso poder psíquico para evitar que este se escape al exterior.', 'Pokémon Moderación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (678, 'Meowstic', 14, NULL, 8.5, 0.6, 74, 48, 76, 83, 81, 104, 'Cuando percibe peligro, yergue sus orejas y desata un poder psíquico capaz de levantar un camión de unas 10 toneladas.', 'Pokémon Autocontrol');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (679, 'Honedge', 9, 8, 2.0, 0.8, 45, 80, 100, 35, 37, 28, 'Cuentan que este pokémon nace cuando el espíritu de un difunto toma posesión de una espada antigua. se aferra a las personas y absorbe su energía.', 'Pokémon Tizona');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (680, 'Doublade', 9, 8, 4.5, 0.8, 59, 110, 150, 45, 49, 35, 'Al evolucionar se divide en dos espadas. se comunican entre sí mediante telepatía y coordinan los ataques para rebanar al enemigo.', 'Pokémon Tizona');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (681, 'Aegislash', 9, 8, 53.0, 1.7, 60, 50, 140, 50, 140, 60, 'Han permanecido junto a estirpes de reyes durante generaciones. Mediante su poder espectral pueden controlar a humanos y a pokémon.', 'Pokémon Espada Real');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (682, 'Spritzee', 18, NULL, 0.5, 0.2, 78, 52, 60, 63, 65, 23, 'Su cuerpo emana una fragancia que embelesa a quien la huele. La fragancia es diferente según los alimentos que ingiere.', 'Pokémon Aroma');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (683, 'Aromatisse', 18, NULL, 15.5, 0.8, 101, 72, 72, 99, 89, 29, 'Puede producir multitud de olores diferentes y, en combate, secreta los que le resultan molestos al enemigo para así contar con cierta ventaja.', 'Pokémon Fragancia');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (684, 'Swirlix', 18, NULL, 3.5, 0.4, 62, 48, 66, 59, 57, 49, 'Escupe hebras pegajosas de color blanco y textura parecida a la del algodón de azúcar, con las que envuelve al enemigo y le impide que se mueva.', 'Pokémon Chuchería');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (685, 'Slurpuff', 18, NULL, 5.0, 0.8, 82, 80, 86, 85, 75, 72, 'Puede rastrear hasta el más débil de los olores. Hace uso de su fino olfato para ayudar a los pasteleros más exquisitos.', 'Pokémon Nata');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (686, 'Inkay', 17, 14, 3.5, 0.4, 53, 54, 53, 37, 46, 45, 'Los enemigos que contemplan el titilante fulgor de su brillante cuerpo terminan mareándose y pierden las ganas de continuar combatiendo.', 'Pokémon Rotación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (687, 'Malamar', 17, 14, 47.0, 1.5, 86, 92, 88, 68, 75, 73, 'Tiene a su disposición los poderes hipnóticos más poderosos de entre todos los pokémon. Controla al enemigo a su voluntad.', 'Pokémon Revolución');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (688, 'Binacle', 6, 11, 31.0, 0.5, 42, 52, 67, 39, 56, 50, 'Dos binacle conviven en la misma roca, pero cuando se produce un altercado entre ambos, uno de ellos se muda a otra.', 'Pokémon Dos Manos');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (689, 'Barbaracle', 6, 11, 96.0, 1.3, 72, 105, 115, 54, 86, 68, 'Al evolucionar, los dos binacle se dividen y dan lugar a siete criaturas que se aglomeran formando un barbaracle y suman su poder al combatir.', 'Pokémon Combinación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (690, 'Skrelp', 4, 11, 7.3, 0.5, 50, 60, 60, 60, 60, 30, 'Adopta la apariencia de algas podridas y lanza un líquido venenoso a las presas desprevenidas que pasan cerca.', 'Pokémon Pseudoalga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (691, 'Dragalge', 4, 16, 81.5, 1.8, 65, 75, 90, 97, 123, 44, 'Ataca de manera indiscriminada a quien ose adentrarse en su territorio, escupiéndole un virulento veneno que puede corroer hasta el casco de un petrolero.', 'Pokémon Pseudoalga');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (692, 'Clauncher', 11, NULL, 8.3, 0.5, 50, 53, 62, 58, 63, 44, 'Abate a enemigos voladores disparando agua a presión con su enorme pinza como si de una pistola se tratara.', 'Pokémon Proyectagua');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (693, 'Clawitzer', 11, NULL, 35.3, 1.3, 71, 73, 88, 120, 89, 59, 'Posee una enorme pinza que dispara bolas de agua que podrían atravesar incluso el casco de un petrolero.', 'Pokémon Lanzachorro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (694, 'Helioptile', 13, 1, 6.0, 0.5, 44, 38, 33, 61, 43, 70, 'Habita en los desiertos y, cuando toma el sol, acumula energía, lo que le permite vivir sin la necesidad de ingerir alimentos.', 'Pokémon Generador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (695, 'Heliolisk', 13, 1, 21.0, 1.0, 62, 55, 52, 109, 94, 109, 'Extiende su gorguera y genera energía eléctrica suficiente para cubrir el consumo de electricidad de un rascacielos entero.', 'Pokémon Generador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (696, 'Tyrunt', 6, 16, 26.0, 0.8, 58, 89, 77, 45, 45, 48, 'Pokémon recreado a partir de fósiles. Cuando sucede algo que no es de su agrado, se enfurece y se vuelve violento.', 'Pokémon Heredero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (697, 'Tyrantrum', 6, 16, 270.0, 2.5, 82, 121, 119, 69, 59, 71, 'En el mundo antiguo en el que habitaba no tenía rivales gracias a sus mandíbulas, con las que podría despedazar con facilidad gruesas placas de acero.', 'Pokémon Tirano');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (698, 'Amaura', 6, 15, 25.2, 1.3, 77, 59, 50, 67, 63, 46, 'Pokémon que ha sido regenerado a partir de un trozo de su antiguo cuerpo, que quedó congelado hace unos 100 millones de años.', 'Pokémon Tundra');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (699, 'Aurorus', 6, 15, 225.0, 2.7, 123, 77, 72, 99, 92, 58, 'Expulsa un gas gélido a temperaturas inferiores a los -150 ºc a través de sus cristales romboides y congela así a su enemigo.', 'Pokémon Tundra');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (700, 'Sylveon', 18, NULL, 23.5, 1.0, 95, 65, 65, 110, 130, 60, 'A través de sus apéndices sensitivos con forma de cinta, emite unas ondas tranquilizadoras mediante las cuales detiene los combates.', 'Pokémon Vínculo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (701, 'Hawlucha', 2, 3, 21.5, 0.8, 78, 92, 75, 74, 63, 118, 'A pesar de ser un pokémon de pequeña estatura, su pericia en el combate le permite plantar cara a pokémon como machamp o hariyama.', 'Pokémon Lucha Libre');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (702, 'Dedenne', 13, 18, 2.2, 0.2, 67, 58, 57, 81, 67, 101, 'Sus bigotes hacen la función de antenas, por lo que puede enviar y recibir ondas eléctricas para comunicarse a grandes distancias.', 'Pokémon Antenas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (703, 'Carbink', 6, 18, 5.7, 0.3, 50, 50, 150, 50, 150, 50, 'Nace en las profundidades subterráneas, a altas temperaturas y a gran presión. Irradia energía por la roca que tiene en la cabeza.', 'Pokémon Joya');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (704, 'Goomy', 16, NULL, 2.8, 0.3, 45, 50, 35, 55, 75, 40, 'Es el pokémon de tipo dragón más débil. Habita en zonas oscuras y húmedas para evitar que su viscoso cuerpo se seque.', 'Pokémon Molusco');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (705, 'Sliggoo', 16, NULL, 17.5, 0.8, 68, 75, 53, 83, 113, 60, 'Segrega una mucosidad que corroe todo lo que toca y consigue así ahuyentar al enemigo. Sus ojos han involucionado, por lo que no puede ver.', 'Pokémon Molusco');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (706, 'Goodra', 16, NULL, 150.5, 2.0, 90, 100, 70, 110, 150, 80, 'Pokémon de tipo dragón muy afable al que le gusta abrazar con efusión a su entrenador, aunque después lo deja totalmente pringado.', 'Pokémon Dragón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (707, 'Klefki', 9, 18, 3.0, 0.2, 57, 80, 91, 80, 87, 75, 'Tiene el hábito de coleccionar llaves. Cuando un enemigo lo ataca, las hace tintinear como método de intimidación.', 'Pokémon Llavero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (708, 'Phantump', 8, 12, 7.0, 0.4, 43, 70, 48, 50, 60, 38, 'Pokémon que nace cuando un espíritu se adentra en un tocón podrido. Habita en bosques que son poco frecuentados por humanos.', 'Pokémon Tocón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (709, 'Trevenant', 8, 12, 71.0, 1.5, 85, 110, 76, 65, 82, 56, 'Puede controlar los árboles a su voluntad. Retiene hasta el fin de sus días a los humanos que se adentran en el bosque y lo mancillan.', 'Pokémon Árbol Viejo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (710, 'Pumpkaboo', 8, 12, 5.0, 0.4, 49, 66, 70, 44, 55, 51, 'En el interior de la calabaza reside un espíritu que ha permanecido atrapado en este mundo. Entra en acción al caer la noche.', 'Pokémon Calabaza');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (711, 'Gourgeist', 8, 12, 12.5, 0.9, 65, 90, 122, 58, 75, 84, 'En las noches de luna nueva deambula por las calles de las ciudades y emite espeluznantes alaridos que maldicen a quienes los oyen.', 'Pokémon Calabaza');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (712, 'Bergmite', 15, NULL, 99.5, 1.0, 55, 69, 85, 32, 35, 28, 'Envuelve su cuerpo en una capa de hielo que le permite evitar los ataques del enemigo. Si se resquebraja, la recompone con aire gélido.', 'Pokémon Témpano');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (713, 'Avalugg', 15, NULL, 505.0, 2.0, 95, 117, 184, 44, 46, 28, 'Su cuerpo, cubierto de hielo, es tan duro como el propio acero. Su abultada figura le permite arramblar con todo lo que se encuentra a su paso.', 'Pokémon Iceberg');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (714, 'Noibat', 3, 16, 8.0, 0.5, 40, 30, 35, 45, 40, 55, 'Habita en cuevas donde reina la más absoluta oscuridad y emite ondas ultrasónicas de 200 000 hz a través de sus enormes orejas.', 'Pokémon Onda Sónica');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (715, 'Noivern', 3, 16, 85.0, 1.5, 85, 70, 80, 97, 80, 123, 'Surca el cielo nocturno cuando ni siquiera la luna brilla y ataca a presas incautas. Es imbatible en combates que tienen lugar a oscuras.', 'Pokémon Onda Sónica');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (716, 'Xerneas', 18, NULL, 215.0, 3.0, 126, 131, 95, 131, 98, 99, 'Según las leyendas, puede compartir el secreto de la vida eterna. Ha despertado tras dormir durante mil años bajo la apariencia de un árbol.', 'Pokémon Creación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (717, 'Yveltal', 17, 3, 203.0, 5.8, 126, 131, 95, 131, 98, 99, 'Pokémon legendario que, al extender sus alas y las plumas de la cola, emite un brillo carmesí que absorbe la energía vital de su enemigo.', 'Pokémon Destrucción');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (718, 'Zygarde', 16, 5, 305.0, 5.0, 108, 100, 121, 81, 95, 95, 'Cuando el ecosistema de kalos se encuentra en peligro, aparece y revela su poder secreto.', 'Pokémon Equilibrio');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (719, 'Diancie', 6, 18, 8.8, 0.7, 50, 100, 150, 100, 150, 50, 'Es una transformación súbita de carbink. Se dice que su cuerpo, que irradia destellos rosados, es lo más bonito de este mundo.', 'Pokémon Joya');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (720, 'Hoopa', 14, 8, 9.0, 0.5, 80, 110, 60, 150, 130, 70, 'Este problemático pokémon envía cualquier objeto a lugares remotos mediante unos anillos que deforman el espacio.', 'Pokémon Travesura');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (721, 'Volcanion', 10, 11, 195.0, 1.7, 80, 110, 120, 130, 90, 70, 'Expulsa vapor y desaparece entre la densa niebla. Dicen que habita en montañas solitarias.', 'Pokémon Vapor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (722, 'Rowlet', 12, 3, 1.5, 0.3, 68, 55, 55, 50, 50, 42, 'Es cauteloso, desconfiado y de naturaleza nocturna. Durante el día acumula energía mediante la fotosíntesis.', 'Pokémon Pluma Hoja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (723, 'Dartrix', 12, 3, 16.0, 0.7, 78, 75, 75, 70, 70, 52, 'Es un poco coqueto, por lo que, cuando puede, se acicala las alas. En ocasiones se niega a combatir por temor a mancharse las plumas.', 'Pokémon Pluma Filo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (724, 'Decidueye', 12, 8, 36.6, 1.6, 78, 107, 75, 100, 100, 70, 'Las plumas de sus alas le sirven de flechas con las que puede acertar a un guijarro a 100 m.', 'Pokémon Pluma Flecha');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (725, 'Litten', 10, NULL, 4.3, 0.4, 45, 65, 40, 60, 40, 70, 'Prende las bolas de pelo que se forman en su estómago tras acicalarse. Las llamas que escupe tienen formas variopintas.', 'Pokémon Gato Fuego');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (726, 'Torracat', 10, NULL, 25.0, 0.7, 65, 85, 50, 80, 50, 90, 'Luce al cuello un cascabel incandescente que tintinea con fuerza cuando desprende llamas.', 'Pokémon Gato Fuego');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (727, 'Incineroar', 10, 17, 83.0, 1.8, 95, 115, 90, 80, 90, 60, 'Posee un carácter rudo y egoísta. Si un entrenador no le cae en gracia, ignora sus órdenes con total impasibilidad.', 'Pokémon Rudo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (728, 'Popplio', 11, NULL, 7.5, 0.4, 50, 54, 54, 66, 56, 40, 'Se le conoce por su diligencia y predisposición. segrega agua por la nariz para hinchar globos con los que golpea a sus rivales.', 'Pokémon León Marino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (729, 'Brionne', 11, NULL, 17.5, 0.6, 60, 69, 69, 91, 81, 50, 'Un bailarín consumado. Crea globos de agua con los que golpea a sus rivales una y otra vez sin dejar de bailar.', 'Pokémon Fama');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (730, 'Primarina', 11, 18, 44.0, 1.8, 80, 74, 74, 126, 116, 60, 'Es capaz de controlar globos de agua mediante un canto cuya melodía se ha transmitido entre los suyos de generación en generación.', 'Pokémon Solista');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (731, 'Pikipek', 1, 3, 1.2, 0.3, 35, 75, 30, 30, 30, 65, 'Hace agujeros en los árboles a una velocidad de 16 picotazos por segundo y los utiliza como despensa o como su nido.', 'Pokémon Carpintero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (732, 'Trumbeak', 1, 3, 14.8, 0.6, 55, 85, 50, 40, 50, 75, 'Almacena en su pico las semillas de las bayas de las que se alimenta para usarlas como arma contra enemigos y posibles presas.', 'Pokémon Trompeta');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (733, 'Toucannon', 1, 3, 26.0, 1.1, 80, 120, 75, 75, 75, 60, 'Cuando va a combatir, calienta su pico hasta temperaturas que superan los 100 °c, lo que puede causar graves quemaduras al contacto.', 'Pokémon Cañón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (734, 'Yungoos', 1, NULL, 6.0, 0.4, 48, 70, 30, 30, 30, 45, 'Sus afilados colmillos lo atraviesan todo. No es autóctono de alola, sino que proviene de otra región.', 'Pokémon Patrulla');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (735, 'Gumshoos', 1, NULL, 14.2, 0.7, 88, 110, 60, 55, 60, 45, 'Al detectar el rastro de una presa, la acecha con tenacidad, pero al ponerse el sol le vence el sueño.', 'Pokémon Vigilante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (736, 'Grubbin', 7, NULL, 4.4, 0.4, 47, 62, 45, 55, 45, 46, 'Con sus duras mandíbulas arranca la corteza de los árboles y sorbe la savia. Suele habitar bajo tierra.', 'Pokémon Pupa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (737, 'Charjabug', 7, 13, 10.5, 0.5, 57, 82, 95, 55, 75, 36, 'Almacena electricidad en su cuerpo, lo que puede resultar útil en numerosas situaciones, como al salir de acampada.', 'Pokémon Batería');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (738, 'Vikavolt', 7, 13, 45.0, 1.5, 77, 70, 90, 145, 75, 43, 'Sobrevuela un área en busca de presas. Puede concentrar una potente carga eléctrica entre sus mandíbulas para atacar a sus rivales.', 'Pokémon Escarabajo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (739, 'Crabrawler', 2, NULL, 7.0, 0.6, 47, 82, 57, 42, 47, 63, 'Protege sus puntos débiles con las pinzas y propina puñetazos en cuanto tiene ocasión. cuando pierde, llora lágrimas espumosas.', 'Pokémon Púgil');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (740, 'Crabominable', 2, 15, 180.0, 1.7, 97, 132, 77, 62, 67, 43, 'Tras perderse intentando coronar una alta montaña, desarrolló una capa de pelo para protegerse del frío.', 'Pokémon Cangrejopelo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (741, 'Oricorio', 10, 3, 3.4, 0.6, 75, 70, 70, 98, 70, 93, 'Junta las alas y lanza voraces llamas al rival. su ataque se asemeja a una elaborada coreografía de fuego.', 'Pokémon Danza');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (742, 'Cutiefly', 7, 18, 0.2, 0.1, 40, 45, 40, 55, 40, 84, 'Se alimenta de néctar y polen. Es capaz de detectar los capullos que están a punto de abrirse.', 'Pokémon Mosca Abeja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (743, 'Ribombee', 7, 18, 0.5, 0.2, 60, 55, 60, 95, 70, 124, 'Fabrica varios tipos de bolas de polen. Algunas se usan como alimento y otras para el combate.', 'Pokémon Mosca Abeja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (744, 'Rockruff', 6, NULL, 9.2, 0.5, 45, 65, 40, 30, 40, 60, 'Su carácter amigable lo convierte en el pokémon ideal para entrenadores novatos. Sin embargo, al crecer suele volverse un poco agresivo.', 'Pokémon Perrito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (745, 'Lycanroc', 6, NULL, 25.0, 0.8, 75, 115, 65, 55, 65, 112, 'Su velocidad desconcierta a sus rivales. Ataca con garras, colmillos y con las piedras afiladas de su melena.', 'Pokémon Lobo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (746, 'Wishiwashi', 11, NULL, 0.3, 0.2, 45, 20, 20, 25, 25, 40, 'Al sentirse amenazado, sus ojos emiten cierto brillo, algo que atrae a otros miembros de su especie, y juntos luchan como un mismo ente.', 'Pokémon Pececillo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (747, 'Mareanie', 4, 11, 8.0, 0.4, 50, 53, 62, 43, 52, 45, 'Aturde a su presa con los aguijones venenosos de la cabeza. Cuando esta se debilita, la rodea con sus diez extremidades y la remata.', 'Pokémon Estrellatroz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (748, 'Toxapex', 4, 11, 14.5, 0.7, 50, 63, 152, 53, 142, 35, 'Se arrastra por el lecho marino con sus doce extremidades. Deja siempre una estela de restos de corsola por donde pasa.', 'Pokémon Estrellatroz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (749, 'Mudbray', 5, NULL, 110.0, 1.0, 70, 100, 70, 45, 55, 45, 'El lodo que impregna sus patas le otorga una fuerte tracción, lo que le permite alcanzar altas velocidades al correr.', 'Pokémon Asno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (750, 'Mudsdale', 5, NULL, 920.0, 2.5, 100, 125, 100, 55, 85, 35, 'El lodo que segrega por la boca se vuelve impermeable al secarse, por lo que solía usarse como capa aislante en las paredes de las casas.', 'Pokémon Caballo Tiro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (751, 'Dewpider', 11, 7, 4.0, 0.3, 38, 40, 52, 40, 72, 27, 'Emerge a la superficie para alimentarse. la burbuja con la que se rodea le sirve para respirar y proteger su delicada cabeza.', 'Pokémon Pompa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (752, 'Araquanid', 11, 7, 82.0, 1.8, 68, 70, 92, 50, 132, 42, 'Propina cabezazos con su burbuja. Los pokémon de menor tamaño quedan atrapados en su interior y perecen ahogados.', 'Pokémon Pompa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (753, 'Fomantis', 12, NULL, 1.5, 0.3, 40, 55, 35, 50, 35, 35, 'Mientras que de día duerme tranquilamente a la luz del sol, al caer la noche busca un sitio más seguro para dormir.', 'Pokémon Filo Hoja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (754, 'Lurantis', 12, NULL, 18.5, 0.9, 70, 105, 90, 80, 90, 45, 'Dedica mucho tiempo y esfuerzo a acicalarse y preservar sus vivos colores. A algunos ejemplares incluso les lleva todo el día.', 'Pokémon Filo Flor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (755, 'Morelull', 12, 18, 1.5, 0.2, 40, 35, 55, 65, 75, 15, 'Cuando titila, esparce esporas luminosas a su alrededor. Todo aquel que ve esta luz cae en un profundo sueño.', 'Pokémon Luminiscente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (756, 'Shiinotic', 12, 18, 11.5, 1.0, 60, 45, 80, 90, 100, 30, 'Es peligroso adentrarse por la noche en un bosque habitado por shiinotic. Su luz confunde a los caminantes, que terminan perdiéndose.', 'Pokémon Luminiscente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (757, 'Salandit', 4, 10, 4.8, 0.6, 48, 44, 40, 71, 40, 77, 'Aturde a sus oponentes con un gas venenoso que produce al quemar sus fluidos corporales y luego se abalanza sobre ellos.', 'Pokémon Lagartoxina');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (758, 'Salazzle', 4, 10, 22.2, 1.2, 68, 64, 60, 111, 60, 117, 'Por algún motivo desconocido, solo hay hembras. Suele formarse su propio harén compuesto por salandit machos.', 'Pokémon Lagartoxina');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (759, 'Stufful', 1, 2, 6.8, 0.5, 70, 75, 50, 45, 50, 50, 'Pese a su aspecto encantador, cuando se enoja, sus pataletas son tan feroces que pueden derribar hasta a un luchador profesional.', 'Pokémon Rabieta');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (760, 'Bewear', 1, 2, 135.0, 2.1, 120, 125, 80, 55, 60, 60, 'Es un pokémon muy peligroso por su fuerza descomunal. Normalmente, se desaconseja penetrar en su hábitat.', 'Pokémon Brazo Fuerte');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (761, 'Bounsweet', 12, NULL, 3.2, 0.3, 42, 30, 38, 30, 38, 32, 'Atraídos por el apetitoso aroma que desprende su cuerpo, los toucannon los engullen con avidez sin ni siquiera masticar.', 'Pokémon Fruto');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (762, 'Steenee', 12, NULL, 8.2, 0.7, 52, 40, 48, 40, 48, 62, 'El cáliz de su cabeza se ha desarrollado y le protege todo el cuerpo. Es tan duro que no le importa que lo picoteen los pokémon pájaro.', 'Pokémon Fruto');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (763, 'Tsareena', 12, NULL, 21.4, 1.2, 72, 120, 98, 50, 98, 72, 'Propinar patadas implacables es su especialidad. Patea a sus oponentes derrotados y proclama su victoria con una gran carcajada.', 'Pokémon Fruto');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (764, 'Comfey', 18, NULL, 0.3, 0.1, 51, 52, 90, 82, 110, 100, 'Prende flores en su tallo, que es muy rico en nutrientes. Las flores se revitalizan y desprenden una agradable fragancia.', 'Pokémon Recogeflores');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (765, 'Oranguru', 1, 14, 76.0, 1.5, 90, 60, 80, 90, 110, 60, 'Es un pokémon famoso por su astucia e ingenio. desprecia a los entrenadores novatos y prefiere a los más veteranos.', 'Pokémon Sabio');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (766, 'Passimian', 2, NULL, 82.8, 2.0, 100, 120, 90, 40, 60, 80, 'Suele ir en grupos de unos veinte ejemplares. sus miembros permanecen siempre unidos y nunca se abandonan unos a otros.', 'Pokémon Cooperación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (767, 'Wimpod', 7, 11, 12.0, 0.5, 25, 35, 40, 20, 30, 80, 'Cuando se asusta, huye despavorido moviendo rápidamente sus múltiples patas. Deja el suelo limpio y pulido allí por donde pasa.', 'Pokémon Huidizo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (768, 'Golisopod', 7, 11, 108.0, 2.0, 75, 125, 140, 60, 90, 40, 'Gracias a su destreza y sus enormes y afiladas garras, puede cortar hasta el aire y el agua de un solo tajo.', 'Pokémon Blindaje');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (769, 'Sandygast', 8, 5, 70.0, 0.5, 55, 55, 80, 70, 45, 15, 'Surgió cuando una persona murió abandonada en plena calle y su resentimiento poseyó un montículo de arena apilado por un niño.', 'Pokémon Montearena');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (770, 'Palossand', 8, 5, 250.0, 1.3, 85, 75, 110, 100, 75, 35, 'Manipulando a la gente ha conseguido transformarse en todo un castillo de arena, lo que ha aumentado su poder maligno.', 'Pokémon Castiarena');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (771, 'Pyukumuku', 11, NULL, 1.2, 0.3, 55, 60, 130, 30, 130, 5, 'Vive en las playas y en aguas poco profundas. expulsa sus entrañas para engañar a sus depredadores y librarse de ellos.', 'Pokémon Pepino Mar');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (772, 'Código Cero', 1, NULL, 120.5, 1.9, 95, 95, 95, 95, 95, 59, 'La pesada máscara le impide liberar toda su fuerza, que se cree que es extraordinaria.', 'Pokémon Multigénico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (773, 'Silvally', 1, NULL, 100.5, 2.3, 95, 95, 95, 95, 95, 95, 'La confianza con su entrenador ha despertado su potencial. Ahora es capaz de cambiar de tipo a voluntad durante el combate.', 'Pokémon Multigénico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (774, 'Minior', 6, 3, 40.0, 0.3, 60, 60, 100, 60, 100, 60, 'Vive en la capa de ozono hasta que su caparazón se hace demasiado pesado y cae sin remisión a tierra firme.', 'Pokémon Meteoro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (775, 'Komala', 1, NULL, 19.9, 0.4, 65, 115, 65, 75, 95, 65, 'Pasa la vida entera sumido en un profundo sueño. Toda su actividad parece reflejar los sueños que tiene en ese momento.', 'Pokémon Duermevela');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (776, 'Turtonator', 10, 16, 212.0, 2.0, 60, 78, 135, 91, 85, 36, 'Posee un caparazón que estalla al contacto. el orificio que se aprecia en su vientre es su punto débil.', 'Pokémon Tortugabomba');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (777, 'Togedemaru', 13, 9, 3.3, 0.3, 65, 98, 63, 40, 73, 96, 'Cuando se siente amenazado, eriza las púas de su lomo para pinchar a quien ose atacarlo.', 'Pokémon Bolita');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (778, 'Mimikyu', 8, 18, 0.7, 0.2, 55, 90, 80, 50, 105, 96, 'Su verdadero aspecto es un misterio total. Un investigador que intentó mirar dentro del saco recibió, literalmente, un susto de muerte.', 'Pokémon Disfraz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (779, 'Bruxish', 11, 14, 19.0, 0.9, 68, 105, 70, 70, 70, 92, 'Cuando la protuberancia que tiene en la cabeza emite poderes psíquicos, se escucha como un ruido de dientes rechinando muy molesto.', 'Pokémon Rechinante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (780, 'Drampa', 1, 16, 185.0, 3.0, 78, 60, 85, 135, 91, 36, 'Es de naturaleza mansa, pero si se le provoca y monta en cólera, destruye todo lo que le rodea con su aliento devastador.', 'Pokémon Sosiego');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (781, 'Dhelmise', 8, 12, 210.0, 3.9, 70, 131, 100, 86, 90, 40, 'Los giros de su enorme ancla son capaces de noquear al mismísimo wailord de un solo golpe. el alga verde es su verdadero cuerpo.', 'Pokémon Alga Ancla');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (782, 'Jangmo-o', 16, NULL, 29.7, 0.6, 45, 55, 65, 45, 45, 45, 'Comunica sus sentimientos haciendo sonar sus escamas. El ruido metálico producido reverbera en las altas montañas donde habita.', 'Pokémon Escamas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (783, 'Hakamo-o', 16, 2, 47.0, 1.2, 55, 75, 90, 65, 70, 65, 'Se lanza sobre su presa entonando un grito de guerra. Golpea al enemigo y lo hace trizas con sus escamas.', 'Pokémon Escamas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (784, 'Kommo-o', 16, 2, 78.2, 1.6, 75, 110, 125, 100, 105, 85, 'Al avistar a su enemigo, lo intimida con el tintineo que producen las escamas de su cola. los más débiles se asustan y huyen.', 'Pokémon Escamas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (785, 'Tapu Koko', 13, 18, 20.5, 1.8, 70, 115, 85, 95, 75, 130, 'El espíritu guardián de melemele, curioso e inquisitivo. Invoca nubes de tormenta y acumula el poder de los rayos en su cuerpo.', 'Pokémon Dios Nativo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (786, 'Tapu Lele', 14, 18, 18.6, 1.2, 70, 85, 75, 130, 115, 95, 'El espíritu guardián de akala, tan inocente como cruel. La fragancia de las flores constituye su fuente de energía.', 'Pokémon Dios Nativo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (787, 'Tapu Bulu', 12, 18, 45.5, 1.9, 70, 130, 115, 85, 95, 75, 'Arranca de cuajo árboles grandes para hacerlos girar. Vuelve exuberante la vegetación y luego absorbe su energía.', 'Pokémon Dios Nativo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (788, 'Tapu Fini', 11, 18, 21.2, 1.3, 70, 75, 115, 95, 130, 85, 'Desconcierta al enemigo provocando una espesa niebla que se convierte en su perdición. Las corrientes marinas son su fuente de energía.', 'Pokémon Dios Nativo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (789, 'Cosmog', 14, NULL, 0.1, 0.2, 43, 29, 31, 29, 31, 37, 'Su cuerpo se compone de una materia gaseosa inestable. Crece poco a poco al acumular las motas de polvo que flotan en el aire.', 'Pokémon Nebulosa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (790, 'Cosmoem', 14, NULL, 999.9, 0.1, 43, 29, 131, 29, 131, 37, 'Permanece inmóvil, como si estuviera muerto, aunque al tocarlo se percibe un ligero calor. antaño se le llamaba crisálida de las estrellas.', 'Pokémon Protostrella');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (791, 'Solgaleo', 14, 9, 230.0, 3.4, 137, 137, 107, 113, 89, 97, 'Se cree que proviene de otro mundo. El brillo que emite su cuerpo es tal que puede hacer que la noche más profunda parezca pleno día.', 'Pokémon Corona Solar');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (792, 'Lunala', 14, 8, 120.0, 4.0, 137, 113, 89, 137, 107, 97, 'Se dice que es la evolución final hembra de cosmog. Su tercer ojo aparece cuando se traslada a un mundo distinto.', 'Pokémon Corona Lunar');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (793, 'Nihilego', 6, 4, 55.5, 1.2, 109, 53, 47, 127, 131, 103, 'Un ultraente envuelto en misterio. Se dice que las víctimas a las que posee se vuelven agresivas.', 'Pokémon Parásito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (794, 'Buzzwole', 7, 2, 333.6, 2.4, 107, 139, 139, 53, 53, 79, 'Un ultraente procedente de otro mundo. Se desconoce si exhibe su robusto cuerpo a modo de amenaza o para que lo admiren.', 'Pokémon Hinchado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (795, 'Pheromosa', 7, 2, 25.0, 1.8, 71, 137, 37, 137, 37, 151, 'Uno de los temibles ultraentes. Se dice que ha sido avistado recorriendo la superficie terrestre a velocidades de vértigo.', 'Pokémon Elegancia');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (796, 'Xurkitree', 13, NULL, 100.0, 3.8, 83, 89, 71, 173, 71, 83, 'Un ultraente rodeado de misterio. Se dice que su cuerpo desprende descargas eléctricas de inmensa potencia.', 'Pokémon Luminaria');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (797, 'Celesteela', 9, 3, 999.9, 9.2, 97, 101, 103, 107, 101, 61, 'Este ultraente surgió de un ultraumbral. Parece haber sido avistado surcando los cielos a velocidades vertiginosas.', 'Pokémon Lanzamiento');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (798, 'Kartana', 12, 9, 0.1, 0.3, 59, 181, 131, 59, 31, 109, 'Este ultraente surgió de un ultraumbral. Se dice que no es muy agresivo, pero su afilado cuerpo es un arma realmente peligrosa.', 'Pokémon Desenvaine');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (799, 'Guzzlord', 17, 16, 888.0, 5.5, 223, 101, 53, 97, 53, 43, 'Se dice que este ultraente ha sido avistado devorando montañas enteras y tragándose prominentes edificios.', 'Pokémon Tragaldabas');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (800, 'Necrozma', 14, NULL, 230.0, 2.4, 97, 107, 101, 127, 89, 79, 'Se dice que yacía en el interior de la tierra. Se cree que esta criatura, parecida a un ultraente, vino de otro mundo hace muchísimo tiempo.', 'Pokémon Prisma');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (801, 'Magearna', 9, 18, 80.5, 1.0, 80, 95, 115, 130, 115, 65, 'Pokémon artificial creado hace más de 500 años. Es capaz de entender el lenguaje humano, pero carece de la facultad del habla.', 'Pokémon Artificial');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (802, 'Marshadow', 2, 8, 22.2, 0.7, 90, 125, 80, 90, 90, 125, 'Debido a que siempre se ha ocultado en las sombras y no le gusta dejarse ver, su misma existencia es todo un misterio.', 'Pokémon Morasombra');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (803, 'Poipole', 4, NULL, 1.8, 0.6, 67, 73, 67, 73, 67, 73, 'En su mundo, este ultraente se considera tan entrañable como para ser elegido compañero de viaje.', 'Pokémon Pin Veneno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (804, 'Naganadel', 4, 16, 150.0, 3.6, 73, 73, 73, 127, 73, 121, 'Una de las temibles criaturas conocidas como ultraentes. En su cuerpo almacena cientos de litros de veneno.', 'Pokémon Pin Veneno');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (805, 'Stakataka', 6, 9, 820.0, 5.5, 61, 131, 211, 53, 101, 13, 'Surgió de un ultraumbral. Parece estar compuesto de varias criaturas que se han combinado para formar un solo ser.', 'Pokémon Muro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (806, 'Blacephalon', 10, 8, 13.0, 1.8, 53, 127, 53, 151, 79, 107, 'Parece ser uno de los temibles ultraentes. se acerca a la gente contoneándose y hace explotar su propia cabeza súbitamente.', 'Pokémon Pirotecnia');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (807, 'Zeraora', 13, NULL, 44.5, 1.5, 88, 112, 75, 102, 80, 143, 'Hace jirones al oponente con sus garras electrificadas. Aunque este esquive los golpes, acaba electrocutado por las descargas.', 'Pokémon Fulgor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (808, 'Meltan', 9, NULL, 8.0, 0.2, 46, 65, 65, 55, 35, 34, 'Su cuerpo está compuesto de acero líquido. funde las partículas de hierro y otros metales del subsuelo para luego absorberlas.', 'Pokémon Tuerca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (809, 'Melmetal', 9, NULL, 800.0, 2.5, 135, 143, 143, 80, 65, 34, 'En la antigüedad se le veneraba por su poder de crear hierro. Después de tres milenios, ha regresado misteriosamente.', 'Pokémon Tuerca');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (810, 'Grookey', 12, NULL, 5.0, 0.3, 50, 65, 50, 40, 40, 65, 'Al marcar el ritmo con su baqueta especial, produce unas ondas sonoras capaces de devolver la vitalidad a la flora.', 'Pokémon Chimpancé');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (811, 'Thwackey', 12, NULL, 14.0, 0.7, 70, 85, 70, 55, 60, 80, 'Los thwackey que marcan el ritmo más contundente con sus dos baquetas son los más respetados por sus congéneres.', 'Pokémon Ritmo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (812, 'Rillaboom', 12, NULL, 90.0, 2.1, 100, 125, 90, 60, 70, 85, 'Controla los poderes y las raíces de su singular tocón en combate golpeándolo como si fuera un tambor.', 'Pokémon Percusión');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (813, 'Scorbunny', 10, NULL, 4.5, 0.3, 50, 71, 40, 40, 40, 69, 'Se pone a correr para elevar su temperatura corporal y propagar la energía ígnea por todo el cuerpo. Desata así su verdadera fuerza.', 'Pokémon Conejo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (814, 'Raboot', 10, NULL, 9.0, 0.6, 65, 86, 60, 55, 60, 94, 'Su suave pelaje lo protege del frío y le permite incrementar todavía más la temperatura de sus movimientos de tipo fuego.', 'Pokémon Conejo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (815, 'Cinderace', 10, NULL, 33.0, 1.4, 80, 116, 75, 65, 75, 119, 'Convierte piedras en balones de fuego dándoles toques y, luego, chuta con fuerza hacia el rival para chamuscarlo.', 'Pokémon Delantero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (816, 'Sobble', 11, NULL, 4.0, 0.3, 50, 40, 40, 70, 40, 70, 'Cuando se espanta, libera unas lágrimas con un factor lacrimógeno equivalente a 100 cebollas para hacer llorar también al rival.', 'Pokémon Acuartija');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (817, 'Drizzile', 11, NULL, 11.5, 0.7, 65, 60, 55, 95, 55, 90, 'Crea bolas de agua con el líquido que segrega por las palmas de las manos y las usa en combate de forma estratégica.', 'Pokémon Acuartija');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (818, 'Inteleon', 11, NULL, 45.2, 1.9, 70, 85, 65, 125, 65, 120, 'Esconde algunos trucos bajo la manga: puede disparar agua por los dedos o planear con las membranas de su espalda.', 'Pokémon Agente');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (819, 'Skwovet', 1, NULL, 2.5, 0.3, 70, 55, 55, 35, 35, 25, 'Este pokémon se encuentra por todo galar. no se queda tranquilo hasta que tiene ambos carrillos atiborrados de bayas.', 'Pokémon Abazón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (820, 'Greedent', 1, NULL, 6.0, 0.6, 120, 95, 95, 55, 75, 20, 'Sus incisivos, de fuerza y dureza extraordinarias, le permiten mordisquear incluso las bayas más duras. Es un pokémon muy común en galar.', 'Pokémon Avaricia');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (821, 'Rookidee', 3, NULL, 1.8, 0.2, 38, 47, 35, 33, 35, 57, 'De naturaleza valiente, planta cara a cualquier rival, por muy fuerte que sea. Los contraataques que recibe le sirven para fortalecerse.', 'Pokémon Pajarito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (822, 'Corvisquire', 3, NULL, 16.0, 0.8, 68, 67, 55, 43, 55, 77, 'Su inteligencia le permite servirse de objetos. Por ejemplo, recoge y lanza piedras con las patas, o utiliza cuerdas para atrapar a su oponente.', 'Pokémon Cuervo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (823, 'Corviknight', 3, 9, 75.0, 2.2, 98, 87, 105, 53, 85, 67, 'No tiene rival en los cielos de galar. El acero negro y lustroso de su cuerpo intimida a cualquier adversario.', 'Pokémon Cuervo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (824, 'Blipbug', 7, NULL, 8.0, 0.4, 25, 20, 20, 25, 45, 45, 'Siempre está recopilando información, por lo que es muy inteligente, aunque su fuerza deja bastante que desear.', 'Pokémon Pupa');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (825, 'Dottler', 7, 14, 19.5, 0.4, 50, 35, 80, 50, 90, 30, 'Apenas se mueve, pero está vivo. Se cree que adquiere poderes psíquicos mientras permanece recluido en su caparazón sin comer ni beber.', 'Pokémon Radomo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (826, 'Orbeetle', 7, 14, 40.8, 0.4, 60, 45, 110, 80, 120, 90, 'Este pokémon es conocido por su inteligencia. el gran tamaño de su cerebro es un indicio de la magnitud de sus poderes psíquicos.', 'Pokémon Siete Puntos');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (827, 'Nickit', 17, NULL, 8.9, 0.6, 40, 28, 28, 47, 52, 50, 'Se sustenta con el alimento que roba a otros pokémon. Las almohadillas de sus patas son tan blandas que no hace ningún ruido al caminar.', 'Pokémon Zorro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (828, 'Thievul', 17, NULL, 19.9, 1.2, 70, 58, 58, 87, 92, 90, 'Marca a su presa sin ser advertido. Sigue el olor y, cuando el otro pokémon baja la guardia, aprovecha para robarle.', 'Pokémon Zorro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (829, 'Gossifleur', 12, NULL, 2.2, 0.4, 40, 40, 60, 40, 60, 10, 'Si planta su única extremidad inferior en la tierra y se expone a abundante luz solar, sus pétalos cobran un color vivo.', 'Pokémon Adornofloral');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (830, 'Eldegoss', 12, NULL, 2.5, 0.5, 60, 50, 90, 80, 120, 60, 'Las semillas que tiene entre la pelusa son muy nutritivas. Arrastradas por el viento, devuelven la vitalidad a la flora y a otros pokémon.', 'Pokémon Adornalgodón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (831, 'Wooloo', 1, NULL, 6.0, 0.6, 42, 40, 55, 40, 45, 48, 'Su lana rizada es tan acolchada que no se hace daño ni aunque se caiga por un precipicio.', 'Pokémon Oveja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (832, 'Dubwool', 1, NULL, 43.0, 1.3, 72, 80, 100, 60, 90, 88, 'Su lana es muy flexible. Las alfombras tejidas con ella adoptan una textura similar a la de las camas elásticas.', 'Pokémon Oveja');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (833, 'Chewtle', 11, NULL, 8.5, 0.3, 50, 64, 50, 38, 38, 44, 'Muerde todo lo que se le ponga por delante. al parecer, lo hace para aliviar el dolor que siente cuando le crecen los incisivos.', 'Pokémon Mordedura');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (834, 'Drednaw', 11, 6, 115.5, 1.0, 90, 115, 90, 48, 68, 74, 'Un pokémon de temperamento feroz que atenaza a su presa con sus fuertes mandíbulas, capaces de destrozar una barra de hierro.', 'Pokémon Mordisco');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (835, 'Yamper', 13, NULL, 13.5, 0.3, 59, 45, 50, 40, 50, 26, 'Al correr, genera electricidad por la base de la cola. Es muy popular entre los pastores de galar.', 'Pokémon Perrito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (836, 'Boltund', 13, NULL, 34.0, 1.0, 69, 90, 60, 90, 60, 121, 'La electricidad que genera y envía a sus patas le ayuda a desplazarse rápidamente. Puede correr sin descanso durante tres días y tres noches.', 'Pokémon Perro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (837, 'Rolycoly', 6, NULL, 12.0, 0.3, 30, 40, 50, 40, 50, 30, 'Fue descubierto hace aproximadamente 400 años en una mina. Casi la totalidad de su cuerpo presenta una composición igual a la del carbón.', 'Pokémon Carbón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (838, 'Carkol', 6, 10, 78.0, 1.1, 80, 60, 90, 60, 70, 50, 'Forma carbón en el interior de su cuerpo. Los antiguos habitantes de galar lo aprovechaban para sus labores diarias.', 'Pokémon Carbón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (839, 'Coalossal', 6, 10, 310.5, 2.8, 110, 80, 120, 80, 90, 30, 'Aunque es de carácter sereno, monta en cólera si ve a seres humanos dañando una mina y reduce todo a cenizas con sus llamas a 1500 °c.', 'Pokémon Carbón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (840, 'Applin', 12, 16, 0.5, 0.2, 40, 40, 80, 40, 40, 20, 'Habita durante toda su vida en el interior de una manzana. Finge ser una fruta para protegerse de los pokémon pájaro, sus enemigos naturales.', 'Pokémon Manzanido');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (841, 'Flapple', 12, 16, 1.0, 0.3, 70, 110, 80, 95, 60, 70, 'Ha evolucionado tras ingerir una manzana ácida. las bolsas de las mejillas albergan un fluido cuya extrema acidez llega a provocar quemaduras.', 'Pokémon Manzanala');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (842, 'Appletun', 12, 16, 13.0, 0.4, 110, 85, 80, 100, 80, 30, 'Ha evolucionado tras ingerir una manzana dulce. de ahí que el olor que emana para atraer a sus presas, los pokémon insecto, sea tan agradable.', 'Pokémon Manzanéctar');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (843, 'Silicobra', 5, NULL, 7.6, 2.2, 52, 57, 75, 35, 50, 46, 'Almacena la arena que ingiere al perforar hoyos en la saca del cuello, cuya capacidad llega a alcanzar incluso los 8 kg.', 'Pokémon Serp. Arena');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (844, 'Sandaconda', 5, NULL, 65.5, 3.8, 72, 107, 125, 65, 70, 71, 'Se retuerce para expulsar por los orificios nasales hasta 100 kg de arena. La ausencia de esta mina su ánimo.', 'Pokémon Serp. Arena');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (845, 'Cramorant', 3, 11, 18.0, 0.8, 70, 85, 55, 85, 95, 85, 'Su colosal potencia le permite machacar al rival de un solo golpe, aunque su carácter despistado lo lleva a olvidarse de su presencia.', 'Pokémon Tragón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (846, 'Arrokuda', 11, NULL, 1.0, 0.5, 41, 63, 40, 40, 30, 66, 'Se siente muy orgulloso de su afilada mandíbula. en cuanto detecta el más mínimo movimiento, va directo al objetivo para cargar contra él.', 'Pokémon Arremetida');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (847, 'Barraskewda', 11, NULL, 30.0, 1.3, 61, 123, 60, 60, 50, 136, 'Su mandíbula es tan puntiaguda como una lanza y tan dura como el acero. Al parecer, su carne es sorprendentemente deliciosa.', 'Pokémon Ensarta');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (848, 'Toxel', 13, 4, 11.0, 0.4, 40, 38, 35, 54, 35, 40, 'Secreta toxinas por la piel y las almacena en una bolsa de veneno interna. Tocarlo da calambre.', 'Pokémon Retoño');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (849, 'Toxtricity', 13, 4, 40.0, 1.6, 75, 98, 70, 114, 70, 75, 'Cuando rasga las protuberancias del pecho para generar energía eléctrica, emite un sonido similar al de una guitarra, que reverbera en el entorno.', 'Pokémon Punki');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (850, 'Sizzlipede', 10, 7, 1.0, 0.7, 50, 65, 45, 50, 50, 45, 'Genera calor consumiendo el gas inflamable que almacena en su cuerpo. Los círculos amarillos del abdomen están particularmente calientes.', 'Pokémon Radiador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (851, 'Centiskorch', 10, 7, 120.0, 3.0, 100, 115, 65, 90, 90, 65, 'Cuando genera calor, su temperatura corporal alcanza aproximadamente los 800 °c. Usa el cuerpo a modo de látigo para lanzarse al ataque.', 'Pokémon Radiador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (852, 'Clobbopus', 2, NULL, 4.0, 0.6, 50, 68, 60, 50, 50, 32, 'Emerge a tierra firme en busca de alimento. Su extrema curiosidad lo induce a golpear con los tentáculos todo lo que entra en su campo visual.', 'Pokémon Malcriado');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (853, 'Grapploct', 2, NULL, 39.0, 1.6, 80, 118, 90, 70, 80, 42, 'Todo su cuerpo es una mole de puro músculo. utiliza los tentáculos para ejecutar técnicas de estrangulamiento con una formidable potencia.', 'Pokémon Jiu-Jitsu');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (854, 'Sinistea', 8, NULL, 0.2, 0.1, 40, 45, 45, 74, 54, 50, 'Según se dice, este pokémon surgió de un alma solitaria que poseyó una taza abandonada llena de un té ya frío.', 'Pokémon Té');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (855, 'Polteageist', 8, NULL, 0.4, 0.2, 60, 65, 65, 134, 114, 70, 'Se hospeda en teteras dignas de un anticuario. las originales son escasísimas, pero existe un gran número de burdas falsificaciones.', 'Pokémon Té');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (856, 'Hatenna', 14, NULL, 3.4, 0.4, 42, 30, 45, 56, 53, 39, 'Percibe los sentimientos de los seres vivos con la protuberancia de la cabeza. Solo abre su corazón a quienes muestren un carácter sosegado.', 'Pokémon Calma');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (857, 'Hattrem', 14, NULL, 4.8, 0.6, 57, 40, 65, 86, 73, 49, 'Silencia a cualquiera que muestre una emoción intensa sin importar de quién se trate y recurre para ello a métodos a cuál más violento.', 'Pokémon Serenidad');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (858, 'Hatterene', 14, 18, 5.1, 2.1, 57, 90, 95, 136, 103, 29, 'Para mantener alejados a los demás seres vivos, emana a su alrededor ondas psíquicas cuya potencia es capaz de provocar jaquecas.', 'Pokémon Silencio');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (859, 'Impidimp', 17, 18, 5.5, 0.4, 45, 45, 30, 55, 40, 50, 'Con el fin de revitalizarse, inhala por la nariz la energía negativa que desprenden tanto personas como pokémon cuando están descontentos.', 'Pokémon Astuto');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (860, 'Morgrem', 17, 18, 12.5, 0.8, 65, 60, 45, 75, 55, 70, 'Su estrategia consiste en postrarse ante el rival y fingir una disculpa para ensartarlo con el mechón que tiene en la espalda, afilado cual lanza.', 'Pokémon Malicioso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (861, 'Grimmsnarl', 17, 18, 61.0, 1.5, 95, 120, 65, 95, 75, 60, 'Cuando enrolla sus cabellos por todo el cuerpo, aumenta su potencia muscular. Posee una fuerza capaz de someter a machamp.', 'Pokémon Voluminoso');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (862, 'Obstagoon', 17, 1, 46.0, 1.6, 93, 90, 101, 60, 81, 95, 'La potencia de su voz es realmente pasmosa. La técnica que usa para intimidar al rival mientras grita recibe el nombre de obstrucción.', 'Pokémon Bloqueador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (863, 'Perrserker', 9, NULL, 28.0, 0.8, 70, 110, 100, 50, 60, 50, 'El pelo de la cabeza se le ha endurecido hasta el punto de parecer un yelmo de hierro. Posee un temperamento muy combativo.', 'Pokémon Vikingo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (864, 'Cursola', 8, NULL, 0.4, 1.0, 60, 95, 50, 145, 130, 30, 'Su energía espiritual ha aumentado hasta hacerlo desprenderse de su base caliza. Protege el alma del núcleo con su cuerpo espectral.', 'Pokémon Coral');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (865, 'Sirfetch’d', 2, NULL, 117.0, 0.8, 62, 135, 95, 68, 82, 65, 'Los ejemplares que superan numerosos combates evolucionan y adoptan esta forma. Abandonan el terreno de combate en cuanto el puerro se seca.', 'Pokémon Pato Salvaje');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (866, 'Mr. Rime', 15, 14, 58.2, 1.5, 80, 85, 75, 110, 100, 70, 'Es un bailarín consumado de claqué. Agita su bastón de hielo mientras exhibe su destreza con gráciles pasos.', 'Pokémon Cómico');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (867, 'Runerigus', 5, 8, 66.6, 1.6, 58, 95, 145, 50, 105, 30, 'Una poderosa maldición pesa sobre esta antigua imagen grabada, que ha cobrado vida tras adueñarse del alma de yamask.', 'Pokémon Resquemor');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (868, 'Milcery', 18, NULL, 0.3, 0.2, 45, 40, 40, 50, 61, 34, 'Su cremoso cuerpo surgió a partir de la unión de partículas odoríferas de dulces aromas presentes en el aire.', 'Pokémon Nata');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (869, 'Alcremie', 18, NULL, 0.5, 0.3, 65, 60, 75, 110, 121, 64, 'Obsequia bayas decoradas con nata a aquellos entrenadores en los que confía.', 'Pokémon Nata');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (870, 'Falinks', 2, NULL, 62.0, 3.0, 65, 100, 100, 70, 60, 75, 'Este pokémon consta de cinco subalternos y un líder, cuyas órdenes obedecen sin rechistar.', 'Pokémon Formación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (871, 'Pincurchin', 13, NULL, 1.0, 0.3, 48, 101, 95, 91, 85, 15, 'Libera electricidad por la punta de las púas. con sus afilados dientes raspa las algas pegadas a las rocas para ingerirlas.', 'Pokémon Erizo de Mar');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (872, 'Snom', 15, 7, 3.8, 0.3, 30, 25, 35, 45, 30, 20, 'Teje un hilo gélido que le permite aferrarse a las ramas y simula ser un carámbano mientras duerme.', 'Pokémon Gusano');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (873, 'Frosmoth', 15, 7, 42.0, 1.3, 70, 65, 60, 125, 90, 65, 'La temperatura de sus alas es de -180 °c. sobrevuela el campo esparciendo sus gélidas escamas, como si de nieve se tratase.', 'Pokémon Polillahielo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (874, 'Stonjourner', 6, NULL, 520.0, 2.5, 100, 125, 135, 20, 20, 70, 'Pasa el tiempo observando inmóvil el recorrido del sol sobre las extensas praderas que habita. se especializa en técnicas de ágiles patadas.', 'Pokémon Megalito');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (875, 'Eiscue', 15, NULL, 89.0, 1.4, 75, 80, 110, 65, 90, 50, 'La corriente lo ha transportado hasta aquí desde un lugar sumamente gélido. Utiliza el hielo para mantener la cara refrigerada en todo momento.', 'Pokémon Pingüino');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (876, 'Indeedee', 14, 1, 28.0, 0.9, 60, 65, 55, 105, 95, 95, 'Percibe las emociones del objetivo con los cuernos de la cabeza. Los machos están atentos a cualquier cosa que necesite su entrenador.', 'Pokémon Sensorio');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (877, 'Morpeko', 13, 17, 3.0, 0.3, 58, 95, 58, 70, 58, 97, 'Siempre tiene hambre. Se nutre de las semillas que guarda en una suerte de bolsillos para generar electricidad.', 'Pokémon Dos Caras');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (878, 'Cufant', 9, NULL, 100.0, 1.2, 72, 80, 49, 40, 49, 40, 'Su constitución fornida le permite transportar sin inmutarse cargas de 5 toneladas. Utiliza la trompa para excavar la tierra.', 'Pokémon Broncefante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (879, 'Copperajah', 9, NULL, 650.0, 3.0, 122, 130, 69, 80, 69, 30, 'Su piel verdosa es resistente al agua. Proviene de tierras lejanas y presta ayuda a las personas en la realización de ciertos trabajos.', 'Pokémon Broncefante');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (880, 'Dracozolt', 13, 16, 190.0, 1.8, 90, 100, 90, 80, 70, 75, 'La robustez de su tren inferior lo hacía invencible en la antigüedad, pero se extinguió tras comerse todas las plantas de las que se nutría.', 'Pokémon Fósil');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (881, 'Arctozolt', 13, 15, 150.0, 2.3, 90, 100, 90, 90, 80, 55, 'La parte superior del cuerpo está congelada y, al temblar, genera electricidad. Camina con suma dificultad.', 'Pokémon Fósil');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (882, 'Dracovish', 11, 16, 215.0, 2.3, 90, 90, 100, 70, 80, 75, 'Su excelente capacidad motriz y la fuerza de su mandíbula lo hacían antaño invencible, pero dar caza a todas sus presas propició su extinción.', 'Pokémon Fósil');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (883, 'Arctovish', 11, 15, 175.0, 2.0, 90, 90, 100, 80, 90, 55, 'Atrapa a sus presas congelando su entorno, pero la ubicación de la boca encima de la cabeza le genera dificultades para deglutirlas.', 'Pokémon Fósil');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (884, 'Duraludon', 9, 16, 40.0, 1.8, 70, 95, 115, 120, 50, 85, 'Su cuerpo, similar a un metal pulido, es tan ligero como robusto. Sin embargo, tiene el defecto de que se oxida con facilidad.', 'Pokémon Aleación');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (885, 'Dreepy', 16, 8, 2.0, 0.5, 28, 60, 30, 40, 30, 82, 'Habitaba los mares en tiempos inmemoriales. Ha revivido en forma de pokémon de tipo fantasma para vagar por su antigua morada.', 'Pokémon Resentido');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (886, 'Drakloak', 16, 8, 11.0, 1.4, 68, 80, 50, 60, 50, 102, 'Vuela a una velocidad de 200 km/h. Lucha junto a un dreepy, al que cuida hasta el momento de su evolución.', 'Pokémon Cuidador');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (887, 'Dragapult', 16, 8, 50.0, 3.0, 88, 120, 75, 100, 75, 142, 'Vive en compañía de dreepy, a los que hospeda en el interior de sus cuernos. Los dispara a velocidad supersónica en combate.', 'Pokémon Furtivo');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (888, 'Zacian', 18, NULL, 110.0, 2.8, 92, 120, 115, 80, 115, 138, 'Este pokémon está considerado como un héroe de leyenda. Absorbe el metal para transformarlo y emplearlo como armamento.', 'Pokémon Guerrero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (889, 'Zamazenta', 2, NULL, 210.0, 2.9, 92, 120, 115, 80, 115, 138, 'Este pokémon se erigió en salvador de galar, tras unir fuerzas con un rey de los hombres. absorbe el metal para utilizarlo en combate.', 'Pokémon Guerrero');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (890, 'Eternatus', 4, 16, 950.0, 20.0, 140, 85, 95, 145, 95, 130, 'Se alimenta de la energía que brota de la tierra de galar absorbiéndola por el núcleo del pecho.', 'Pokémon Gigantesco');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (891, 'Kubfu', 2, NULL, 12.0, 0.6, 60, 90, 60, 53, 50, 72, 'Perfecciona su técnica mediante una estricta disciplina. Según la corriente marcial que siga, adoptará una forma u otra al evolucionar.', 'Pokémon Kung-fu');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (892, 'Urshifu', 2, 17, 105.0, 1.9, 100, 130, 100, 63, 60, 97, 'Su estilo de lucha se basa en abatir a sus rivales de un solo golpe. Se abalanza sobre ellos con su puño cincelado por el entrenamiento.', 'Pokémon Kung-fu');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (893, 'Zarude', 17, 12, 70.0, 1.8, 105, 120, 105, 70, 95, 105, 'Viven en manada en la selva. Debido a su marcada agresividad, son muy temidos por otros pokémon selváticos.', 'Pokémon Simiestro');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (894, 'Regieleki', 13, NULL, 145.0, 1.2, 80, 100, 50, 100, 50, 200, 'Su cuerpo entero es energía eléctrica. Por lo visto, quitarle los anillos que lleva libera su poder oculto.', 'Pokémon Electrón');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (895, 'Regidrago', 16, NULL, 200.0, 2.1, 200, 100, 50, 100, 50, 80, 'Cierta teoría afirma que la forma de sus brazos representa la cabeza de un pokémon dragón ancestral, aunque esto no ha podido verificarse.', 'Pokémon Dragosfera');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (896, 'Glastrier', 15, NULL, 800.0, 2.2, 100, 145, 130, 65, 110, 30, 'Libera un potente vaho gélido por las pezuñas. un ser violento que no duda en tomar a la fuerza todo cuanto desea.', 'Pokémon Corcel Feroz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (897, 'Spectrier', 8, NULL, 44.5, 2.0, 100, 65, 60, 145, 80, 130, 'Examina su entorno con todos los sentidos, salvo la vista. Se dice que sus coces son capaces de robarle el alma a quien las recibe.', 'Pokémon Corcel Veloz');
INSERT INTO pokemon (numeropokedex, nombre, tipo1, tipo2, peso, altura, ps, ataque, defensa, at_especial, df_especial, velocidad, descripcion, categoria) VALUES (898, 'Calyrex', 14, 12, 7.7, 1.1, 100, 80, 80, 80, 80, 80, 'Un pokémon muy compasivo agraciado con el poder de la curación. Reinó en galar en tiempos remotos.', 'Pokémon Rey');
